INSERT INTO `ict_module` (`modul_nr`, `modul_name`, `id_fachschaft`, `bereich`, `semester`, `lbv`, `lessons_ifz`, `lessons_uifz`, `bivo`, `hanoks`, `todo`) VALUES
('100', ' Daten charakterisieren', 3, '', 1, '', 40, 28, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=406">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Daten charakterisieren, aufbereiten und auswerten</dd>\n<dt>Kompetenz</dt>\n<dd>Daten für eine Auswertung aufbereiten und die Ergebnisse der Auswertung überprüfen und interpretieren.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Daten bzw. Datenbestand nach den für die Verarbeitung resp. Auswertung wichtigen Merkmalen hinsichtlich Struktur (Text, Datenblätter, Datenbank usw.) charakterisieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die verschiedenen Formen von Daten (numerische - Messdaten/ Klassifikationen; verbale - Texte in schriftlicher oder gesprochener Form; nonverbale - Musik, Filme, Videos) die auf einem Datenträger gespeichert werden können und kann aufzeigen, wie sich diese Daten hinsichtlich der Auswertbarkeit mit einem Computersystem verhalten.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Strukturmerkmale verschiedener Informationsquellen (Textdokument, Datenblätter, Datenbanken) und kann erläutern, welche Konsequenzen sich daraus für die elektronische Auswertung dieser Datenquellen ergeben.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Beziehungen zwischen Datenstrukturen (Assoziationen, Kardinalitäten) und kann an Beispielen aus dem Alltag aufzeigen, welche Sachverhalte sich damit in einem Datenbestand abbilden lassen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Daten bzw. Datenbestand nach den für die Auswertung wichtigen Merkmalen hinsichtlich Datenqualität (Vollständigkeit, Eindeutigkeit und Redundanz) charakterisieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Merkmale eines Datenbestandes bezüglich Eindeutigkeit und Vollständigkeit der Daten (unterschiedliche Angaben zur gleichen Information, unterschiedlicher Informationsgehalt etc.) und kann erläutern, welche Konsequenzen diese Merkmale auf die Aussagekraft von Auswertungen haben können.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Aufgrund der Charakteristik eines Datenbestands die Informationen auswählen, die sich für eine bestimmte Auswertung eignen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kann die Skalentypen nominal ("alt" - "jung", "ungenügend" - "genügend" - "gut"), ordinal (Ränge, Noten etc.) und metrisch (Alter, Grösse etc.) unterscheiden und die Konsequenzen aufzeigen, die sich daraus für die Auswertungsmöglichkeiten ableiten lassen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Unstrukturierte Daten in eine strukturierte, verarbeitbare Form bringen, um Auswertungen zu ermöglichen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kann die Skalentypen nominal ("alt" - "jung", "ungenügend" - "genügend" - "gut"), ordinal (Ränge, Noten etc.) und metrisch (Alter, Grösse etc.) unterscheiden und die Konsequenzen aufzeigen, die sich daraus für die Auswertungsmöglichkeiten ableiten lassen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Grundlagen der Mengenlehre und kann  daraus die Operatoren (z.B. Aggregatfunktion zur Gruppierung von Daten) ableiten.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die verbreiteten Datentypen (Zahl, Zeit, Datum, Text etc.) und kann deren Grenzen hinsichtlich der Auswertbarkeit von Daten aufzeigen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Für Daten und Zusammenhänge eine geeignete visuell erfassbare Form wählen und diese in der gewählten Form darstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die einfachsten statistischen Kenngrössen (Mittelwert, Minimum, Maximum) und kann aufzeigen, welche Voraussetzungen (Skalentypen, Anzahl Beobachtungen, Qualität der Daten etc.) erfüllt sein müssen, damit diese eine zuverlässige Aussage machen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die einfachsten Diagrammtypen für die Darstellung von Daten und kann erläutern, bei welchen Skalentypen der Daten (metrisch-, nominal-, ordinal-skalierte) sich diese eignen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Bedeutung und Aussagekraft der Auswertung kritisch hinterfragen und kommentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Notwendigkeit, Auswertungsergebnisse bezüglich ihrer Relevanz zu beurteilen und kann anhand von Beispielen aus dem Alltag aufzeigen, dass Tendenzen in Auswertungsergebnissen relevant aber auch nicht relevant sein können.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Notwendigkeit, Auswertungsergebnisse aufgrund unterschiedlicher Perspektiven (Arbeitnehmer - Arbeitgeber; Bürger - Staat etc.) zu beurteilen und kann anhand von Beispielen aus dem Alltag aufzeigen, dass Auswertungsergebnissen je nach Perspektive, die man einnimmt, relevant aber auch nicht relevant sein können.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Data Management</dd>\n<dt>Objekt</dt>\n<dd>Datenbestände (Texte, Listen, einzelne Tabellen, Messreihen).</dd>\n<dt>Niveau</dt>\n<dd>1</dd>\n<dt>Voraussetzung</dt>\n<dd>keine</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n<tab data-title="2.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=167">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Daten charakterisieren, aufbereiten und auswerten</dd>\n<dt>Kompetenz</dt>\n<dd>Daten f&amp;uuml;r eine Auswertung aufbereiten und die Ergebnisse der Auswertung &amp;uuml;berpr&amp;uuml;fen und interpretieren.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Daten bzw. Datenbestand nach den für die Verarbeitung resp. Auswertung wichtigen Merkmalen hinsichtlich Struktur (Text, Datenblätter, Datenbank usw.) charakterisieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die verschiedenen Formen von Daten bzw. Informationen (numerische - Messdaten/ Klassifikationen; verbale - Texte in schriftlicher oder gesprochener Form; nonverbale - Musik, Filme, Videos) die auf einem Datenträger gespeichert werden können und kann aufzeigen, wie sich diese Daten hinsichtlich der Auswertbarkeit mit einem Computersystem verhalten.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Strukturmerkmale verschiedener Informationsquellen (Textdokument, Datenblätter, Datenbanken) und kann erläutern, welche Konsequenzen sich daraus für die elektronische Auswertung dieser Datenquellen ergeben.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Strukturelemente von relationalen Datenbanken (Datenbanktabellen, Fremdschlüssel, Primärschlüssel, Assoziationen, Kardinalitäten) und kann an Beispielen aus dem Alltag aufzeigen, welche Sachverhalte sich damit in einem Datenbestand abbilden lassen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Daten bzw. Datenbestand nach den für die Auswertung wichtigen Merkmalen hinsichtlich Eindeutigkeit (Redundanz, Widersprüchlichkeit) charakterisieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Merkmale eines Datenbestandes bezüglich Eindeutigkeit der Informationen (unterschiedliche Angaben zur gleichen Information, unterschiedlicher Informationsgehalt etc.) und kann erläutern, welche Konsequenzen diese Merkmale auf die Aussagekraft von Auswertungen haben können.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Aufgrund der Charakteristik eines Datenbestands die Informationen auswählen, die sich für eine bestimmte Auswertung eignen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kann die Skalentypen nominal ("alt" - "jung", "ungenügend" - "genügend" - "gut"), ordinal (Ränge, Noten etc.) und metrisch (Alter, Grösse etc.) unterscheiden und die Konsequenzen aufzeigen, die sich daraus für die Auswertungsmöglichkeiten ableiten lassen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Ausgewählte Informationen in eine verarbeitbare Form bringen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Möglichkeiten, die Tabellen für die Gliederung von Datenbeständen beinhalten und kann an Beispielen erläutern, welche Möglichkeiten sich aus dieser Strukturierung für die Auswertung von Daten ergeben.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Grundlagen der Mengenlehre und kann die Beziehung zu den Operatoren (z.B. Aggregatfunktion zur Gruppierung von Daten) eines Abfrage-Assistenten herstellen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die verbreiteten Datenformate (Währung, Zeit, Datum, Text etc.) und kann deren Grenzen hinsichtlich der Auswertbarkeit von Daten aufzeigen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Für die verlangten Aussagen eine geeignete Form der Auswertung und der Darstellung auswählen (Liste, Tabelle, Grafik, Kennwerte) und umsetzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die einfachsten statistischen Kenngrössen (Mittelwert, Minimum, Maximum) und kann aufzeigen, welche Voraussetzungen (Skalentypen, Anzahl Beobachtungen, Qualität der Informationen etc.) erfüllt sein müssen, damit diese eine zuverlässige Aussage machen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die einfachsten Diagrammtypen für die Darstellung von Informationen und kann erläutern, bei welchen Skalentypen der Daten (metrisch-, nominal-, ordinal-skalierte) sich diese eignen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Bedeutung und Aussagekraft der Auswertung kritisch hinterfragen und kommentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Weiss um die Notwendigkeit, Auswertungsergebnisse bezüglich ihrer Relevanz zu beurteilen und kann anhand von Beispielen aus dem Alltag aufzeigen, dass Tendenzen in Auswertungsergebnissen relevant aber auch nicht relevant sein können.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Weiss um die Notwendigkeit, Auswertungsergebnisse aufgrund unterschiedlicher Perspektiven (Arbeitnehmer - Arbeitgeber; Bürger - Staat etc.) zu beurteilen und kann anhand von Beispielen aus dem Alltag aufzeigen, dass Auswertungsergebnissen je nach Perspektive, die man einnimmt, relevant aber auch nicht relevant sein können.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Data Management</dd>\n<dt>Objekt</dt>\n<dd>Datenbestände (Texte, Listen, einzelne Tabellen, Messreihen).</dd>\n<dt>Niveau</dt>\n<dd>1</dd>\n<dt>Voraussetzung</dt>\n<dd>keine</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('101', ' Webauftritt erstellen und veröffentlichen', 1, '', 1, '', 40, 28, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=408">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Webauftritt erstellen und veröffentlichen</dd>\n<dt>Kompetenz</dt>\n<dd>Webauftritt mit einer Seitenbeschreibungssprache nach Vorgabe realisieren und veröffentlichen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Vorgegebene Aufgabenstellung (Zielsetzung, Inhalte, Funktionalitäten) analysieren und Text- bzw. Grafikelemente für den Webauftritt auswählen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt das Vorgehen bei der Situationsanalyse und weiss, welchen Beitrag dieses zur Definition eines klaren Auftrags liefert.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Lösungsvorschlag für den Webauftritt (Site-Struktur, Navigation, Präsentation, usw.) ausarbeiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die grundlegenden Unterschiede der verschiedenen Endgeräte hinsichtlich Funktionalität, Darstellung und Auflösung und Bandbreite der Netzwerkanbindung.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Kriterien die bei der Strukturierung von Informationen auf Websites verwendet werden.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die wichtigsten rechtlichen Rahmenbedingungen (Copyrights, illegale Downloads, Lizenzierungsbedingungen) bei der Verwendung fremder Inhalte.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die gängigsten Medienformate für das Web und deren Einbindung in einer Webseite.</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt die wesentlichen Gestaltungsregeln, die beim Einsatz von Schriften, Farben und Grafiken auf einer Website zu berücksichtigen sind.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Lösungsvorschlag mit einer Seitenbeschreibungssprache in einen Webauftritt umsetzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Grundelemente von HTML und Style Sheets (CSS) und kann diese für die Erstellung einer Website einsetzen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die wichtigsten Grundelemente (Schrift, Farbe, Bild), die bei der Gestaltung einer Website berücksichtigt werden müssen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Einsatzmöglichkeiten von PDF und kann Dokumente webgerecht aufbereiten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Webauftritt auf Erfüllung der Vorgaben aus der Aufgabenstellung überprüfen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt eine Methode (z.B. Testen, Validierung) für die Erfolgskontrolle und kann deren Einsatz für die Bestimmung der Zielerfüllung erläutern.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Testkriterien um die korrekte Funktionsweise des Webauftritts auf unterschiedlichen Endgeräten und Browsern sicherzustellen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Webauftritt auf einem Webserver veröffentlichen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt was beachtet werden muss, wenn die Website-Präsentation von der lokalen Arbeitsumgebung auf einen Web-Server übertragen wird.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Werkzeuge zur sicheren Übertragung einer Website von einer lokalen auf eine serverbasierte Umgebung.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Web Engineering</dd>\n<dt>Objekt</dt>\n<dd>Webauftritt mit Text, Grafik, Animation und Links über maximal 3 Hierarchiestufen (ohne Server-/Clientseitige Programmierung).\nZum Beispiel: Webauftritt für einen kleinen Verein.</dd>\n<dt>Niveau</dt>\n<dd>1</dd>\n<dt>Voraussetzung</dt>\n<dd>keine</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('104', ' Datenmodell implementieren', 3, '', 1, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=410">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Datenmodell implementieren</dd>\n<dt>Kompetenz</dt>\n<dd>Strukturierte Daten anhand eines konzeptionellen Datenbankschemas in einer Datenbanksoftware abbilden und verwalten.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Konzeptionelles Datenbankschema interpretieren anhand von Entitäten, Attributen und Beziehungen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Begriffe Entität, Attribut und Beziehung in einem konzeptionellen Datenbankschema und deren Darstellung .</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die unterschiedlichen Kardinalitäten und deren Aussage bezüglich der Beziehungen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Logisches Datenbankschema ermitteln, normalisieren und als Diagramm darstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt das Vorgehen eines Datenbankentwurfs (Tabellen identifizieren, Attribute definieren, Beziehungen klären).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Logisches Datenbankschema mit einer Datenbanksoftware implementieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Elemente eines Datenbankprogramms zur Umsetzung eines Datenmodells in ein physisches Datenbankschema.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Mit einer Datenbanksoftware Benutzerschnittstellen zur Erfassung, Veränderung und Auswertung von Daten unter Berücksichtigung von Datenschutz und Datensicherheit in einer Datenbank erstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt GUI-Komponenten und Formularfelder für die Definition von Benutzerschnittstellen zur Erfassung, Veränderung sowie Auswertung von Daten.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die wichtigsten Regeln, die bei der ergonomischen Gestaltung einer Benutzeroberfläche für eine Datenbank hinsichtlich Abfolge und Bezeichnung der Informationen einzuhalten sind.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die wichtigsten Abfragen zur Manipulation, Selektion und Auswertung von Datenbeständen.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die Möglichkeit eines Datenbankprogrammes um die Prinzipien von Datenschutz und Datensicherheit umzusetzen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Anwendung mit Testdaten überprüfen und die Ergebnisse protokollieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Testkriterien für Datenintegrität und Datenkonsistenz) und kann aufzeigen, wie Testfälle für die Überprüfung dieser Kriterien aufgebaut sein müssen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Data Management</dd>\n<dt>Objekt</dt>\n<dd>Single User Datenbank mit ca. 5 Tabellen\nZum Beispiel: Mitgliederdatenbank eines Vereins</dd>\n<dt>Niveau</dt>\n<dd>1</dd>\n<dt>Voraussetzung</dt>\n<dd>Daten charakterisieren, aufbereiten und auswerten</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('105', ' Datenbankserver mit SQL bearbeiten', 3, '', 2, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=430">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Datenbanken mit SQL bearbeiten</dd>\n<dt>Kompetenz</dt>\n<dd>Logisches Datenbankschema mit Standardsprachmitteln (Structured Query Language SQL) in ein relationales Datenbank Management System (RDBMS) integrieren. Transaktionen durchführen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Ein Logisches Datenbankschema mit Hilfe von SQL-Befehlen (DDL) das Datenbankschema in einem RDBMS implementieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Befehle einer Datendefinitionssprache (DDL) zur Einrichtung einer Datenbank und kann beschreiben, wie diese Definitionen den Zugriff auf die Datenbank unterstützen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kann die geforderten Beziehungen unter Berücksichtigung der referenzielle Integrität (Datenkonsistenz) implementieren.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Rollen/Berechtigungen vergeben zur Gewährleistung der Datensicherheit und des Datenschutzes.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Funktionen eines Datenbankmanagementsystems um gemäss Vorgabe die Einschränkung des Zugriffs und der Manipulationen von Daten zu steuern.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Mit einem Datenbank Utility (Bulk load) oder mittels SQL-Befehlen die Datenbank mit Testdaten laden.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Varianten, grössere Datenmengen in eine Datenbank zu laden und kann erläutern, welche Varianten bei bestimmten Ausgangssituationen vorzugsweise einzusetzen sind.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Mit SQL-Befehlen Transaktionen zur Bearbeitung und Auswertung der Datenbank ausführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Befehle einer Datenmanipulations- und Abfragesprache (DML, SQL) zur Manipulation, Selektion und Auswertung von Datenbeständen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Aufgrund geänderter Anforderungen das Datenbankschema anpassen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt das schrittweise Vorgehen bei der Änderung eines Datenbankschemas und kann an Beispielen aufzeigen, welche Auswirkungen die Änderungen auf einen bestehenden Datenbestand haben können.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Data Management</dd>\n<dt>Objekt</dt>\n<dd>Datenbank für z.B. Kursverwaltung, Ausleihe von Filmen/Büchern, Mitgliederverwaltung, (ca. 5 Tabellen).</dd>\n<dt>Niveau</dt>\n<dd>3</dd>\n<dt>Voraussetzung</dt>\n<dd>Datenmodell implementieren</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('112', 'Im First Level Support arbeiten', 4, '', 1, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="1.10">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=171">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Im First Level Support arbeiten</dd>\n<dt>Kompetenz</dt>\n<dd>Im First Level Support Probleme und Bedürfnisse von Kundinnen und Kunden aufnehmen, klassieren und weiterleiten resp. geeignete Lösungsvorschläge unterbreiten.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Das Problem, die Ursachen des Problems oder die Bedürfnisse der Benutzerinnen und Benutzer zuvorkommend aufnehmen und durch gezielte Fragen strukturiert eingrenzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die verschiedenen Fragetechniken (offene Fragen, geschlossene Fragen) und kann aufzeigen, bei welchen Situationen in der Aufnahme und Analyse eines Problems sich diese im Gespräch mit den Kunden eignen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Methoden zur Eingrenzung von Fehlerursachen (z.B. Ausschlussprinzip, Entscheidungsbäume) und kann erläutern, wie diese eine systematische Analyse von Fehlern bzw. Problemen erlauben.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Auf emotionale Reaktionen von Kundinnen oder Kunden mit Verständnis reagieren und das Gespräch auf die Sachebene führen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die zentralen Anforderungen eines Kunden an eine Supportstelle (Zuverlässigkeit, Reaktionsfähigkeit, Kompetenz, Einfühlungsvermögen) und kann erläutern, mit welchem Verhalten resp. welchen persönlichen Voraussetzungen man diesen Rechnung trägt.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Regeln, die für eine klare und verständliche Ausdrucksweise zu berücksichtigen sind und kann an Beispielen erläutern, wie diese Regeln in der Kommunikation mit den Kunden eingesetzt werden.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die wichtigsten Verhaltensweisen von Kunden und deren Hintergrund und kann aufzeigen, wie man darauf korrekt reagiert.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Aufgrund der Zuständigkeiten im Support abwägen und entscheiden, ob eine Kundenanfrage selber behandelt oder an eine andere Stelle eskaliert werden muss.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Kategorien von Anfragen, die in einer Umgebung mit einer verbreiteten IT Unterstützung (PC/LAN, Office-Programme, Standardsoftware) beim Helpdesk eingehen und kann aufzeigen, wie sich diese hinsichtlich ihrer Komplexität und der Stelle, die üblicherweise für die Problemlösung zuständig ist, unterscheiden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Falls erforderlich das Problem und die Ursache nachvollziehbar beschreiben und als Problemlösungsauftrag (Ticket) an den Second Level Support weiterleiten (Eskalation).</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Kategorien von Informationen, die bei der Weiterleitung eines Problems an eine andere Stelle mitgeliefert werden müssen und kann erläutern, welchen Beitrag diese für eine effektive Problembehandlung leisten.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kann ein kundenorientiertes Vorgehen bei der Weiterleitung eines Gesprächs aufzeigen und erläutern, welchen Anforderungen der Kunden man mit den einzelnen Schritten Rechnung trägt.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Mit den vorhandenen Hilfsmitteln (Problemlösungsdatenbanken, Herstellerdokumentationen, Online-Hilfen etc.) die Problemlösung identifizieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die verbreiteten Hilfsstrukturen, die der Orientierung in einer Herstellerdokumentation oder in Online-Hilfen dienen (Thematische Gliederung, Index, Suchfunktionen etc.) und kann erläutern, wie damit das gezielte Auffinden von Hinweisen und Anleitungen unterstützt wird.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Der Benutzerin/dem Benutzer die Lösung verständlich erläutern oder sie/ihn mit gezielten Instruktionen bei der Problemlösung anleiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Regeln, die bei der Kommunikation und Anleitung von Problemlösungen (Fachbegriffe umschreiben, einfache Sätze, schrittweises Vorgehen etc.) einzuhalten sind und kann erläutern, wie damit eine effiziente und nachhaltige Problemlösung sicher gestellt wird.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>7</td>\n<td>Ein bisher nicht aufgetretenes Problem und die Problemlösung für die Problemlösungsdatenbank dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kann die Regeln aufzeigen, die bei der Dokumentation von Supportproblemen und den dazugehörigen Problemlösungen eingehalten müssen und kann erläutern, wie damit die Wiederverwendbarkeit dieser Lösungsansätze in vergleichbaren Problemsituationen sichergestellt wird.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Service Management</dd>\n<dt>Objekt</dt>\n<dd>Kundinnen oder Kunden mit Hard- und/oder Softwareproblemen.</dd>\n<dt>Niveau</dt>\n<dd>1</dd>\n<dt>Voraussetzung</dt>\n<dd>keine</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('114', ' Codierungs-,  Kompressions- und Verschlüsselungsverfahren', 3, '', 2, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=412">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Codierungs-, Kompressions- und Verschlüsselungsverfahren einsetzen</dd>\n<dt>Kompetenz</dt>\n<dd>Codierungs-, Kompressions- und Verschlüsselungsverfahren einsetzen</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Codierungen von Daten situationsbezogen auswählen und einsetzen. Aufzeigen, welche Auswirkung die Codierung auf die Darstellung von Daten hat.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Typen von Binärcodes (z.B. ASCII, ANSI-, BCD-, EAN-,1-aus -n-, UTF, Uni-Code) und kann anhand ihrer Merkmale (Zeichenvorrat, Redundanz) aufzeigen, wie sich diese hinsichtlich der Bewertbarkeit, Fehlererkennbarkeit und Rechenfähigkeit unterscheiden.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die wichtigsten Eigenschaften von Bildern (S/W-Strichzeichnung, Farbfoto, bewegte/nicht bewegte Bilder, vektorisiert/pixelorientiert usw.) und kann erläutern, wie damit die Bildqualität (Auflösung, Farbtiefe), der Bildaufbau und der Speicherbedarf beeinflusst werden kann.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt binäre, oktale und hexadezimale Zahlensysteme. Kennt logische Operationen (OR, AND, NOT) und kennt die Verfahren zur Umrechnung in das jeweils andere Zahlensystem.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Verfahren zur binären Kodierung von Zahlen (z.B. negative Zahlen/Zweierkomplement, Gleitkommazahlen).</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt Verfahren zur Repräsentation von Farben (z.B. RGB, CMYK).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kompressionsverfahren gemäss Vorgaben für die Aufbewahrung, Wiederherstellung und Übertragung von Daten auswählen und einsetzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Merkmale (Kompressionsrate, Qualitätsverlust) für gängige verlustlose und verlustbehaftetet Kompressionsverfahren.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt ausgewählte Normen und Standards (z.B. JPEG, PNG, MPEG, H261/263, Huffmann-Verfahren) und kennt typische Anwendungsbereiche wo diese eingesetzt werden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Verschlüsselungsverfahren zur Sicherung von Daten gemäss Vorgaben gegen unbefugten Zugriff auf Datenspeicher und Übertragungswegen auswählen und einsetzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt das grundsätzliche Prinzip der Verschlüsselung von Informationen (Kryptografie/Steganografie) und kann anhand eines einfachen Verschlüsselungskonzepts aufzeigen, wie damit Informationen chiffriert und dechiffriert werden können.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Gesicherte Übertragungsverfahren für Dateien mit asymmetrischen und symmetrischen Verschlüsselungsverfahren nutzen. Dabei Aspekte wie Public/Private Key, Zertifikate, Protokolle und Standards berücksichtigen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die prinzipiellen Unterschiede zwischen einer symmetrischen und asymmetrischen Verschlüsselung (Passwörter, private und öffentliche Schlüssel) und kann erläutern, wie sich diese auf den Grad der Datensicherheit auswirken.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt das Prinzip elektronischer Signatursysteme und kann anhand von Beispielen aufzeigen, wie damit die Sicherheit (Authentizität, Integrität) der Übermittlung gewährleistet werden kann.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt den Zweck digitaler Zertifikate und kann an Beispielen erläutern, wie damit das Vertrauen zwischen Anbieter und Bezüger (einer Leistung, eines Produktes) sichergestellt werden kann.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>IT Sicherheit</dd>\n<dt>Objekt</dt>\n<dd>Zu speichernde und zu übertragende Daten.</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>keine</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('117', ' Informatik- und Netzinfrastruktur für ein kleines Unternehmen realisieren', 6, '', 1, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=414">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Informatik- und Netzinfrastruktur für ein kleines Unternehmen realisieren</dd>\n<dt>Kompetenz</dt>\n<dd>Lokales Netzwerk ohne zentrale Benutzerverwaltung installieren.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Mit dem Auftraggeber die Anforderungen an das zu installierende Netzwerk aufnehmen und die gewünschten Dienste/Services bestimmen (File, Print, Internet).</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die verbreiteten lokalen Netzwerkdienste und kann aufzeigen, welche Anforderungen an ein Netzwerk sich daraus ergeben.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Logischen und physischen Aufbau des Netzwerks gemäss betrieblichen Anforderungen und räumlichen Verhältnissen definieren und in einem Netzwerkschema und einem Verkabelungsplan abbilden.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die grundsätzlichen Informationen, die aus einem einfachen Netzwerkschema hervorgehen müssen und kann aufzeigen, wie diese abgebildet werden können.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die wichtigsten Regeln für eine korrekte Netzwerkkonfiguration (IP-Adressformat, Subnetmaske, Klassen, private Adressen, Standardgateway, DNS-Server) und kann diese anhand von Beispielen erläutern.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die prinzipiellen Aufgaben der Netzwerkkomponenten Switch, Accesspoint und Router und kann aufzeigen, wo und zu welchem Zweck diese in einem Netzwerk eingesetzt werden.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die verbreiteten technologischen Möglichkeiten zur Erstellung eines Internetzugangs und kann erläutern, welche Konsequenzen diese für die Nutzung des Internets und die daraus resultierenden Kosten haben.</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt gängige Kabeltypen, Steckertypen und Ethernet-Varianten (z.B. Twisted Pair, UTP, STP, Glasfaser, RJ45, 100BaseTX, 1000BaseFX, 1000BaseTX etc.) und kann aufzeigen, bei welche Anforderungen hinsichtlich Leistung und bei welchen räumlichen Gegebenheiten diese zum Einsatz kommen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Arbeitsschritte für die Installation und Konfiguration planen, die den Aufbau des Netzwerks und die baulichen Verhältnisse berücksichtigen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt relevante bauliche Gegebenheiten und Installationsmöglichkeiten hinsichtlich der Netzwerk-Verkabelung und kann deren Auswirkungen auf Installationsaufwand, Zugänglichkeit für den Unterhalt und Kosten aufzeigen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Netzwerk aufbauen und die Netzwerkkomponenten gemäss Herstellerdokumentationen installieren und konfigurieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die notwendigen Einstellungen der Netzwerkkonfiguration der Netzwerkkomponenten (z.B. Computer, Router, Accesspoint) und kann aufzeigen, welchen Beitrag diese zur Sicherstellung der Kommunikation im Netzwerk leisten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Ressourcenverwaltung (Gruppen, Shares) und File-/ Printsharing definieren und dabei die Vorgaben hinsichtlich Datensicherheit und Verfügbarkeit berücksichtigen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die prinzipiellen Vorkehrungen, die Netzwerkbetriebssysteme für die Ressourcenzuteilung bieten (Lese-, Schreibrecht, Benutzer, Benutzergruppen, Shares) und kann aufzeigen, wie diese die Sicherheit von Daten gewährleisten.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Möglichkeiten, die Vergabe von Rechten zu dokumentieren (z.B. Matrix der Beziehungen zwischen Benutzergruppen und Shares) und kann aufzeigen, wie damit eine korrekte Vergabe der Rechte erleichtert wird.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Netzwerk und angeschlossene Systeme testen und die Testergebnisse dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Symptome der wichtigsten Fehler in einem Netzwerk und kann mögliche Ursachen (Konfigurationsfehler, Fehler bei der Verkabelung etc.) dafür beschreiben.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die wichtigsten Informationen in der Dokumentation eines einfachen Netzwerks und kann erläutern, wie diese für die Wartung und den Betrieb benötigt werden.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt den Zweck und die Funktionen der Schichtenmodelle (OSI, TCP/IP-Modell) und kann die verwendeten Protokolle sowie Netzwerkkomponenten den entsprechenden Schichten zuordnen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Network Management</dd>\n<dt>Objekt</dt>\n<dd>Lokales Netzwerk ohne zentrale Benutzerverwaltung mit bis zu 10 Anschlüssen und Internetanschluss, das Computer und Drucker in verschiedenen Räumen des gleichen Gebäudes miteinander verbindet.</dd>\n<dt>Niveau</dt>\n<dd>1</dd>\n<dt>Voraussetzung</dt>\n<dd>keine</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('120', ' Benutzerschnittstellen implementieren', 2, 'API', 4, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=432">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Benutzerschnittstellen implementieren</dd>\n<dt>Kompetenz</dt>\n<dd>Benutzerschnittstellen für eine Applikation entwerfen und implementieren. Dabei Standards und ergonomische Anforderungen beachten.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Für grafische Benutzerschnittstellen vorgegebene Standards und Ergonomieanforderungen anwenden.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Regeln für die Gestaltung einer Benutzerschnittstelle und weiss, wie diese nach ergononmischen Kriterien aufgebaut wird.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Komponenten und entsprechende Konventionen für die Gestaltung von Benutzerschnittstellen (z.B. Menu-Darstellung).</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt den Nutzen von Style Guides.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt mehrere Möglichkeiten zur gezielten Integration von Hilfefunktionen in einer Benutzerschnittstelle.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Geeignete Entwurfsmuster zur Trennung von Präsentations- und Anwendungslogik einsetzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Kriterien zur Trennung von Präsentations- und Anwendungslogik.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt das MVC-Entwurfsmuster und dessen Funktionsprinzip.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Benutzerschnittstellen implementieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt das Implementationskonzept der View-Komponenten einer Programmierumgebung und deren Einsatz.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt das Konzept der Ereignisverarbeitung.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt den Einsatz von MVC in einer Benutzerschnittstelle.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Ergonomie (Benutzerfreundlichkeit) und Funktionalität der Benutzerschnittstellen prüfen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die grundlegenden Schritte, die bei einem Test durchlaufen werden müssen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Kriterien zum Testen der ergonomischen Aspekte einer Benutzerschnittstelle (Usability).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Application Engineering</dd>\n<dt>Objekt</dt>\n<dd>Ergonomische Benutzerschnittstelle (Bildschirmmaske, Printout).</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>keine</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('121', ' Steuerungsaufgaben bearbeiten ', 6, '', 2, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=434">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Steuerungsaufgaben bearbeiten</dd>\n<dt>Kompetenz</dt>\n<dd>Steuerungsaufgabe aus einer Vorgabe identifizieren, analysieren und als Steuerung oder als Regelkreis realisieren.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Einen zu steuernden Prozess analysieren, die erforderlichen Elemente für die Steuerung bestimmen und diese in einer Prinzipskizze dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den Aufbau häufig eingesetzter Sensoren und Aktoren und deren grundsätzliche Funktionsweise.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Kriterien für die Auswahl von Sensoren und Aktoren, sowie deren mögliche Einsatzgebiete.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Elemente für die schematische Darstellung von Steuerungen und Regelungen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>System auf der Grundlage der Prinzipskizze in Form einer Schaltung entwerfen oder als Simulation realisieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Arten, Aufbau und Verhalten von offenen Steuerungen und Regelkreisen, sowie deren Unterscheidungsmerkmale.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Grundprinzipien für die Steuerung von Abläufen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Möglichkeit mittels Zustandsdiagrammen Steuerungsaufgaben zu beschreiben.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Geeignete Elemente für die Steuerung inkl. Aktoren und Sensoren auswählen und zu einem System zusammenbauen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wesentlichen Komponenten zur Realisierung einfacher Steuerungen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Möglichkeiten, wie einfache Steuerungen simuliert oder realisiert werden können.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Die Testfälle aus den Anforderungen ableiten, die Funktionen des Systems testen und Fehler finden und korrigieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Methodik, systematisch aus Anforderungen Testfälle zu definieren, sowie Grenzzustände zu berücksichtigen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Technical Software Engineering</dd>\n<dt>Objekt</dt>\n<dd>Prozess, der mit Sensoren und Aktoren gesteuert wird (Ampelsteuerung, Liftsteuerung, Temperaturregelung, etc.).</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>keine</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', '');
INSERT INTO `ict_module` (`modul_nr`, `modul_name`, `id_fachschaft`, `bereich`, `semester`, `lbv`, `lessons_ifz`, `lessons_uifz`, `bivo`, `hanoks`, `todo`) VALUES
('122', ' Abläufe mit einer Scriptsprache automatisieren', 5, '', 2, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=436">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Abläufe mit einer Scriptsprache automatisieren</dd>\n<dt>Kompetenz</dt>\n<dd>Abläufe mit Scripts in der Systemadministration automatisieren.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Zu automatisierende Funktion oder zu automatisierenden Ablauf mit den dazugehörigen Benutzerinteraktionen als Ablaufstruktur (z.B. Programmablaufplan) grafisch darstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Beispiele von Automatisierungsaufgaben.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Kriterien, die für die Beurteilung des Automatisierungspotentials von Bedeutung sind.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt grundlegende Kontrollstrukturen und deren Einsatz bei der Ablaufautomatisierung.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt das Vorgehen bei der Situationsanalyse und weiss, welchen Beitrag diese zur Definition eines klaren Auftrags liefert.</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt das Vorgehen zur grafischen Darstellung von Abläufen.</td>\n</tr>\n<tr>\n<td>6</td>\n<td>Kennt das Vorgehen zur Realisierung von Scripts in der Systemadministration.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Ablaufstruktur mit Hilfe einer Scriptsprache umsetzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt grundlegende Funktionalitäten der eingesetzten Scriptsprache.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Script in eine Systemumgebung integrieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt gängige Sicherheitsmassnahmen für den Einsatz von Scripts.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Integrationsmöglichkeiten von Scripts im eingesetzten Betriebssystem.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Script auf eine vollständige und korrekte Ausführung der erforderlichen Funktionalität bzw. des Ablaufs testen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt ein Testverfahren für Scripts.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt das Testvorgehen zur Integration von Scripts.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Dokumentation für den Einsatz des Scripts erstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Elemente einer Dokumentation für die involvierten Rollen (z.B. System, Administrator, Entwickler)</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Bedeutung der Dokumentation in Bezug auf Qualitätssicherung und Wartbarkeit.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Service Management</dd>\n<dt>Objekt</dt>\n<dd>Funktionen und Abläufe mit Kontrollstrukturen (Sequenz, Selektion und Iteration) mit dazugehörigen Benutzerinteraktionen.</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>Modul 403 Programmabläufe prozedural implementieren\nModul 404 Objektbasiert programmieren nach Vorgabe\nModul 304 Einzelplatz-Computer in Betrieb nehmen \nModul 305 Betriebssysteme installieren, konfigurieren und  administrieren</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('123', ' Server in Betrieb nehmen', 5, 'SYS', 3, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="2.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=416">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Serverdienste in Betrieb nehmen</dd>\n<dt>Kompetenz</dt>\n<dd>Verschiedene Serverdienste für den lokalen Netzwerkbetrieb nach Vorgaben installieren und konfigurieren. Funktionalität der Serverdienste überprüfen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Die Konfiguration der verlangten Serverdienste (Verzeichnisdienst, DHCP, DNS, File, Print) gemäss den betrieblichen Vorgaben definieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die grundsätzlichen Aufgaben eines Servers (Verzeichnisdienst, DHCP, DNS, File, Print) und kann die Vorteile einer zentralen Benutzerverwaltung erläutern.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die verbreiteten Typen von Serverbetriebssystemen und Serverdiensten (File-, Print-, Web-, Mail-Server etc.) und kann erläutern, welche Funktionalität diese in einem Netzwerk zur Verfügung stellen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Anforderungen an einen Server bezüglich Leistung, Verfügbarkeit und Energieeffizienz und kann erläutern, welche Systemkomponenten (Server-Hardware, -Software) mit ihren spezifischen Eigenschaften zur Sicherstellung dieser Anforderungen beitragen.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die Vorteile und Nachteile von dedizierten und nichtdedizierten sowie cloud-basierten Serverdiensten.</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt den prinzipiellen Unterschied zwischen einer dynamischen und einer statischen Zuweisung von IP Adressen und kann aufzeigen, in welchem Anwendungsfall eine dynamische resp. eine statische Adress-Zuweisung sinnvoll ist.</td>\n</tr>\n<tr>\n<td>6</td>\n<td>Kennt die Einstellungen zur Konfiguration eines DHCP Servers und kann erläutern, wie diese Einstellgrössen die Zuweisung einer IP Adresse, einer Subnet-Maske und allenfalls Angaben zu DNS-Servern und Standard-Gateways bei der Anfrage eines Clients beeinflussen.</td>\n</tr>\n<tr>\n<td>7</td>\n<td>Kennt die Einstellungen zur Konfiguration eines DNS-Servers und kann erläutern, wie diese Einstellgrössen das Verfahren bei der Umsetzung von Namen in IP Adressen (Namensauflösung mittels "Hosts", DNS) beeinflussen resp. sicherstellen.</td>\n</tr>\n<tr>\n<td>8</td>\n<td>Kennt die Einstellungen zur Konfiguration eines Print-Servers (Drucker-Spooler, Druckertreiber) und kann aufzeigen, welche Funktionalitäten bzw. welche Möglichkeiten die Einstellgrössen für die Nutzung und den Betrieb dieses Dienstes bereitstellen.</td>\n</tr>\n<tr>\n<td>9</td>\n<td>Kennt Kriterien (gemeinsame Nutzung, Datensicherheit/-Informationssicherheit Sensitivität etc.), die bei der Konzeption der Datenablage in einem Netzwerk zu beachten sind und kann erläutern, wie diese Kriterien den Entscheid, welche Daten zentral resp. dezentral abgelegt werden, beeinflussen.</td>\n</tr>\n<tr>\n<td>10</td>\n<td>Kennt die Einstellungen zur Konfiguration eines File Servers und kann aufzeigen, wie die Einstellgrössen die physische Datenablage (Datenträger, Verzeichnisse), die Nutzung der Datenablage (z.B. Volumen) oder den Datenzugriff (z.B. Rechte) im Netzwerk beeinflussen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Die Serverdienste gemäss den definierten Vorgaben installieren und konfigurieren. Die nicht erforderlichen Dienste deaktivieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Vorgehensweise bei der Inbetriebnahme von Serverdiensten (installieren, konfigurieren, starten, auf Client verfügbar machen, testen) und kann erläutern, welche technischen/logischen Abhängigkeiten mit dieser Abfolge berücksichtigt werden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Durch die notwendigen Anpassungen der Clients die verlangten Serverdienste den Benutzerinnen und Benutzern zur Verfügung stellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die notwendigen Netzwerkeinstellungen eines Clients in einer DHCP-/DNS-Serverumgebung und kann erläutern, welche Auswirkungen diese Einstellungen für den Betrieb eines Hosts/Clients in einem serverbasierten Netz haben.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Die grundlegenden Sicherheitsmassnahmen eines Server-Betriebssystems für den Zugriffsschutz gemäss den betrieblichen Vorgaben implementieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Möglichkeiten eines Server-Betriebssystems zur Gewährleistung und Absicherung des Zugriffs auf Netzwerk-Ressourcen (Benutzer-Authentifizierung, Benutzer-Autorisierung, Ressourcenanbindung) und kann an Beispielen erläutern, wie damit die Dienste, Konfigurationsdateien sowie weitere Daten vor unerlaubtem Zugriff geschützt werden können.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die wichtigsten Regeln und Vorgehensweisen bei der Definition von Passwörtern und kann aufzeigen, wie damit das "Hacken" von Passwörtern erschwert werden kann.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Die Verfügbarkeit und die Funktionalität der Serverdienste gegenüber den betrieblichen Vorgaben überprüfen und protokollieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Einträge in Log-Dateien und kann erläutern, wie diese Informationen (z.B. Status-, Fehlermeldungen) die Fehlersuche hinsichtlich Verfügbarkeit und Funktionalität der Dienste unterstützen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt ein Verfahren zum Testen und Protokollieren der installierten Serverdienste.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Die Dokumentation und die Abnahme/Betriebsfreigabe der eingerichteten Serverdienste vornehmen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Elemente eines Abnahmeprotokolls und kann deren Bedeutung für die Qualität der Leistung und den vollständigen Abschluss eines Auftrags darlegen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Struktur einer Betriebsdokumentation (Dienste, Konfigurationsdateien usw.) und kann die Bedeutung der einzelnen Elemente für die Administration des Servers und einen sicheren Betrieb darlegen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>System Management</dd>\n<dt>Objekt</dt>\n<dd>Computer mit Serverbetriebssystem in einem einfachen lokalen Netzwerk (eine Domäne, identische Clients).</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd><ul>\n<li>IP Adressierung </li>\n<li>Betriebssysteme installieren, konfigurieren und administrieren</li>\n</ul></dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('126', ' Peripheriegeräte im Netzwerkbetrieb einsetzen', 6, '', 2, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=440">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Peripheriegeräte im Netzwerkbetrieb einsetzen</dd>\n<dt>Kompetenz</dt>\n<dd>Netzwerkfähige Peripheriegeräte und dazugehörige Dienste in ein Netzwerk integrieren.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Integration von netzwerkfähigen Peripheriegeräten (mit direktem Netzwerkanschluss) für den Einsatz planen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt ein Verfahren für die Integration von Peripheriegeräten in ein Netzwerk (LAN, WLAN).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt das Prinzip der Abwicklung von Druckaufträgen über Warteschlangen (Spool) in Netzwerken.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Vorteile der gemeinsamen Nutzung von Netzwerkressourcen (z.B. Wirtschaftlichkeit)</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Nutzungskriterien und organisatorische Rahmenbedingungen (z.B. Berechtigungskonzept) für den Einsatz von Peripheriegeräten im Netzwerk.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Direkt über das Netzwerk angesprochenes Peripheriegerät installieren, konfigurieren, testen und in der Netzwerkdokumentation nachführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die notwendigen Konfigurationseinstellungen auf dem Peripheriegerät und dem Client um die Nutzung zu ermöglichen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Möglichkeiten um die Peripheriegeräte (Firmware, Treiber) zu aktualisieren.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt ein Testverfahren um das Peripheriegerät auf korrekte Funktionsweise zu überprüfen.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die Bedeutung einer nachgeführten Netzwerkdokumentation.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Über einen Netzwerkdienst angesprochenes Peripheriegerät sowie den dazugehörigen Dienst installieren, konfigurieren, testen und in der Netzwerkdokumentation nachführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die notwendigen Konfigurationseinstellungen für den Dienst um das Peripheriegerät auf dem Client zu nutzen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Möglichkeiten von Scripts für die automatisierte Einbindung und Nutzungsberechtigung von Peripheriegeräten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Funktionsweise netzwerkfähiger Peripheriegeräte verstehen und erläutern.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Funktionsweise verschiedener Peripheriegeräte (z.B. Drucker, Scanner, NAS, Webcam)</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Benutzeranleitung für netzwerkfähiges Peripheriegerät erstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Elemente einer Benutzeranleitung.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Hardware Management</dd>\n<dt>Objekt</dt>\n<dd>Netzwerkfähige Peripheriegeräte in einem LAN.</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>Ein kleines Netzwerk aufbauen</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('127', ' Server betreiben ', 5, 'SYS', 3, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=442">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Server betreiben</dd>\n<dt>Kompetenz</dt>\n<dd>Server (-Systeme) in einer bestehenden Umgebung betreiben und Performance, Verfügbarkeit  sowie Systemsicherheit überwachen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Mit Hilfe der Betriebsdokumentation sich in die bestehende Umgebung einarbeiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Inhalt, Aufbau und die Anwendung einer Betriebsdokumentation.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Bedeutung einer vollständigen und nachgeführten Betriebsdokumentation für das Sicherstellen der Wartbarkeit.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Änderungen am Zugriffs- und Berechtigungskonzept in der Betriebsdokumentation nachführen und Benutzer administrieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Möglichkeiten eines Betriebssystems, Zugriffsberechtigungen auf Ressourcen festzulegen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Für die Systemüberwachung und den Serverbetrieb verfügbare Hilfsmittel aufgabenorientiert einsetzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die im Betriebssystem integrierten Hilfsmittel zur Systemüberwachung und wendet diese an.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt ein weiteres Hilfsmittel zur Systemüberwachung und Auswertung.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Aufgaben, die bei der Administration von Servern anhand der Auswertung der Systemüberwachung (z.B. Reorganisation der Filestruktur) erforderlich sind.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Auf Servern Updates und Patches / Workarounds nach Vorgabe installieren und testen sowie die Betriebsdokumentation nachführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt das Vorgehen für die Integration von Updates, Patches und Workarounds.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Nach Vorgabe Hardwarestörungen beheben und Hardwareausbau durchführen sowie Betriebsdokumentation nachführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt das korrekte Vorgehen für Hardwareersatz und Hardwareausbau.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die typischen Störungen von Hardwarekomponenten (z.B. RAM, HD, USV, Netzteil, Lüfter).</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die RAID Technologie zur redundanten Speicherung der Daten auf verschieden Festplatten zur Steigerung der Sicherheit und Performance.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>System Management</dd>\n<dt>Objekt</dt>\n<dd>•    Server mit Print- und File-Services.\n•    Betriebsbereites LAN mit einigen Arbeitsstationen (Clients).</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>•    Serverdienste in Betrieb nehmen\n•    Vorgaben aus Systemhandbüchern beachten\n•    Umgang mit gesicherten Quellen im Internet (Lieferanten, Hersteller)</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('129', ' LAN-Komponenten in Betrieb nehmen', 6, '', 0, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=444">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>LAN-Komponenten in Betrieb nehmen</dd>\n<dt>Kompetenz</dt>\n<dd>Netzwerkkomponenten in einer LAN-Umgebung nach Vorgaben und unter Beachtung der Sicherheitsanforderungen in Betrieb nehmen, konfigurieren, testen und abnehmen lassen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Anforderungen für ein neues Netzwerk aufnehmen und die erforderlichen Netzwerkkomponenten bestimmen (Switch, Router).</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Gremien (IEEE, ISO) sowie die von ihnen definierten Standards (z.B. 100BaseT, IEEE 802.x).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die aktuellen LAN-Technologien und deren Einsatzgebiete und Funktionsweise.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Funktionsweise von Switch und Router und deren Einsatzgebiete.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Eigenschaften und Zusatzfeatures von Switches (z.B. manageable, stackable, auto-sense, spanning tree).</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt die Sicherheitsschwachstellen von Switch und Routern (z.B. Defaultpasswort, telnet).</td>\n</tr>\n<tr>\n<td>6</td>\n<td>Kennt Symbole zur schematischen Darstellung von Netzwerken.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Adressschema für IP Netz mit Subnetzen anpassen und geeignetes Subnetting mit zugehöriger Netzmaske aus Vorgaben ableiten (z.B. Aufteilung in IP Netze, Anzahl Clients).</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Elemente und Funktionen des IP-Protokolls (MAC- und IP-Adressen, IP-Adressklassen, private Adressen, Netzmasken, Routing, Adress Resolution Protocol (ARP)).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Gründe für die Aufteilung eines Netzwerks in IP-Subnetze.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Algorithmen zur (binären) Berechnung von IP-Subnetzen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Netzwerkkomponenten gemäss Netzwerkschema und Adressierung in Betrieb nehmen und konfigurieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die notwendigen Einstellungen für Router und Switch zur Sicherstellung der Kommunikation im Netzwerk.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Statisches Routing gemäss Netzwerkschema implementieren und Routing Tabelle interpretieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Unterschiede zwischen statischem und dynamischem Routing.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den Aufbau und den Inhalt von Routingtabellen und den Zusammenhang zum Netzwerkschema.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Konfigurationsfehler und ihre Ursachen mit geeigneten Hilfsprogrammen analysieren und beheben.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Verfahren zur systematischen Eingrenzung von Fehlern im Netzwerk (z.B. Ausschlussverfahren, Einordnung im OSI-Schichtenmodell).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Werkzeuge zur Fehleranalyse und –behebung und weiss, bei welchen Symptomen welche Werkzeuge eingesetzt werden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Netzwerkdokumentation erstellen bzw. nachführen (Konfiguration, Netzwerkschema).</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Aufbau und Inhalt einer Netzwerkdokumentation.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>7</td>\n<td>Netzwerk mit einem Abnahmeprotokoll dem Kunden übergeben.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den Aufbau und Inhalt eines Abnahmeprotokolls.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Network Management</dd>\n<dt>Objekt</dt>\n<dd>Switches, 3 Router und drei LAN hintereinander angeordnet</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>Modul 117 Informatik- und Netzinfrastruktur für ein kleines  Unternehmen realisieren\nErfahrungen im Aufbau von einfachen Netzwerken</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('130', ' LAN ausmessen und prüfen', 6, '', 0, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=446">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>LAN ausmessen und prüfen</dd>\n<dt>Kompetenz</dt>\n<dd>In einem Local Area Network (LAN) die Funktionalität überprüfen und die Performance messen. Die Ergebnisse interpretieren und allfällige Massnahmen einleiten. Störungen mit geeigneten Werkzeugen und Vorgehensweisen lokalisieren, zuordnen und beheben.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Funktionalität und Performance eines LAN im Betrieb mit geeigneten Werkzeugen überprüfen und messen. Die Ergebnisse protokollieren und beurteilen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Methoden und Werkzeuge zur Messung der Auslastung und Leistungsfähigkeit von Netzen (z.B. Referenz, Baseline Messung, Belastungssimulation, Trendanalyse).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Problematik des Datenschutzes und der Verletzung von weiteren Sicherheitsbestimmungen beim Einsatz von Analyse-Werkzeugen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die häufigsten Ursachen für Störungen der Zuverlässigkeit und Performance von Netzwerken.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Allfällige Massnahmen zur Verbesserung der Performance oder Störungsvermeidung vorschlagen, dokumentieren und umsetzen. Netzwerk- und Betriebsdokumentation nachführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Inhalt und Aufbau einer Netzwerkdokumentation um Wartung und Betrieb sicherzustellen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt bewährte Massnahmen zur Verbesserung der Leistung oder zur Vermeidung von Störungen hinsichtlich Zuverlässigkeit und Performance von Netzwerken.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Bei Störungen die Fehlermeldungen erfassen, interpretieren und Bericht erstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt das OSI-Schichtenmodell zur systematischen Störungssuche.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Inhalt und Aufbau einer Störungsbeschreibung.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Die Eigenschaften (elektrisch, elektromagnetisch oder optisch) und die Funktionalität von Netzwerk-Anschlusspunkten mit geeigneten Werkzeugen überprüfen und protokollieren (Layer 1).</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt häufige Störungen und deren Symptome, welche im OSI-Layer 1 auftreten.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Methoden und Werkzeuge zur Überprüfung von Medien und Verbindungen (z.B. visuelle Beurteilung, Geräte LEDs, Kabeltester, Netzwerkmessgeräte, Multimeter).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Protokolle und Kommunikationsabläufe im Netzwerk mit einem Protocol Analyzer untersuchen, protokollieren und Messwerte beurteilen (Layer 2-7).</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Funktion und die wichtigsten Header-Felder der Protokolle eines IP Netzes (z.B. Ethernet, IP, ARP, ICMP, TCP, UDP, DHCP, DNS)..</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt häufige Störungen und deren Symptome, welche im OSI-Layer 2-7 auftreten.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Methoden und Werkzeuge zur Analyse der Protokolle und Kommunikationsabläufe (Protocol Analyzer, Netzwerkmonitor).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Störung systematisch eingrenzen, mögliche Ursachen eruieren, Störung dokumentieren, beheben und Erfolg der eingeleiteten Massnahmen kontrollieren. Allenfalls erforderliche Eskalation veranlassen. Falls notwendig Netzwerk- und Betriebsdokumentation nachführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Vorgehensweise zur systematischen Eingrenzung von Störungen (z.B. Ausschluss intakter Teilsysteme).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den Prozess zur Eskalation von Störungen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Network Management</dd>\n<dt>Objekt</dt>\n<dd>LAN mit Routers, Switches, WLAN-Komponenten. \nMessgeräte, wie Kabeltester, Multimeter, Netzwerkmonitor, Traffic Simulator und Protocol Analyzer.</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>Kenntnisse im Aufbau und Betreiben von Client-/Server-Umgebungen und Local Area Network (LAN)</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('133', ' Web-Applikation mit Session-Handling realisieren', 1, 'API', 3, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=448">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Web-Applikation mit Session-Handling realisieren</dd>\n<dt>Kompetenz</dt>\n<dd>Web-Applikation gemäss Vorgabe mit einer Programmiersprache realisieren und testen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Vorgabe analysieren, Funktionalität entwerfen und Realisierungskonzept festlegen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Lösungsansätze, um basierend auf der Vorgabe Funktionalitäten auf Client- und Serverseite abzugrenzen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Elemente eines Realisierungskonzepts für Web-Applikationen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Vorteile der Trennung von Präsentation und Programmlogik und deren Umsetzung in einer Web-Applikation.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Spezifische Funktionalität einer Web-Applikation mit Session-Handling, Authentifizierung und Formularüberprüfungen realisieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Formularelemente und Funktionen zur Überprüfung von Benutzereingaben und wie diese gemäss Vorgabe eingesetzt werden.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt gängige Techniken zur Realisierung einer Sitzungsverfolgung.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Sicherheitsrisiken und Lösungsansätze zum Schutz einer Web-Applikation.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Möglichkeiten einfache Datenbestände zu verwalten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Web-Applikation mit einer einer Programmiersprache unter Berücksichtigung sicherheitsrelevanter Anforderungen programmieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Möglichkeiten zur Gewährleistung der  Authentifizierung und Autorisierung in Web-Applikationen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt mögliche Programmiersprachen für Web-Applikationen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Lösungsansätze zur Umsetzung geeigneter Architekturen für Web-Applikationen.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Möglichkeiten zur Strukturierung von Quellcode und die Umsetzung unter Berücksichtigung von Codierungs-Richtlinien.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Web-Applikation gemäss Testplan auf Funktionalität und Sicherheit überprüfen, Testergebnisse festhalten und allenfalls erforderliche Korrekturen vornehmen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Testverfahren und deren Beitrag zur Gewährleistung der Qualität von Web-Applikation.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Testverfahren um die Lösungsansätze gegen Cross-Site-Scripting, Skrip-Injection und Session-Hijacking zu prüfen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Web Engineering</dd>\n<dt>Objekt</dt>\n<dd>Web-Applikation mit Datenbestand (z.B. Gästebuch, Forum, Login, Umfrage usw.).</dd>\n<dt>Niveau</dt>\n<dd>3</dd>\n<dt>Voraussetzung</dt>\n<dd>• Einfache Webseiten erstellen \n• Erfahrung in der Verwendung von Programmiersprachen</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('141', ' Datenbanksystem in Betrieb nehmen', 3, 'SYS', 3, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=450">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Datenbanksystem in Betrieb nehmen</dd>\n<dt>Kompetenz</dt>\n<dd>Datenbanksystem installieren, konfigurieren, Urladung durchführen, Funktionalität sicherstellen und Übergabe in den produktiven Betrieb durchführen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Datenbanksystem nach Vorgaben (zB. Anzahl Benutzer, Speicherbedarf, Transaktionsvolumen, Verfügbarkeitsanforderungen) installieren und für den produktiven Betrieb einrichten bzw. vorbereiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Arten von Datenbanken (zB. relationale-, objektorientierte-, hierarchische, Verteilte DB) und deren typische Merkmale der physischen Datenorganisation.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt wichtige Parameter zur Konfigurierung eines Datenbanksystems und  deren Bedeutung für die Funktionalität und Performance.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt eine Vorgehensweise um ein Datenbanksystem nach Vorgaben umzusetzen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Datenbank und Tabellen nach Vorgabe einrichten. Testdaten laden und geforderte Funktionalität überprüfen. Urladung/Datenmigration vorbereiten, dokumentieren und durchführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die grundlegenden Aktionen und ihre Abfolgen, welche zum Aufsetzen einer Datenbank in einem Datenbanksystem notwendig sind.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den Normalisierungsprozess zur Überführung von logischen Schemas in Datenbankschemas.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Bedeutung von Funktionen und Komponenten des Data Dictionary (Katalog der Metadaten) in einem Datenbanksystem.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Datenbanksystem für den operativen Betrieb vorbereiten, Security-, Backup-, Restart- und Recovery-Prozeduren (Scripts) bereitstellen und testen. Systemtests (Volumen-, Stress- und Crash-Tests) durchführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Sinn und Zweck von Security-, Backup-, Restart- und Recovery-Prozeduren in einem Datenbanksystem für die Sicherstellung des operativen Betriebs.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die wesentlichen Schritte, die bei einem Test durchlaufen werden.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt spezifische Testmethoden für Datenbanksysteme zur Überprüfung von Performance und Verfügbarkeit.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Mittels Standardreports Performance und Verfügbarkeit überprüfen und interpretieren und. Erforderliche Optimierungsmassnahmen durchführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt mögliche Optimierungsmassnahmen zur Verbesserung der Performance und Verfügbarkeit.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Grundberechtigungen für den produktiven Einsatz einrichten und Standardreports in Form von Views, Stored Procedure endbenutzergerecht zur Verfügung stellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Vorgehensweise für die Inbetriebnahmeund Betriebsfreigabe einer Datenbank.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Möglichkeiten zur Vergabe von Zugriffsrechten.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt das Vorgehen um Business Logik in Stored Procedures abzubilden.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt das Vorgehen um Abfragen in Views zur Verfügung zu stellen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Übergabe in die operative Umgebung planen durchführen und Abnahmeprotokoll erstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den Inhalt und Aufbau einer Dokumentation (zB. Installations-, Lizenz-, Betriebs- und Wartungsdokumentation).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>System Management</dd>\n<dt>Objekt</dt>\n<dd>Datenbankserver in einer Client/Server Umgebung</dd>\n<dt>Niveau</dt>\n<dd>3</dd>\n<dt>Voraussetzung</dt>\n<dd>Datenmodelle (ERM) \nClient/Server Konzepte \nSQL Anwendung \nServer-Betriebssysteme</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', '<h3>Kritik Gabriel?</h3>'),
('143', ' Backup- und Restore-Systeme implementieren', 5, 'SYS', 0, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Backup- und Restore-Systeme implementieren</dd>\n<dt>Kompetenz</dt>\n<dd>Datensicherungskonzepte nach vorgegebenen Rahmenbedingungen erstellen, testen und freigeben.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Datensicherheitskonzept anhand der Rahmenbedingungen (zB. Datenmengen, Aufbewahrungsfristen, gesetzliche Vorschriften, Sicherungsintervall, Aufbewahrungssicherheit, Wiederherstellungsverfügbarkeit ) und technischen Vorgaben erstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt betriebliche Rahmenbedingungen und gesetzliche Vorschriften, die beim Einsatz von Datensicherungssystemen zu beachten sind.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt organisatorische, technische und personelle Risiken welche die Datensicherheit beeinflussen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt häufig verwendeten Datensicherungsverfahren (zB differentielles oder inkrementelles Backup) und deren Einsatz.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Kriterien  für einen effizienten und bedarfsgerechten Einsatz von Backup- und Restore-Systemen.</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt aktuelle technische Möglichkeiten um Daten zu sichern (zB. SAN, iSCSI, NAS, Cloud-Systeme).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Erstelltes Konzept auf Machbarkeit prüfen und gegebenenfalls überarbeiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt technische und betriebswirtschaftliche Kriterien, welche für die Überprüfung der Machbarkeit des Backup- und Restore-Konzepts zu beachten sind.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Anhand des Konzepts den Bedarf an Geräten und Speichermedien ermitteln.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Vorgehensweise für die Ermittlung des Speicherbedarfs.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Einsatzmerkmale von Backupgeräten und Speichermedien.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Sicherungsprozeduren erstellen, testen, in die produktive Ablaufsteuerung integrieren und dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Befehle, welche für die Erstellung von Sicherungs- und Wiederherstellungsprozeduren notwendig sind.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Verfahren um Sicherungs- und Wiederherstellungsprozeduren zu testen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt das Vorgehen um Sicherungs- und Wiederherstellungsprozeduren in den produktiven Betrieb zu integrieren und zu dokumentieren.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Sicherungs- und Wiederherstellungsprozesse durchführen und austesten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Szenarien um Sicherungs- und Wiederherstellungsprozesse durchzuführen und zu testen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Betriebs- und Wartungsdokumente nachführen. Backup- und Restore-Systeme für den produktiven Betrieb freigeben.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Notwendigkeit einer aktuell nachgeführten Betriebs- und Wartungsdokumentation.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Elemente einer Freigabemitteilung.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>System Management</dd>\n<dt>Objekt</dt>\n<dd>Backup- und Restore-Systeme, Applikation, Client-Server Umgebung.</dd>\n<dt>Niveau</dt>\n<dd>3</dd>\n<dt>Voraussetzung</dt>\n<dd>Bedienen und Überwachen von Servern/Rechnern \n<ul>\n<li>Sicherungs- und Sicherheitskonzepte </li>\n<li>Sicherungstechniken</li>\n</ul></dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n<small>Publiziert: 22.04.2015 11:25:27</small>\n</tab>\n</tabs>\n</tab>', ''),
('145', ' Netzwerk betreiben und erweitern ', 6, '', 0, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=454">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Netzwerk betreiben und erweitern</dd>\n<dt>Kompetenz</dt>\n<dd>Performance und Verfügbarkeit eines Netzwerks überwachen und Ergebnisse interpretieren. Netzwerke nach Vorgabe mit WLAN /VLAN erweitern und entfernte lokale Netze sicher verbinden.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Dokumentation eines Netzwerkes interpretieren und nachführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Aufbau und Inhalt einer Netzwerkdokumentation (Netzwerkplan und Netzwerkschema, Konfigurationen und Inventarlisten).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt gängige Darstellungsarten und Symbole für Netzwerkplan und Netzwerkschema.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Performance und Verfügbarkeit des Netzes mit Tools überwachen (Netzwerk Management System). Auswertungen/Logfiles interpretieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt mögliche Datenquellen (Netzwerkkomponenten und angeschlossene Endsysteme) für die Überwachung eines Netzwerkes.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Tools zur Überwachung von Netzwerken.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die relevanten Parameter zur Auswertung von Performance und Verfügbarkeit.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die wichtigsten Darstellungsarten für die erhobenen Daten und und weiss wie diese interpretiert werden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Bei Störungen, Fehlersymptome und -meldungen systematisch erfassen. Ursachen von Störungen mit Tools ermitteln und beheben.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Indizien bzw. Symptome, welche auf Störungen hinsichtlich Verfügbarkeit und Performance hinweisen können.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Methoden um Störungen systematisch zu ermitteln und beheben.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Netzwerke in virtuelle LAN aufteilen und konfigurieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Möglichkeiten zur physikalischen und logischen Gliederung eines Netzwerkes und ihre Auswirkungen auf Performance und Verfügbarkeit.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die VLAN Typen (tagbasiert, portbasiert und dynamisch) und typische Konfigurationsmöglichkeiten der Geräte.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Netzwerke mit Wireless LAN erweitern und mit gesichertem Zugang konfigurieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt WLAN Standards, Antennenarten und typische Einsatzgebiete. sowie deren Vorteile und Nachteile</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt WLAN-Sicherheitsmassnahmen</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Entfernte lokale Netze sicher verbinden.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Möglichkeiten und typische Einsatzgebiete für eine sichere Verbindung entfernter LANs.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Vorteile und Nachteile von VPN-Technologien für eine sichere Verbindung.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt den prinzipiellen Unterschied zwischen WAN-Access, WAN-Core-Netzwerk und VPN Verbindungen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Network Management</dd>\n<dt>Objekt</dt>\n<dd>Zwei lokale Netze, SNMP/VLAN fähige Netzwerkgeräte, WLAN Komponenten.</dd>\n<dt>Niveau</dt>\n<dd>3</dd>\n<dt>Voraussetzung</dt>\n<dd>LAN-Komponenten in Betrieb nehmen</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', '');
INSERT INTO `ict_module` (`modul_nr`, `modul_name`, `id_fachschaft`, `bereich`, `semester`, `lbv`, `lessons_ifz`, `lessons_uifz`, `bivo`, `hanoks`, `todo`) VALUES
('146', ' Internetanbindung für ein Unternehmen realisieren ', 6, 'SYS', 4, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=456">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Internetanbindung für ein Unternehmen realisieren</dd>\n<dt>Kompetenz</dt>\n<dd>Entwickeln, Planen und Realisieren von Internetanschlüssen für Unternehmen unter der Berücksichtigung von Sicherheits-, Verfügbarkeits- und Leistungsaspekten.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Internetanschluss nach Kundenvorgaben (Sicherheit, Performance, Verfügbarkeit und Wartung) bestimmen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Anforderungen an eine Internetanbindung (Bandbreite, Verfügbarkeit, Sicherheit und Wartung).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Sicherheits- und Überwachungsmassnahmen beim Betrieb eines Internetanschlusses.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Klassieren der eruierten Kundevorgaben nach Prioritäten und Bedeutung für das Unternehmen und in einem Pflichtenheft für die Evaluation eines Serviceproviders festhalten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Zugangsmöglichkeiten zum Internet sowie deren Anbieter (Provider).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Methoden um Kundenvorgaben zu klassieren.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Aufbau und Inhalt eines Pflichtenhefts.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt den Ablauf eines Evaluationsprozesses.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Resultate der Evaluation unter Berücksichtigung des Pflichtenheftes und wirtschaftlichen Aspekten bewerten und darstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Kriterien für die Bewertung eines Angebotes.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Darstellungsarten für die Beurteilung von Offerten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Netzwerkplan und Netzwerkschema für die Internetanbindung erstellen oder anpassen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Regeln für das Erstellen eines Namens- und Nummerierungskonzepts.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Funktionsweise von Firewall, DMZ, Proxy und DNS.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt gängige Darstellungsarten und Symbole für Netzwerkpaln und Netzwerkschemata.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Erforderliche Hardware- und Softwarekomponenten bestimmen und Beschaffungsantrag erstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Aufbau und Inhalt eines Beschaffungsantrags aus der durchgeführten Evaluation.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Inbetriebnahme der Internetanbindung realisieren und Abnahme durchführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt das Vorgehen für die Planung und Inbetriebnahme des Internetzugangs.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt das Vorgehen für die Übergabe des Systems in den operativen Betrieb.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Aufbau und Inhalt eines Abnahmeprotokolls.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Network Management</dd>\n<dt>Objekt</dt>\n<dd>Bestehendes Client/Server Netzwerk und neuer ISP Anschluss.</dd>\n<dt>Niveau</dt>\n<dd>3</dd>\n<dt>Voraussetzung</dt>\n<dd>•    Kenntnisse der wichtigsten Sicherheitsanforderungen im Bereich Internet\n•    Kenntnisse der Techniken zur Anbindung von lokalen Netzwerken ans Internet\n•    Erfahrung im Aufbau von lokalen Netzwerken</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('150', ' E-Business-Applikationen anpassen', 1, 'API', 4, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=458">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>E-Business-Applikationen anpassen</dd>\n<dt>Kompetenz</dt>\n<dd>E-Business-Applikationen gemäss Vorgabe und unter Beachtung der Sicherheitsvorschriften und technischer Rahmenbedingungen anpassen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Aufbau der Applikation, Transaktionskonzept, Applikationsumgebung und Rahmenbedingungen (Sicherheit, Performance, Verfügbarkeit, Transaktionsvolumen, usw.) erfassen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt mögliche Architekturen von Web-Applikationen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt wesentliche Elemente zur Gewährleistung der Sicherheit bei Web-Applikationen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Kriterien, welche die Performance und Verfügbarkeit einer Web-Applikationen beeinflussen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Vorgabe analysieren, clientseitigen, serverseitigen und datenbankseiti-gen Änderungsbedarf formulieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Schritte zur Formulierung eines Änderungsantrages (Request for Change - RfC).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt ein Vorgehen um die Auswirkungen von Änderungen auf die verschiedenen Komponenten einer Web-Applikation abzuschätzen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Auswirkungen der Änderungen auf Sicherheit und Schutzwürdigkeit der Informationen bei allen beteiligten Komponenten wie Client, Webser-ver, Applikationsserver und Datenbankserver überprüfen und doku-mentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Bestimmungen des Datenschutzes  und der Informationssicherheit und deren Bedeutung für Web-Applikationen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Methoden der Datenverschlüsselung und der Gewährleistung der Authentizität (HTTPS, Zertifikate).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Änderungen inklusive Implementierung und Test (funktional und nicht-funktional) gemäss einem vordefinierten Änderungsprozess planen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt ein Vorgehensmodell zur Planung von Änderungen einer Web-Applikation.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt das Test- und Abnahmeverfahren im Rahmen eines Änderungsprozesses.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt das Vorgehen bei der Einführung geänderter Applikationen im Rahmen eines Änderungsprozesses.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Änderungen realisieren, testen und dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt ein methodisches Vorgehen zur schrittweisen Anpassung von Applikationskomponenten inklusive der Verwaltung der Versionen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt ein Verfahren zur Migration und Einführung der geänderten Applikation.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt das Vorgehen zur Durchführung von Tests- und zur Abnahme der Änderungen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Web Engineering</dd>\n<dt>Objekt</dt>\n<dd>Online-Shop, Ticketing, Wiki oder E-Learning- Lösung.</dd>\n<dt>Niveau</dt>\n<dd>4</dd>\n<dt>Voraussetzung</dt>\n<dd>• Erfahrung mit client- und serverseitigen Skriptsprachen \n• Web-Applikationen mit Datenbanken realisieren</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('151', ' Datenbanken in Web-Applikation einbinden', 1, 'API', 4, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=460">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Datenbanken in Web-Applikation einbinden</dd>\n<dt>Kompetenz</dt>\n<dd>Eine bestehende Datenbank bzw. Datenbestand nach Vorgabe in einer Web-Applikation einbinden unter Berücksichtigung von technischen und sicherheitsrelevanten Anforderungen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Anforderungen an eine Web-Applikation und Gegebenheiten der einzubindenden Datenbank bzw. des Datenbestandes analysieren, Anbindungstechnik definieren und dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Möglichkeiten zur Analyse des Aufbaus einer Web-Applikation und das Zusammenspiel der Softwarekomponenten .</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Eigenschaften und Funktionen von Schnittstellen zu Datenbanksystemen und Datenbeständen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Schutz- und sicherheitswürdige Informationen unter Beachtung des Datenschutzes identifizieren und Massnahmen definieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Kriterien zur Bestimmung schutzwürdigen Informationen wie besonders schützenswert Personendaten und sensible Geschäftsdaten.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt organisatorische und technische Sicherheitsvorkehrungen zur Sicherung von Informationen/Daten und Web-Applikationen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Anbindung der Web-Applikation an die Datenbank bzw. an den Datenbestand realisieren und dabei Transaktionen, Datensicherheit und Datenschutz beachten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Script- und Programmiersprachen für Web-Applikation, sowie deren typischen Eigenschaften.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt gängige Techniken zur Umsetzung von Datenbank-Transaktionen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt das Vorgehen zur Gewährleistung der Datenintegrität, Verfügbarkeit, Authentizität und Vertraulichkeit in Web-Applikationen.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt eine Technik zum applikatorischen Locking von Datensätzen.</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt unterschiedliche Architekturen zur Einbindung von Datenbeständen bzw. Datenbanken in einer Web-Applikation.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Änderungswunsch gemäss vorgegebenem Änderungsvorgehen umsetzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Struktur und Merkmale eines Änderungskonzeptes und dessen Bedeutung für die vollständige Auftragsdurchführung.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Test- und Abnahmeverfahren definieren , durchführen und im Testprotokoll dokumentieren. Allenfalls erforderliche Korrekturen vornehmen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Test- und Abnahmeverfahren, die sich für eine übergreifende Prüfung einer Web-Applikation mit Datenbank eignen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt das Vorgehen zur Bestimmung von Prüf- und Abnahmekriterien aus einem Change Request.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Web Engineering</dd>\n<dt>Objekt</dt>\n<dd>Web-Applikation mit Daten aus einer relationalen Datenbank und/oder Schnittstelle zu einem Datenbestand.</dd>\n<dt>Niveau</dt>\n<dd>3</dd>\n<dt>Voraussetzung</dt>\n<dd>•    Seitenbeschreibungssprachen und Datenbankfunktionen einsetzen \n•    Erfahrung in der Entwicklung von Web-Applikationen \n•    Datenschutz und Sicherheitsvorschriften (gesetzliche, innerbetriebliche) anwenden</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('152', ' Multimedia-Inhalte in Webauftritt integrieren', 1, 'API', 3, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=462">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Multimedia-Inhalte in Webauftritt integrieren</dd>\n<dt>Kompetenz</dt>\n<dd>Multimedia-Elemente für Webauftritte nach Vorgabe erstellen und bestehende Multimedia-Inhalte aufbereiten, optimieren und in einen bestehenden Webauftritt integrieren.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Auftrag/Vorgabe für ein zu erstellendes Multimedia-Element analysieren, Voraussetzungen der Einbindung in einen Webauftritt untersuchen (Browser, Plattformen, Scriptsprachen, Sicherheitsvorschriften) und geeignetes Tool für die Realisierung auswählen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Möglichkeiten zur Evaluation eingesetzter Software bei einem Webauftritt und kann das Zusammenspiel der Software (Tools und Script-/Programmiersprachen, usw.) an Beispielen darlegen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die rechtlichen Bestimmungen des Urheberrechts und kann darlegen, welche Aspekte bei der Verwendung fremder Komponenten in einem Web-Auftritt zu beachten sind.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt gängige Dateiformate für Multimedia-Inhalte und kann aufzeigen für welche Art von Inhalten bzw. welche Art von Anwendungen sich diese eignen.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Grundfunktionen von Tools, mit denen Multimedia-Elemente generiert und bearbeitet werden können.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Webspezifische Multimedia-Inhalte mit geeigneten Werkzeugen erstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Techniken und Werkzeuge (Tools) zur Aufbereitung, Adaptierung und Optimierung von webspezifischen Multimedia-Inhalten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Prozeduren zur Aufbereitung, Optimierung und Integration von Bildern, Grafiken, Animationen, Tondokumenten oder Video-Inhalten erstellen und testen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Techniken zur Integration von Multimedia-Inhalten (Streaming, externe Inhalte, Scripts).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Befehle für die Automatisierung von Prozeduren, welche der automatischen Konvertierung und Integration von Multimedia-Inhalten dienen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Integrierte Multimedia-Inhalte auf unterschiedlichen stationären und mobilen Plattformen und mit verschiedenen Browsern testen, allenfalls adaptieren und freigeben.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Möglichkeiten die Eigenschaften von Browsern sowie deren Unterstützung von Multimedia-Inhalten zu eruieren.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt das Plug-In Konzept der Browser und kann dementsprechend Komponenten auswählen oder erstellen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Möglichkeiten, Multimedia-Inhalte auf stationären und mobilen Plattformen abzubilden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Web Engineering</dd>\n<dt>Objekt</dt>\n<dd>• Webauftritt mit Multimedia-Inhalten\n• Bebilderter oder mit Grafiken ausgestalteter Produktkatalog\n• Mit Multimedia-Inhalten angereicherte Shop-Lösung</dd>\n<dt>Niveau</dt>\n<dd>4</dd>\n<dt>Voraussetzung</dt>\n<dd>• Webauftritt erstellen und veröffentlichen \n• Gute Kenntnisse in der Webprogrammierung •</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('153', ' Datenmodelle entwickeln', 3, 'API', 3, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=464">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Datenmodelle entwickeln</dd>\n<dt>Kompetenz</dt>\n<dd>Kundenanforderungen für Informationen und Informationsbestände aufnehmen, analysieren und Datenmodell(e) entwickeln.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Informationsbedürfnisse und Anforderungen an die Datenhaltung zusammen mit dem Auftraggeber aufnehmen (z.B. Geschäftsfälle), analysieren, sowie Schutz- und Sicherheitsbedürfnisse der Daten definieren und dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die verbreiteten Methoden (z.B. Studium Dokumentationen, Interview) zur Erhebung von Informationen in einem Betrieb und kann erläutern, für welche Arten von Informationen und bei welchen betrieblichen Rahmenbedingungen diese geeignet sind.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Konzeptionelles Datenmodell erstellen und fehlende Informationen ermitteln bzw. Redundanzen klären.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die methodische Vorgehensweise bei der Entwicklung eines konzeptionellen Datenmodells und kann für die einzelnen Schritte erläutern, wie dabei vorgegangen wird.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Regeln, welche beim Spezialisieren bzw. Generalisieren von Objekten in Entitätsmengen und Beziehungen zu beachten sind.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Konzeptionelles Datenmodell in ein logisches überführen und Attribute, Identifikations- und Fremdschlüssel, Datentypen ergänzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die spezifischen Merkmale, welche ein Identifikationsschlüssel in einem Datenmodell erfüllen muss und kann erläutern, welche Verarbeitungsmöglichkeiten damit sichergestellt werden.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die erweiterten Konstruktionselemente des ER Modells (Rekursive Assoziation) und der Datenmodellierung (Generalisierung/Spezialisierung) und kann aufzeigen, bei welchen Sachverhalten diese in der Datenmodellierung eingesetzt werden.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Schritte der Normalisierung bis zur 3. Normalform und kann an Beispielen erläutern, welchen Beitrag diese zu einer redundanzfreien Datenhaltung beitragen.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die verschiedenen Kardinalitäten (Assoziationen) und kann erläutern, wie diese mittels der Definition von Primär- und Fremdschlüsseln in einem logischen Datenmodell umzusetzen sind. (aus 5.3)</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Konzeptionelles Datenmodell zusammen mit dem Auftraggeber überprüfen und allfällige Anpassungen vornehmen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den Einsatz einer Reviewtechnik um das Datenmodell mit dem Auftraggeber zu verifizieren.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Einsatzmöglichkeiten eines Prototyps, um in der Diskussion mit dem Auftraggeber die Anforderungen zu überprüfen und zu konkretisieren.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Für das vorliegende logische Datenmodell das physikalische Datenbankschema entwickeln und implementieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Möglichkeiten, wie die Zugriffszeiten auf eine Datenbank durch die Definition von Indizes und durch Denormalisierung (kontrollierte Redundanz) von Tabellen verbessert werden können und kann anhand von Beispielen erläutern, wie diese Vorkehrungen zur Verbesserung dieser Zugriffszeiten beitragen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Data Management</dd>\n<dt>Objekt</dt>\n<dd>Anspruchsvolle Kundenanforderungen (komplexe und rekursive Beziehungstypen).</dd>\n<dt>Niveau</dt>\n<dd>4</dd>\n<dt>Voraussetzung</dt>\n<dd>Datenmodell implementieren</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', '<h3>Toolbox besser an LB3 anpassen</h3>'),
('156', ' Neue Services entwickeln und Einführung planen', 4, 'SYS', 4, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="2.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=466">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Neue Services entwickeln und Einführung planen</dd>\n<dt>Kompetenz</dt>\n<dd>Einen neuen Service aus Kundenanforderungen ableiten, Produkte und Ressourcen evaluieren, den verlangten Service entwickeln und die Einführung planen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Auf Grund einer Kundenanforderung die benötigten Informationen recherchieren, die erforderlichen Produkte / Ressourcen evaluieren und ein Konzept zur Erbringung des verlangten Services erarbeiten. Dabei den qualitativen und quantitativen Nutzen bestimmen und die Servicekosten abschätzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Merkmale von Leistungen einer Serviceeinheit, die für die Zusammenarbeit zwischen Leistungserbringer und Leistungsempfänger relevant sind.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den betriebswirtschaftlichen Nutzen einer differenzierten Spezifikation eines Services.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt ein bewährtes Vorgehen zur Definition von Services. .</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt technische, organisatorische, kalkulatorische und rechtliche Aspekte, die zu einer verbindlichen Spezifikation von Services zwischen Leistungserbringer und Leistungsempfänger beitragen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Das Konzept den zuständigen Gremien präsentieren und einen Entscheid für die Realisierung herbeiführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Aufbau und Inhalt einer professionellen Präsentation.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Aufbau und Inhalt der notwendigen Dokumente um dem zuständigen Gremium einen Entscheid zu ermöglichen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>In Zusammenarbeit mit Fachspezialisten die für das Service Level Management massgebenden Leistungsziele und wirtschaftlichen Ziele ausarbeiten. Dabei Sicherheitsanforderungen, Risiken und das Verfahren für den Störungsfall beachten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Regeln welche bei der erfolgreichen Zusammenarbeit mit Fachkräften aus einem anderen Gebiet zu beachten sind.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt spezifische Risiken und deren Merkmale, die bei der Erbringung von Services auf Seite Leistungserbringer und Leistungsempfänger bestehen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt eine Methode um das Verfahren im Störungsfall abzubilden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Messkriterien für die Serviceüberwachung und Serviceverrechnung vorschlagen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt einen Überwachungsprozess zur Beurteilung der Serviceleistung.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Kriterien für die Kostenüberwachung von Dienstleistungen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Den Service entwickeln, dokumentieren, Testumgebung aufsetzen   und die Einführung planen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Dokumentationsformen für die Beschreibung eines Services/einer Dienstleistung.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Testverfahren zur Überprüfung der Funktionalität, Integrität und Wirtschaftlichkeit von Services.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Aspekte, die bei der Übergabe eines Services in den operativen Betrieb beachtet werden müssen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Service Management</dd>\n<dt>Objekt</dt>\n<dd>IT-System in einem KMU mit neuem Bedarf.</dd>\n<dt>Niveau</dt>\n<dd>4</dd>\n<dt>Voraussetzung</dt>\n<dd>IT Kleinprojekte abwickeln</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('157', ' IT-System-Einführung planen und durchführen', 4, 'SYS', 3, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=468">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>IT-System-Einführung planen und durchführen</dd>\n<dt>Kompetenz</dt>\n<dd>Ablösung bzw. Erstinstallation eines IT-Systems (Server, Einzelplatz-Computer, Betriebssysteme) gemäss Auftrag, planen, initialisieren und durchführen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Auftrag, Kundenanforderungen, Anwendungen, Netzwerk- und Plattformstrategie sowie Soll-Anforderungen analysieren, Machbarkeit klären und Prototyp der Plattform entwerfen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Elemente eines vollständigen Arbeitsauftrags (z.B. Zielsetzungen, Rahmenbedingungen, Termine, Ressourcen, Skills).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Schritte des Evaluationsprozesses bei der Beschaffung von Hardware und Software.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt das Vorgehen zur Spezifikation einer Hardwareplattform (z.B. Server, Netzwerkkomponenten, Einzelplatz-Computer) und der dazugehörenden Betriebssysteme.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die Aspekte, die bei der Migration auf eine neue Hardwareplattform zu beachten sind.</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt ein Verfahren zur konzeptionellen Darstellung eines IT-Systems.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Vorgehen planen: Betriebswirtschaftliche Rahmenbindungen, Termine, bevorzugte Lieferanten und erforderliche Ressourcen (intern/extern) bestimmen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Rahmenbedingungen, die hinsichtlich der zeitlichen Machbarkeit eines Auftrages (z.B. Anzahl Mitarbeitende, Verfügbarkeit der Mitarbeitenden, Anzahl Arbeitstage bis zum Endtermin, Verfügbarkeit der notwendigen Sachmittel) zu berücksichtigen sind.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die wichtigsten Rahmenbedingungen, die hinsichtlich der inhaltlichen Machbarkeit eines Auftrages zu berücksichtigen sind (z.B. Kompetenzen der Projektmitarbeiter, technologische und betriebswirtschaftliche Voraussetzungen).</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Aufbau und Inhalt einer Planung.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Testvorgehen für den Nachweis der Funktionalität und Sicherheit ausarbeiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Testverfahren für den Nachweis der Funktionalität und Sicherheit.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Notwendigkeit von Integrationstests in einer realen oder virtuellen Testumgebung und deren Bedeutung für die Risikominimierung.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Rollout Planung erstellen, Termine festlegen und genehmigen lassen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Aufbau und Inhalt einer Rollout Planung (z.B. Ausgangs- und Zielsystem, Zeitplanung, örtliche Gegebenheiten, Migration, Beteiligte, Schulung, Support).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt das Genehmigungsverfahren bei der Bearbeitung von Aufträgen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Pilotinstallation aufbauen, Funktionalität und Sicherheit testen, Plattformsoftware bzw. Softwareverteilung erproben, Datenmigration testen und Abnahmetest mit dem Auftraggeber durchführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Vorgehensweise für die Planung und Durchführung einer Pilotinstallation.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt ein Verfahren um den personellen und materiellen Ressourcenbedarf für den Rollout auf der Basis einer Pilotinstallation zu ermitteln.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Vorgaben für die Durchführung und Dokumentation eines Abnahmetests.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Rollbackverfahren, Anwenderinstruktionen und Servicedesk-Informationen zusammen mit dem Auftraggeber ausarbeiten und dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Vorkehrungen zur Sicherstellung eines Rollback-Verfahrens, welche vor dem Start des Roll out getroffen werden müssen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die begleitenden Massnahmen für den Rollout sowie Aufbau und Inhalt der entsprechenden Dokumentation (z.B. Rollback, Anwenderinstruktion, Servicedesk-Information).</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die wesentlichen Schritte, welche bei einer Anwenderinstruktion durchlaufen werden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>7</td>\n<td>Rollout einleiten und durchführen bzw. durchführen lassen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Auftragsinhalt und -aufbau für den Rollout.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Hardware Management</dd>\n<dt>Objekt</dt>\n<dd>Einzelplatz-Computer, Server, Betriebssysteme, Installationssoftware und Tools für die Softwareverteilung im KMU-Bereich mit ca. 20-50 Clients.</dd>\n<dt>Niveau</dt>\n<dd>4</dd>\n<dt>Voraussetzung</dt>\n<dd>Aufträge im IT-Umfeld selbstständig durchführen \nHardware- und Software für Einzelplatz-Computer beurteilen und beschaffen \nErfahrung im Umgang mit Servern und Systemsoftware</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('158', '  <span style="text-decoration:line-through;">Software-Migration planen und durchführen</span>', 4, '', 0, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=470">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Software-Migration planen und durchführen</dd>\n<dt>Kompetenz</dt>\n<dd>Migrationen für Versionswechsel oder Softwareablösung planen und durchführen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Auf Grund eines Auftrages die Migration für den Wechsel auf die neue Software-Version (Major Release) oder auf eine neue Software mit ähnlicher Funktionalität erarbeiten (Vorgaben, Ziele, Termine, Tätigkeiten, Ressourcen, technische Anforderungen). Dokumentation mit Release Informationen sichten und relevante Punkte für die Migration erkennen und festhalten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt eine bewährte Vorgehensweise für die Migration von Software.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt mögliche Risiken, welche bei der Migration auf eine neue Software bzw. auf eine neue Software-Version auftreten können.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Kriterien zur Bildung von Arbeitspaketen zur termingerechten und reibungslosen Abwicklung des Vorhabens.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Methoden zur Erkennung und Gegenüberstellung der für den Betrieb relevanten technischen Aspekte und Anforderungen einer Software (z.B. Systemumgebung, Datenmenge, Sicherheitsanforderungen).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Neue Version oder neue Software auf einem Testsystem installieren und sich mit den wesentlichen Unterschieden / Neuerungen vertraut machen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Kategorien von Merkmalen, die für einen systematischen Vergleich von Softwarereleases relevant sind.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Konfigurationsmerkmale, die für den Betrieb der neuen Software-Version oder Software relevant sind.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Zu migrierende Datenbestände analysieren, identifizieren und Funktionalität auf dem Testsystem mit der neuen Version bzw. der neuen Software erproben. Relevante Punkte für die Migration erkennen und festhalten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt ein bewährtes Verfahren zur Analyse von Datenbeständen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den Nutzen von Tests in einer realen oder virtuellen Testumgebung.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Bisherige Daten extrahieren, aufbereiten und ins neue System laden. Wo möglich die Vorgänge automatisieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Möglichkeiten Vorgänge mit einer Skriptsprache zu automatisieren.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt ein Vorgehen um Datenbestände zu migrieren.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>In einer Testumgebung die Funktionalität, Verfügbarkeit, Sicherheit und Vollständigkeit der Umstellung nachweisen und einen Freigabeantrag stellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt eine Vorgehensweise zum Testen der Migration in der Testumgebung.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Anforderungen für einen Freigabeantrag.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>User-, Betriebs- und Wartungsdokumentation anpassen, definitive Umstellung planen und bewilligen lassen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Aufbau und Inhalt der Dokumentation für die Wartung und den Betrieb von Software .</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die begleitenden Massnahmen für die Migration sowie Aufbau und Inhalt der entsprechenden Dokumentationsformen (z.B. Rollback, Anwenderinstruktion, Servicedesk-Information).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>7</td>\n<td>Umstellung durchführen oder durchführen lassen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Auftragsinhalt und -aufbau für die definitive Migration.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Service Management</dd>\n<dt>Objekt</dt>\n<dd>Neuer Major-Release einer Software inkl. Datenübernahme\nNeue Software für die Ablösung bestehender Software inkl. Datenübernahme</dd>\n<dt>Niveau</dt>\n<dd>4</dd>\n<dt>Voraussetzung</dt>\n<dd>Erfahrung im Umgang mit Scripts \nErfahrung in der Aufarbeitung von Datenbeständen</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('159', ' Directoryservices konfigurieren und in Betrieb nehmen', 5, 'SYS', 4, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=472">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Directoryservices konfigurieren und in Betrieb nehmen</dd>\n<dt>Kompetenz</dt>\n<dd>Directoryservices konfigurieren, Directorystrukturen implementieren, Benutzerdaten verwalten, Sicherheitseinstellungen nach Vorgabe vornehmen, Synchronisation sicherstellen, Directoryservices testen und an Betrieb übergeben.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Kundenbedürfnisse, vorhandene Services, abzugleichende Verzeichnisse (Synchronisation) und Kommunikationsprozesse analysieren und dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Struktur und Objekte, die in einem Directoryservice (Verzeichnisdienst) enthalten sind.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Prinzipien und die Auswirkungen der Synchronisation von Objekten zwischen Directory- und anderen Informationssystemen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Vorgaben und Ergebnisse auf Machbarkeit, Vollständigkeit und Funktionaliät überprüfen und in einem Directoryservices-Konzept abbilden.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Visualisierung von Datenstrukturen eines Directoryservices.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Möglichkeiten mit Hilfe des Lightweight Directory Access Protocol (LDAP) die Struktur eines Directoryservices abzufragen und zu verändern.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die grundlegende Möglichkeiten und Funktionalitäten eines Directoryservices (z.B. Objektklasse und Attribute).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Directoryservice gemäss definiertem Konzept konfigurieren und implementieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt das Prinzip eines sicheren Datenaustausches innerhalb eines Directoryservices.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Synchronisation der Directoryservices mit geeigneten Mitteln realisieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Elemente (z.B. Skriptsprache) zur Steuerung der Synchronisation.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Funktionalität der Directoryservices gemäss Anforderungen testen und Ergebnisse im Testprotokoll festhalten. Bei Bedarf, erforderliche Korrekturen vornehmen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Möglichkeiten die geforderten Funktionalitäten des Directoryservices sinnvoll zu testen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Bestehende Daten, falls erforderlich automatisiert, in den Directoryservice migrieren. Fehlerfreies Zusammenwirken mit anderen Diensten sicherstellen und testen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die grundlegenden Schritte, wie Daten aus bestehenden Systemen ins Directory migriert werden.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Möglichkeiten wie andere Dienste die Daten des Directoryservices abfragen können.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>7</td>\n<td>Übergabe in den operativen Betrieb planen, Abnahme vorbereiten und durchführen. Betriebs- und Wartungsdokumentation nachführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Struktur der für den Betrieb eines Directoryservices erforderlichen Dokumentationen (Betriebs- und Wartungsdokumentation).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den Ablauf einer Produktivsetzung eines Directoryservices.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>System Management</dd>\n<dt>Objekt</dt>\n<dd>Verzeichnisdienste in einer Client/Server Infrastruktur</dd>\n<dt>Niveau</dt>\n<dd>4</dd>\n<dt>Voraussetzung</dt>\n<dd>Erfahrung mit Client/Serverinfrastrukturen \nErfahrung mit LAN-Protokollen und dem Netzwerkbetrieb</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('182', ' Systemsicherheit implementieren ', 6, 'SYS', 3, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=474">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Systemsicherheit implementieren</dd>\n<dt>Kompetenz</dt>\n<dd>Server und Arbeitsplätze bezüglich Systemsicherheit untersuchen, Verbesserungsvorschläge ausarbeiten und umsetzen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Bestehende Systeme (Client, Server, Netzwerkkomponenten und Services)gezielt mit geeigneten Mitteln auf Sicherheitslücken und auf Konfigurationsmängel untersuchen</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Mittel und Methoden zum Aufspüren von Sicherheitslücken und Konfigurationsmängeln in Systemen (z.B. Portscanner, Hardening Tools)</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Bedeutung einer vollständigen Dokumentation in Bezug auf Systemsicherheit.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die häufigsten sicherheitsrelevanten Fehler bei der Konfiguration von Systemen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Auf Basis der gesammelten Informationen Massnahmen für die Systemsicherheit ausarbeiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die für Systeme relevanten Sicherheitseinstellungen auf der Ebene von Betriebssystem, Applikationen, Netzwerkkomponenten und Benutzern.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die für den Betrieb nötigen Dienste und deren Abhängigkeiten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Sicherheitsmassnahmen implementieren und dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Verfahren zur Aktualisierung von Lizenzen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt das Vorgehensprinzip und die Sicherheitsvorschriften zur Identifikation sicherer Quellen für Software-Updates und Patches.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Kriterien zur Überprüfung der Aktualität von Software und kennt die Folgen einer Nicht-Aktualisierung.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Standardverfahren zum Härten von Systemen</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt Möglichkeiten um die physikalische Sicherheit von Hardware zu gewährleisten.</td>\n</tr>\n<tr>\n<td>6</td>\n<td>Kennt das Prinzip der Restriktion von Benutzerberechtigung.</td>\n</tr>\n<tr>\n<td>7</td>\n<td>Kennt Möglichkeiten mittels Instruktion der Benutzer die Systemsicherheit zu erhöhen (z.B. Social Engineering, Passwortcontainer)</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Nach Vorgabe auf ein zuvor abgesichertes System ein Host basierendes Intrusion Detection Systems, HIDS installieren und konfigurieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Möglichkeiten mittels eines HDIS Systeminformationen zu sammeln.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Änderungen / Anpassungen bezüglich Sicherheit und Funktionsfähigkeit mit den zur Verfügung stehenden Log- und Systeminformationen sowie Informationen aus dem HIDS auf Wirksamkeit überprüfen. Falls erforderlich, Systemdokumentation nachführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den Inhalt sicherheitsrelevanter Logdaten des Systems und des HIDS.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt typische System-Anomalien (z.B. Datenvolumen, Zugriffe)</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Inhalte einer Systemdokumentation.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Implementierte Systemsicherheit überwachen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die bei einer Systemüberwachung sicherheitsrelevanten Prinzipien.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>IT Sicherheit</dd>\n<dt>Objekt</dt>\n<dd>Client, Server, Netzwerkkomponenten und Services  für einen KMU Betrieb.</dd>\n<dt>Niveau</dt>\n<dd>4</dd>\n<dt>Voraussetzung</dt>\n<dd>Betriebssysteme installieren und konfigurieren \nServersysteme betreiben \nPraxiserfahrung mit Netzwerkkomponenten und -protokollen</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', '');
INSERT INTO `ict_module` (`modul_nr`, `modul_name`, `id_fachschaft`, `bereich`, `semester`, `lbv`, `lessons_ifz`, `lessons_uifz`, `bivo`, `hanoks`, `todo`) VALUES
('183', ' Applikationssicherheit implementieren', 2, 'API', 4, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=476">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Applikationssicherheit implementieren</dd>\n<dt>Kompetenz</dt>\n<dd>Applikationen sicher planen, entwickeln und in Betrieb nehmen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Aktuelle Bedrohungen erkennen und erläutern können. Aktuelle Informationen zum Thema (Erkennung und Gegenmassnahmen) beschaffen und mögliche Auswirkungen aufzeigen und erklären können.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Informationsquellen zu aktuellen Bedrohungen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Sicherheitslücken und mögliche Folgen von Angriffen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Sicherheitslücken und ihre Ursachen in einer Applikation erkennen  können. Gegenmassnahmen vorschlagen und implementieren können.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt mögliche Ursachen von Sicherheitslücken in Applikationen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt ein Vorgehen zur Identifikation von Sicherheitslücken in Applikationen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt geeignete Gegenmassnahmen zu den verschiedenen Kategorien von Bedrohungen und wie diese implementiert werden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Mechanismen  für die Authentifizierung und Autorisierung umsetzen können.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Authentifizierungsmechanismen und deren Funktionsweise.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Verschlüsselungsmechanismen und deren Einbindung in der Applikation.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt verschiedene Verfahren zur Zugriffssteuerung.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Sicherheitsrelevante Aspekte bei Entwurf, Implementierung und Inbetriebnahme berücksichtigen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt sicherheitsrelevante Aspekte beim Entwurf von Applikationen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Techniken bei der Realisierung zur Vermeidung von Sicherheitslücken (z.B. Input/ Output Validierung, Defensives Programmieren, Session-Management, Error Handling).</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt ein Vorgehen für das sichere Deployment von Applikationen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Informationen für Auditing und Logging generieren. Auswertungen und Alarme definieren und implementieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Sinn, Aufbau und Inhalt eines Logs.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Sinn, Aufbau und Inhalt eines Audit-Trails.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt mögliche Formen der Alarmierung und Regeln für eine Alarmauslösung.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Application Engineering</dd>\n<dt>Objekt</dt>\n<dd>Web-Applikation mit Datenanbindung, Webservice</dd>\n<dt>Niveau</dt>\n<dd>4</dd>\n<dt>Voraussetzung</dt>\n<dd>Erfahrung im Entwickeln von Applikationen (Multi-User, Web, verteilte Applikationen)</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('184', ' Netzwerksicherheit implementieren', 6, '', 0, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=478">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Netzwerksicherheit implementieren</dd>\n<dt>Kompetenz</dt>\n<dd>Bei einem bestehenden Netzwerk einen sicheren Netzwerkzugang implementieren und den Netzwerkverkehr mit einem NIDS überwachen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Bestehendes Netzwerk gezielt mit Hilfe der Netzwerkdokumentation und geeigneten technischen Mitteln auf Sicherheitslücken und Konfigurationsmängel untersuchen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die erforderlichen Informationen für ein Asset Management/Inventar und kann diese zur Verifizierung einer Dokumentation auf Aktualität einsetzen. Kennt die grundlegenden Elemente einer Netzwerkdokumentation.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt verschiedene Netzwerkkomponenten und deren Sicherheitseinstellungen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt technische Grundlagen für den Remote Access.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Möglichkeiten des kontrollierten, sicheren WAN Zugangs.</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt technische Hilfsmittel zur Analyse (z.B. Portscanner, Sniffer) des Datenverkehrs im Netzwerk.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Erarbeiten eines Konzepts für externe Netzzugänge, definieren von Nutzungsrichtlinien ins WAN und festlegen der Technologie für den externen Zugang ins LAN.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Standardverfahren für die Härtung von Netzwerkkomponenten (z.B. Router, Firewall, Proxy).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt technische Verfahren für einen sicheren Remote Access (z.B. Protokolle, Standards, Technologien).</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt technische Verfahren für einen sicheren Datenverkehr LAN/WAN (z.B. Protokolle, Technologien, Richtlinien).</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt organisatorische Massnahmen zur Definition von Nutzungsrichtlinien.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Konfiguration der Sicherheitssysteme (z.B. Remote Access, Firewall, Proxy) gemäss erarbeitetem Konzept.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt verschiedene Sicherheitssysteme und Zugänge und deren Konfigurationsmöglichkeiten (z.B. Remote Access, Firewall, Proxy).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>NIDS nach Vorgaben installieren, konfigurieren und ins Netzwerk integrieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die technischen Möglichkeiten und die Funktionsweise eines NIDS.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Änderungen / Anpassungen bezüglich Sicherheit und Funktionsfähigkeit mit den zur Verfügung stehenden Log- und Systeminformationen sowie Informationen aus dem NIDS auf Wirksamkeit überprüfen. Falls erforderlich, Netzwerkdokumentation nachführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Kriterien und Inhalte von Logfiles aus Netzwerkkomponenten und dem NIDS. Sicherheitseinstellungen und Netzkonfigurationen beschreiben.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Verfahren aus den Logfiles sicherheitsrelevante Informationen zu erkennen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt aktuelle Exploits und kennt Vorgehensweisen diese in Bezug zu den gewonnenen Erkenntnissen zu setzen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Implementierte Netzwerksicherheit überwachen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Möglichkeiten die Informationen des NIDS im operativen Betrieb zu nutzen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>IT Sicherheit</dd>\n<dt>Objekt</dt>\n<dd>Kommunikationsnetz in einem KMU mit Internet Zugang, Remote Access für Support und für Mitarbeiter</dd>\n<dt>Niveau</dt>\n<dd>4</dd>\n<dt>Voraussetzung</dt>\n<dd>Praxis im Aufbau und in der Administration von lokalen Netzen \nPraxis im Umgang mit Netzwerkprotokollen \nPraxis im Umgang mit System-Sicherheit\nErfahrung im Umgang mit Netzwerkkomponenten</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('213', ' Teamverhalten entwickeln', 4, '', 0, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=480">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Teamverhalten entwickeln</dd>\n<dt>Kompetenz</dt>\n<dd>Persönliches Verhalten sowie die eigene Wirkung im Team wahrnehmen und reflektieren, alternative Handlungsmöglichkeiten erkennen und umsetzen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Normen und Entwicklungsphasen eines Teams wahrnehmen und mittels eigner Beiträge zum Gelingen des Teamworks beitragen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die verschiedenen Phasen der Teamentwicklung (Forming, Storming, Norming, Performing, Separation/Termination), sowie Rahmenbedingungen welche das Verhalten der Teammitglieder prägt und die Zusammenarbeit in diesen Phasen charakterisiert.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt das idealtypische Verhalten der Teammitglieder in den einzelnen Phasen der Teambildung.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Der jeweiligen Situation angepasst, mit Teamkolleginnen und Teamkollegen sach- und beziehungsorientiert kommunizieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Regeln der Kommunikation (Fachbegriffe umschreiben, einfache Sätze, inhaltlich logische Gliederung etc.), sowie die Wirkung dieser Regeln auf die Zusammenarbeit im Team.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Regeln für eine situationsgerechte Kommunikation (Ich-Botschaft, wenn eine klare Stellungnahme erwartet wird; vollständiges Feedback, wenn das Arbeitsergebnis eines Teammitglieds bewertet werden muss etc.).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Rollen im Team erkennen und eigene Beiträge zielorientiert in die Teamarbeit einbringen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die verschiedenen Rollen und ihre Verhaltensmerkmale, welche Mitglieder eines Teams einnehmen können (Integrator/-in, Querdenker/-in, Kritiker/-in etc.).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die unterschiedlichen Funktionen, welche Mitglieder eines Teams bei der Organisation einer effektiven Teamarbeit einnehmen können (Moderation, Protokollführung etc.).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Eigenes Verhalten im Team reflektieren, Alternativen überlegen, diese gegeneinander abwägen und bevorzugte Alternative begründen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt kritische Situationen aus persönlicher Sicht, die bei der Ergebniserarbeitung im Team auftreten und welches Verhalten der Betroffenen daraus resultieren könnte (Teammitglied schlägt Alternative zur eigenen Lösung vor, anderes Mitglied äussert sich abschätzig über einen eigenen Vorschlag etc.) .</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Strategien und Anwendungsbereiche, die eine möglichst optimale Berücksichtigung aller Ideen bei der Ergebniserarbeitung im Team erlauben (Win-Win Situation anstreben, Kompromissvorschlag unterbreiten etc.).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Spezifische Verhaltensweisen und Fähigkeiten wahrnehmen und in der Teamarbeit berücksichtigen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt geschlechter-, alters-, und kultur-spezifische Aspekte um konstruktives Kommunikations-, Konflikt- und Problemlöseverhalten zu erlangen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Konfliktpotentiale/Konflikte im Team erkennen und durch konstruktives Verhalten verhindern bzw. entschärfen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Merkmale des Verhaltens von Personen im Team, die auf einen bevorstehenden oder bereits aktuellen Konflikt hinweisen, sowie mögliche Verhaltensweisen um diesem Konflikt vorzubeugen oder diesen zu entschärfen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>IT Projektmanagement</dd>\n<dt>Objekt</dt>\n<dd>Eigene Person</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>keine</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('214', ' Benutzer/innen im Umgang mit Informatikmitteln instuieren', 4, '', 3, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=482" style="display:block;">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Benutzer/innen im Umgang mit Informatikmitteln instruieren</dd>\n<dt>Kompetenz</dt>\n<dd>Benutzerinnen und Benutzer im Umgang mit Informatikmitteln instruieren und unterstützen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Kurzanleitung oder Checkliste mit Regeln, Hinweisen und Vorschriften für den Umgang mit Informatikmitteln (Systemzugang, System- und Programmbedienung, Sicherheit, Datenschutz usw.) erstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt prinzipielle Unterschiede zwischen einer Benutzerinstruktion und dem Support der Benutzer bei Problemen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den Umgang mit Informatikmitteln am Arbeitplatz (zB. Systemzugang, System- und Programmbedienung, Sicherheit, Datenschutz).</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Strukturen, die der Orientierung in einer Benutzeranleitung dienen (zB. thematische Gliederung, Inhaltsverzeichnis, FAQ).</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Methoden die Benutzer/innen das Selbstudium im Umgang mit Informatikmitteln ermöglichen</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Qualität (Klarheit, Verständlichkeit, Nachvollziehbarkeit, usw.) der Kurzanleitung/Checkliste und Instruktion durch persönliche Reflexion und das Einholen von Rückmeldungen verbessern.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Fragetechniken (zB. offene Fragen, geschlossene Fragen) und welche Arten von Rückmeldungen (zB. allg. Einstufungen der Qualität, Hinweise auf Verbesserungspotentiale) sich damit einholen lassen, resp. wie sich diese für den Verbesserungsprozess einsetzen lassen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Benutzerinnen und Benutzern Regeln und Hinweise für die Anwendung der Funktionen der Informatikmittel anwendergerecht erläutern und schrittweise anleiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Regeln, die bei der Benutzerinstruktion einzuhalten sind (zB. Sprechgeschwindigkeit, Blickkontakt, wichtige Schritte visualisieren, Fachbegriffe umschreiben, einfache Sätze, strukturiertes Vorgehen).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Benutzerinnen und Benutzern Sicherheitsstandards und Sicherheitsvorschriften erläutern und zum verantwortungsbewusstem Umgang mit Informationen und Informatikmitteln anleiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die grundlegenden Bereiche der Informationssicherheit, des Datenschutz, verbreitetes Verhalten von Benutzerinnen und Benutzern (zB. Öffnen von E-Mail-Anhängen unbekannter Sender, Bekanntgabe von Passwörtern, Herunterladen von Daten aus nicht bekannten Quellen im Internet) und die häufigsten Gefahrenquellen (zB. Viren, Hacker, Social Engineering)..</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Möglichkeiten, um Benutzerinnen und Benutzer für ein sicherheitskonformes Verhalten zu sensibilisieren (zB. schrittweises Aufzeigen der Konsequenzen aus einem Fehlverhalten, für die eigene Person und den Betrieb).</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Bedeutung der Vorbildfunktion in diesem Bereich.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Mit geeigneten Massnahmen den Erfolg einer Instruktion überprüfen und Anliegen aufnehmen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Vorgehensweisen, um Benutzerinnen und Benutzern auf eine zuvorkommende Art bei der Ausübung ihrer Tätigkeit zu überprüfen (zB. Aufforderung ein weiteres Beispiel selber durchzuführen, Aufforderung eine Aktion noch einmal durchzuspielen, Schritte erklären lassen etc.).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Service Management</dd>\n<dt>Objekt</dt>\n<dd>...</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>keine</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n<small>Publiziert: 07.06.2014 17:15:41</small>\n</tab>\n</tabs>\n</tab>', ''),
('222', 'Applikationen objektorientiert konzipieren', 2, 'API', 4, '', 40, NULL, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="2.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=211" style="display:block;">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Applikationen objektorientiert konzipieren</dd>\n<dt>Kompetenz</dt>\n<dd>Anforderungen an eine Applikation mit objektorientierter Methode analysieren und unter Berücksichtigung von Rahmenbedingungen in einen objektorientierten Entwurf (Design) überführen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Anforderungen analysieren (OA) und daraus Use Cases ableiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt vom objektorientierten Entwicklungsprozess die beiden Grundtypen (Wasserfall, Agile d.h. iterativ/inkrementell) und kann sie auf Fallbeispiele anwenden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Use Cases analysieren und daraus ein Objektmodell (Klassendiagramm, Sequenzdiagramm, Zustandsdiagramm) ableiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Resultate (Artefakten) der objektorientierten Analyse (Use Cases, Klassendiagramm, Sequenzdiagramm, Zustandsdiagramm, etc.), ihre Syntax, Bedeutung und Beziehungen untereinander gemäss dem aktuellen UML Standard und kann sie für ein Fallbeispiel erarbeiten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Objektmodell der objektorientierten Analyse unter Anwendung von Design Patterns in ein objektorientiertes Design (OD) überführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Bausteine (Design Patterns) des objektorientierten Design, ihre Bedeutung und Beziehungen untereinander und kann sie auf ein Fallbeispiel anwenden.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Merkmale des objektorientierten Designs (Kapslung, Transaktion) und ihren Einfluss auf die Qualität des Designs und kann sie an einem Fallbeispiel erklären.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Objektmodell in einen relationalen Datenbank Design überführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den Entwicklungsprozess eines Relationalen Datenbank Design ausgehend von einem Objektmodell (Mapping) und kann ihn auf ein Fallbeispiel anwenden.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Merkmale eines Relationalen Datenbank Design (Redundanzfreiheit, Stabilität, etc.) und ihren Einfluss auf die Qualität des Designs und kann sie an einem Fallbeispiel erklären.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Application Engineering</dd>\n<dt>Objekt</dt>\n<dd>Applikation in einem beliebigen System- und Geschäftsumfeld.</dd>\n<dt>Niveau</dt>\n<dd>5</dd>\n<dt>Voraussetzung</dt>\n<dd>Erfahrung im Programmieren von Applikationen Erfahrung in der Datenmodellierung und im Datenbankdesign</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fachausweis</dd>\n</dl>\n<small>Publiziert: 30.10.2014 10:09:37</small>\n</tab>\n</tabs>\n</tab>', ''),
('223', ' Multi-User-Applikationen', 2, 'API', 4, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=484">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Multi-User-Applikationen objektorientiert realisieren</dd>\n<dt>Kompetenz</dt>\n<dd>Multi-User-Applikation objektorientiert entwerfen, erforderliche Datenbankanpassungen vornehmen und Applikation implementieren, testen und dokumentieren.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Einschätzen, ob eine Datenbank die Anforderungen der Multi-User-Fähigkeit erfüllt und allfällige Anpassungen dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Anforderungen an das Datenbankmanagement-System bezüglich Multi-User-Fähigkeit.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Aspekte bei der Datenmodellierung, welche die Multi-User-Fähigkeit ermöglichen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Applikation entwerfen und mittels Transaktionen Multi- User-Fähigkeit sicherstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die prinzipiellen Unterschiede zwischen Geschäftsobjektmodell und relationalem Datenmodell .</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt wichtige Architekturvarianten und -konzepte (Client/Server, Multi-Tier, Middleware, Framework, Klassenbibliothek).</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Umsetzung einer objekt-relationalen Abbildung eines Geschäftsobjektmodells und deren Spezifikation mittels UML (Klassendiagramm, Sequenzdiagramm).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>User Interfaces, Datenbankanpassungen und Transaktionen implementieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt spezifische Elemente für die Umsetzung von Multi-User-fähigen Benutzerschnittstellen (z.B. Profil, unterschiedliche Benutzersichten, Berechtigungskonzept, usw.).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Möglichkeiten ein mehrbenutzer-fähiges Rechtemanagement zu implementieren.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Möglichkeiten um Transaktionen im DBMS sicherzustellen.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Möglichkeiten um Transaktionen in der Applikation zu implementieren.</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt relevante Techniken für die Implementation einer Persistenzschicht.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Testspezifikation für funktionale und nicht-funktionale Aspekte der Multi-User-Fähigkeit definieren, Applikation testen und Tests protokollieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt relevante Aspekte, welche bei der Testspezifikation einer Muli-User-Applikation zu berücksichtigen sind.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt ein Vorgehen, um nicht-funktionale Anforderungen zu testen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Transaktionen dokumentieren  und dabei auf Wartbarkeit und Nachvollziehbarkeit achten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Möglichkeiten zur Dokumentation von Transationen um DBMS und in der Applikation.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Application Engineering</dd>\n<dt>Objekt</dt>\n<dd>Multi-User-Applikation mit transaktionsfähiger Datenbank.</dd>\n<dt>Niveau</dt>\n<dd>4</dd>\n<dt>Voraussetzung</dt>\n<dd>Datenmodelle entwickeln und umsetzen \nObjektorientiert entwerfen und implementieren</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('226', ' <span style="text-decoration:line-through;">Objektorientiert implementieren</span>', 2, 'API', 3, '', 80, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="2.10">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=292">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Objektorientiert implementieren</dd>\n<dt>Kompetenz</dt>\n<dd>Ein objektorientiertes Design (OOD) implementieren, testen und dokumentieren.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Ein objektorientiertes Design (OOD) nachvollziehen und durch technische Klassen ergänzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt das Paradigma des objektorientierten Ansatzes und kann an Beispielen erläutern, welche prinzipiellen Unterschiede gegenüber dem funktionalen Ansatz bestehen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kann aufzeigen, wie durch Klassen und deren Attribute und Methoden die reale Welt im (vorgegebenen) Design abgebildet wird.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Beziehungstypen (Assoziation, Aggregation, Komposition) zwischen Klassen und das Konzept der Vererbung und kann aufzeigen, wie diese umgesetzt werden.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die Bedeutung der technischen Klassen und kann an Beispielen erläutern, wie diese zusammen mit den fachlichen Klassen das objektorientierte Design vervollständigen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Dynamische und statische Strukturen zwischen Objekten resp. Klassen mittels Unified Modeling Language, UML (Klassen-/Sequenzdiagramme) darstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Notation des Klassen- und Objektdiagramms und kann aufzeigen, wie sich diese unterscheiden.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Notation von Sequenz- und Kollaborationsdiagramm und kann aufzeigen, wie sich damit Abläufe darstellen lassen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Notation von Schnittstellen und Paketen und kann aufzeigen, wie sich diese auf die Implementation (Typisierung, Programmierung im Team usw.) auswirken.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Objektorientiertes Design implementieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kann erläutern, wie Klassenmodelle mit einer objektorientierten Programmiersprache umgesetzt werden können.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kann an einem Codebeispiel den Effekt von Polymorphie aufzeigen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kann aufzeigen, wie durch die Nutzung von Schnittstellen der Code unabhängig erstellt und getestet werden kann.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die grundlegenden Funktionen eines CASE Tools und kann erläutern, mit welchen Funktionen die Implementation unterstützt wird.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Klassen systematisch prüfen (Unit Test).</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die grundlegenden Schritte, die bei einem Unit Test durchlaufen werden müssen und kann aufzeigen, welchen Beitrag diese zu einem qualitativ guten Ergebnis leisten.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kann zu einer Klasse Testfälle und Grenzwerte festlegen und diese in einer Testklasse implementieren.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Klassen- und Systemdokumentation vervollständigen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kann den Programmcode vollständig und korrekt dokumentieren, um daraus die API Spezifikation abzuleiten.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Struktur einer Systemdokumentation und kann ihre Bedeutung für Wartung und Nachvollziehbarkeit darlegen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Application Engineering</dd>\n<dt>Objekt</dt>\n<dd>Applikation mit 3-5 fachlichen Klassen (z.B. Karteikasten, CD-Sammlung, Adressverwaltung usw.).</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>Analysieren und objektbasiert programmieren mit Komponenten, bzw. Analysieren und strukturiert implementieren</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>80</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('226A', 'Klassenbasiert (ohne Vererbung) implementieren', 2, 'API', 3, NULL, 40, NULL, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.10">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=574" style="display:block;">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Klassenbasiert (ohne Vererbung) implementieren</dd>\n<dt>Kompetenz</dt>\n<dd>Anforderungen in ein Klassendesign ohne Vererbung umsetzen und mit einer objektorientierten Programmiersprache implementieren, dokumentieren und testen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Ein SW-Design mit Klassen nachvollziehen und mit eigenen fachlichen und technischen Klassen ergänzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Abstraktionsmittel wie Beziehungen, Klassen, Attribute und Methoden, mit denen die reale Welt im Design abgebildet wird.<br/></td>\n</tr>\n<tr>\n<td>2</td>\n<td>Versteht die Klasse als abstrakten Datentyp (ADT).<br/></td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Beziehungstypen wie Assoziation, Aggregation, Komposition zwischen Klassen.<br/></td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die Bedeutung von Fachklassen und Klassen, die für die Implementation der Anwendung nötig sind und das klassenbasierte Design vervollständigen (technische Klassen).<br/></td>\n</tr>\n<tr>\n<td>5</td>\n<td>Versteht das Zusammenspiel mehrerer Klassen in einer Anwendung und wendet das Prinzip der Delegation an.<br/></td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Die Notation dynamischer und statischer Strukturen einer Anwendung mittels Unified Modeling Language (UML) nachvollziehen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Diagrammnotationen zur Darstellung von Klassen und Objekten.<br/></td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Diagrammnotationen zur Darstellung des Meldungsaustausches zwischen Objekten.<br/></td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Klassenbasiertes Design implementieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt eine objektorientierte Sprache, welche für die Umsetzung des klassenbasierten Designs geeignet ist.<br/></td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kann das Prinzip des Information Hiding und der Kapselung beim Entwurf von Klassen anwenden.<br/></td>\n</tr>\n<tr>\n<td>3</td>\n<td>Versteht den Unterschied zwischen dem Programm zur Kompilationszeit (Klassen) und zur Laufzeit (sich referenzierende Objekte).<br/></td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Für funktionale Einheiten einer Anwendung Testfälle implementieren, um die Anwendung automatisch zu prüfen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Vorgehen um für Einheiten Testfälle mittels geeigneter Grenzwerte oder Pfadprüfungen zu finden und festzulegen.<br/></td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die zur Verfügung gestellten Mittel einer Testinfrastruktur um die Testfälle zu implementieren.<br/></td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Mittel, die Abdeckung der Tests zu messen.<br/></td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Klassen der Anwendung systematisch, unter Verwendung der hierfür vorgesehenen Infrastruktur, dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Möglichkeiten der Dokumentationsinfrastruktur und weiss wie die Programmeinheiten gemäss Vorgaben zu dokumentieren sind.<br/></td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den Nutzen von Api-Dokumentationen und weiss wie diese erzeugt werden.<br/></td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Application Engineering</dd>\n<dt>Objekt</dt>\n<dd>Applikation mit 3-5 fachlichen Klassen (z.B. Karteikasten, Bibliothek, Adressverwaltung usw.).</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>Grundfertigkeiten im Umgang mit Programmierstrukturen.</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n<small>Publiziert: 04.09.2015 12:10:16</small>\n</tab>\n</tabs>\n</tab>', '<h3>Rework Sven Jun2017</h3><p>Toolbox, Drehbuch auf SWING und neues Lehrmittel anpassen</p>'),
('226B', 'Klassenbasiert (mit Vererbung) implementieren', 2, 'API', 3, NULL, 40, NULL, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.10">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=576" style="display:block;">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Objektorientiert (mit Vererbung) implementieren</dd>\n<dt>Kompetenz</dt>\n<dd><p>Ein objektorientiertes Design (OOD) implementieren, testen und dokumentieren.</p></dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Ein objektorientiertes Design nachvollziehen und durch Einsatz der Vererbung erweitern.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den objektorientierten Ansatz mit Klassen und Vererbung.<br/></td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kann durch die Anwendung von Vererbung Redundanzen im Klassendesign eliminieren.<br/></td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kann durch den Einsatz von Delegation und Vererbung switchartige Strukturen vermeiden und dadurch die Erweiterbarkeit der Anwendung sicherstellen.<br/></td>\n</tr>\n<tr>\n<td>4</td>\n<td>Versteht das Prinzip der dynamischen Bindung.<br/></td>\n</tr>\n<tr>\n<td>5</td>\n<td>Berücksichtigt beim Erstellen von Vererbungshierarchien das Prinzip von Spezialisierung/Generalisierung inklusive abstrakter Klassen.<br/></td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Die Notation dynamischer und statischer Strukturen einer Anwendung mittels Unified Modeling Language (UML) nachvollziehen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Diagrammnotationen zur Darstellung von Vererbung.<br/></td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Diagrammnotationen zur Darstellung von Schnittstellen.<br/></td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Objektorientiertes Design implementieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Elemente einer objektorientierten Sprache für die Umsetzung der Vererbung im objektorientierten Design.<br/></td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Elemente einer objektorientierten Sprache für das Überschreiben von Methoden im objektorientierten Design.<br/></td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Elemente einer objektorientierten Sprache für die Realisierung von Schnittstellen und abstrakten Klassen.<br/></td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Fortgeschrittene Testfälle für funktionale Einheiten implementieren, welche durch geeignete Techniken von anderen Systemteilen unabhängig sind.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt das Hilfsmittel des Mockings um Abhängigkeiten aus den Testfällen zu eliminieren.<br/></td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Mittel zur Isolierung der Testdaten und Testfälle, um die gegenseitige Unabhängigkeit der Testfälle zu garantieren.<br/></td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Werkzeuge zur Implementation von isolierten Testfällen.<br/></td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Application Engineering</dd>\n<dt>Objekt</dt>\n<dd><p>Applikation mit 3-5 fachlichen Klassen (z.B. Grafikeditor, einfache Spiele usw.).</p></dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>Grundfertigkeiten im Umgang mit Programmierstrukturen.</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n<small>Publiziert: 04.09.2015 12:11:27</small>\n</tab>\n</tabs>\n</tab>', ''),
('239', ' Internetserver in Betrieb nehmen', 5, 'SYS', 3, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=486">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Internetserver in Betrieb nehmen</dd>\n<dt>Kompetenz</dt>\n<dd>Internetserver mit verschiedenen Diensten konfigurieren und in Betrieb nehmen und dabei Sicherheitsvorgaben und betriebliche Anforderungen beachten.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Anforderungen (Sicherheit, Lastprofil, Datenvolumen, Verfügbarkeit, zu integrierende Applikationen) an einen Internetserver aufnehmen und dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Kriterien, welche bei der Beurteilung einer Ausgangssituation für die Dimensionierung eines Internetservers angewendet werden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Bestehende Infrastruktur (Server, Netzwerk, Dienste) mit den Anforderungen abgleichen und notwendige Anpassungen bzw. Erweiterungen vorschlagen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Merkmale und deren Bedeutung für die technische Spezifikation eines Internetservers.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt das Prinzip der Namensauflösung mittels DNS und deren Einfluss auf  den Betrieb weiterer Internetdienste (z.B. Virtual Hosts bei Webserver).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Erforderliche Einstellungen gemäss Sicherheits- und Betriebskonzept festlegen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die empfohlenen (best practice) Betriebs- und Sicherheitseinstellungen eines Internetservers.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Software installieren, konfigurieren und Dienste einrichten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Abhängigkeiten der zu installierenden Dienste und den Einfluss auf die Reihenfolge der Installation/Konfiguration.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Kriterien, die bei der Anbindung fremder Ressourcen an einen Internetserver zu beachten sind.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Zugriffsberechtigungen vergeben, sichere Kommunikation  und Log-Services einrichten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Kriterien für die Definition der Zugriffsberechtigungen von Benutzern und Gruppen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt das Prinzip der sicheren Kommunikation und den Einsatz von Zertifikaten.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Protokollierung und Auswertung von Log-Services.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Internetserver testen (Last-, Sicherheits- und Crashtest).</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt grundlegende Testverfahren und deren Bedeutung für die Betriebssicherheit eines Internetservers.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>System Management</dd>\n<dt>Objekt</dt>\n<dd>Internetserver im KMU-Umfeld mit mindestens zwei Diensten, wie z.B. WWW, FTP, DNS, Mail.</dd>\n<dt>Niveau</dt>\n<dd>3</dd>\n<dt>Voraussetzung</dt>\n<dd>Erfahrung mit TCP/IP Protokollen \nErfahrung mit Client/Server Umgebungen \nInternetprotokolle und -services konfigurieren</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('242', 'Microprozessoranwendung realisieren', 2, 'API', 3, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=488">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Mikroprozessoranwendung realisieren</dd>\n<dt>Kompetenz</dt>\n<dd>Mikroprozessoranwendung nach Vorgabe strukturieren, programmieren, auf der Zielhardware implementieren und testen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Vorgabe auf verlangte Funktionen und benötigte Input- und Outputdaten/-Signale analysieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den grundlegenden Aufbau einer Mikroprozessoranwendung, sowie deren Logik-Beschreibung (Zustandsdiagramm).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den Aufbau der Zielhardware mit den wichtigsten Komponenten (Register, RAM, EEPROM, Port, Timer, UART, ADC, etc.) und kennt ihre Bedeutung für die Lösung der Aufgabenstellung.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Zeitkritische und zeitlich unabhängige Funktionen identifizieren und in einem Programmentwurf logisch gliedern, Datentypen den Daten/Signalen zuordnen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Lösungsvarianten für die Realisierung zeitkritischer und ereignisorientierter Abläufe und deren Vor- und Nachteile.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Datentypen und deren Bezug zu den unterschiedlichen Komponenten der Zielhardware.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Mittels einer Programmiersprache und Programmierumgebung den Programmentwurf umsetzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Entwicklungsumgebung  und deren Möglichkeiten, eine Mikroprozessoranwendung für die Zielhardware zu realisieren.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Verfahren um Quellcode zu editieren, zu übersetzen, auf das Zielsystem zu laden und auszuführen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die hardwarespezifischen Teile der Programmiersprache und deren Einsatzmöglichkeiten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Aus der Vorgabe die Testfälle identifizieren, spezifizieren und dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Testkriterien (Hardware- und Zeit-Abhängigkeit) für die Überprüfung von Mikroprozessoranwendungen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Möglichkeiten, die Wechselwirkung der Prozesse und deren zeitkritischen Anforderungen für die geforderte Funktionalität in den Tests zu berücksichtigen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Realisierte Applikation mit geeigneten Werkzeugen auf der Zielhardware austesten, Fehler identifizieren, korrigieren und dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Werkzeuge einer Entwicklungsumgebung (Simulator, Debugger, In-System-Debugger, etc.), sowie die Möglichkeiten, eine realisierte Applikation auf der Zielhardware schrittweise auszuführen und zu testen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Technical Software Engineering</dd>\n<dt>Objekt</dt>\n<dd>Hardwarenahe Mikroprozessoranwendung</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>Programmieren nach Vorgabe</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', '');
INSERT INTO `ict_module` (`modul_nr`, `modul_name`, `id_fachschaft`, `bereich`, `semester`, `lbv`, `lessons_ifz`, `lessons_uifz`, `bivo`, `hanoks`, `todo`) VALUES
('254', ' Geschäftsprozesse beschreiben', 4, '', 3, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=490">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Geschäftsprozesse beschreiben</dd>\n<dt>Kompetenz</dt>\n<dd>Geschäftsprozesse dokumentieren, Aufgabenanalyse durchführen und Prozessabläufe grafisch darstellen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Relevante Prozessinformationen (Bezeichnung, auslösendes Ereignis, Ergebnis, Auslöser, Empfänger) identifizieren, dokumentieren und sich dabei auf die Beschreibung einer zuvor durchgeführten Erhebung ab-stützen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den Unterschied zwischen der Ablauf- und der Aufbauorganisation einer Unternehmung.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die relevanten Elemente einer Unternehmensorganisation, welche die Zuständigkeiten und Verantwortungen in einem Geschäftsprozess beschreiben.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Grundelemente Objekt und Verrichtung zur Beschreibung eines Geschäftsprozesses.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die wesentlichen Elemente zur Darstellung eines Geschäftsprozesses.</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt die relevanten Informationen zur Definition eines Geschäftsprozesses und dessen Abgrenzung zu anderen Geschäftsprozessen.</td>\n</tr>\n<tr>\n<td>6</td>\n<td>Kennt typische Merkmale von auslösenden Ereignissen und Ergebnissen in einem Geschäftsprozess.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Geschäftsprozess in einer Aufgabenanalyse in Prozessschritte zerlegen und den Prozessablauf grafisch darstellen. Die grafische Darstellung erfolgt auf der Grundlage von anerkannten und üblichen Beschreibungsstandards.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt eine Möglichkeit Folgebeziehungen und deren Sachverhalt visuell darzustellen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt eine Darstellungstechnik (z.B. UML Anwendungsfalldiagramm, Aktivitätsdiagramm) zur Modellierung eines Prozessablaufs.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Prozessablauf mit Zusatzinformationen wie benötigten Hilfsmitteln, ausführenden Stellen sowie der Beschreibung des zu erzeugenden Outputs (Ergebnis) präzisieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt prozessunterstützende Mittel und Ressourcen sowie deren Anwendung (z.B. Checkliste, Workflow-Managementsoftware).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Anforderungen an Informationen und deren Darstellung zur Übergabe an nachfolgende Prozessschritte.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Business Engineering</dd>\n<dt>Objekt</dt>\n<dd>Bestehender Geschäftsprozess, der alle Grundformen von Folgebeziehungen und 10-20 Prozessschritte umfasst.</dd>\n<dt>Niveau</dt>\n<dd>4</dd>\n<dt>Voraussetzung</dt>\n<dd>Prozessablauf grafisch darstellen\nAufbau und Inhalt einer Dokumentation kennen</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('300', ' Plattformübergreifende Dienste in ein Netzwerk integrieren ', 5, '', 0, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=492">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Plattformübergreifende Dienste in ein Netzwerk integrieren</dd>\n<dt>Kompetenz</dt>\n<dd>Plattformübergreifende Dienste nach Vorgabe für eine heterogene Systemumgebung konfigurieren, in Betrieb nehmen, testen und freigeben.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Aus den Vorgaben  die erforderlichen Dienste ermitteln, Schutz- und Sicherheitsanforderungen ableiten und ein Konzept für die Integration der Dienste ausarbeiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Einsatz- und Konfigurationsmöglichkeiten der vorgegebenen Betriebssysteme und Dienste.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Clients und Server gemäss Vorgaben konfigurieren, einrichten und geforderte Funktionalität überprüfen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die übliche (best practice) Vorgehensweise bei der Inbetriebnahme von Serverdiensten (zB. installieren, konfigurieren, starten, testen).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt betriebssystemspezifische Konzepte zur Konfiguration von Software (zB. Konfigurationsdateien, Registry, systemweite / benutzerspezifische Konfiguration).</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Möglichkeiten von Betriebssystemen zur Gewährleistung und Absicherung des Zugriffs auf Netzwerk-Ressourcen (Authentifizierung, Autorisierung).</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die unterschiedlichen Konzepte, Systembefehle und Hilfsprogramme für die Benutzer- und Rechteverwaltung (zB. User-ID, Zugriffsrechte, Gruppenmitgliedschaft, Standardrechte, Vererbung, Homeverzeichnis).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Netzwerkverbindungen einrichten, Dienste in Betrieb nehmen und testen. Definierte Schutz- und Sicherheitsmassnahmen überprüfen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Konfigurationsmöglichkeiten eines DHCP Servers (zB. Zuweisung einer IP Adresse, einer Subnet-Maske, Angaben zu DNS-Servern, Standard-Gateways).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Konfigurationsmöglichkeiten eines DNS-Servers .</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die notwendigen Einstellungen bei einem Client in einer DHCP-/DNS-Serverumgebung.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die Elemente und Funktionen des TCP/IP-Protokolls (zB. MAC- und IP-Adressen, IP-Adressklassen, private Adressen, Netzmasken, Routing, Adress Resolution Protocol (ARP), wichtige Portnummern).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Anwendungen und Tools installieren, einrichten und geforderte Funktionalität überprüfen und gemeinsame Ressourcen einbinden</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt technische Möglichkeiten um Ressourcen im Netzwerk durch Gruppen gemeinsam zu nutzen (zB. Groupware).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Allfällige Fehler systematisch eingrenzen, protokollieren und Massnahmen zur Fehlerbehebung einleiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Methoden zur systematischen Fehlereingrenzung (zB. Ausschlussverfahren intakter Systeme).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Werkzeuge zur Fehleranalyse und –behebung.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt den Aufbau und die wesentlichen Merkmale eines Testprotokolls.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Dokumentation für die Administration des Netzes, der Rollen und Rechte und der eingerichteten Dienste und Anwendungen erstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Aufbau und Inhalt einer Netzwerk- und Systemdokumentation.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>System Management</dd>\n<dt>Objekt</dt>\n<dd>Clients, Server (File, Print, DNS, DHCP, Directory Services, Terminal, SSH, Web) und Peripherie mit unterschiedlichen Betriebssystemen in einem einfachen Netzwerk.</dd>\n<dt>Niveau</dt>\n<dd>3</dd>\n<dt>Voraussetzung</dt>\n<dd>Konfigurieren und betreiben von PCs. Servern und Peripherie (Multiusersysteme) \nErfahrung im Aufbau von lokalen Netzwerken</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('301', ' Office Werkzeuge anwenden', 4, '', 1, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="1.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=180">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Office Werkzeuge anwenden</dd>\n<dt>Kompetenz</dt>\n<dd>Dokumente mit Text-, Tabellen- und Präsentationswerkzeugen strukturieren und gestalten. Internet und E-Mail nutzen und Dokumentverwaltung organisieren.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Strukturierte Ablage mit Ordnern, Ordnerstrukturen, Namenskonventionen etc. für Dateien/Dokumente erstellen, verwalten und pflegen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den Aufbau und die Funktionen eines Dateimanagements mit Ordnern, Dateien und Dateitypen auf Datenträgern und kann erläutern, wie diese eine effiziente Verwaltung von Dateien auf Datenträgern unterstützen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die einfachsten Möglichkeiten, die ein Betriebssystem und die verbreiteten Tools im Bereich des Zugriffsschutzes bieten und kann erläutern, für welche Art von Informationen diese sinnvoll eingesetzt werden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Inhalt und Struktur von Texten und Daten/Informationen analysieren und für die Bearbeitung und Gestaltung ein Werkzeug (Textverarbeitung, Tabellenkalkulation, Präsentation) auswählen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die grundsätzlichen Möglichkeiten, welche die verbreiteten Tools im Bereich Textverarbeitung, Präsentation, Tabellenkalkulation, Internet und Mail bieten und kann aufzeigen, für welche Art von Aufgaben sich diese eignen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Dokumente einrichten (Seitenzahlen, Kopf- und Fusszeilen usw.), Dokumentinhalte (Überschriften, Absätze, Abschnitte usw.) bestimmen und mit standardisierten Formatvorlagen strukturieren und gestalten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Strukturelemente Seite, Absatz und Zeichen und kann erläutern, wie diese für die inhaltliche Gliederung und die Gestaltung eines Dokumentes eingesetzt werden.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die verschiedenen Schriftmerkmale (Schriftart, Schriftgrad, Schriftschnitt) und kann darlegen, wie diese für die Hervorhebung bzw. Markierung von Textstellen zweckmässig eingesetzt werden.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt das Formatvorlagenkonzept der verbreiteten Anwendungsprogramme und kann aufzeigen, wie sich Formatvorlagen für eine effiziente und einheitliche Gestaltung und Strukturierung von Dokumenten einsetzen lassen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Inhalte von Dokumenten mit Tabulatoren oder Tabellen übersichtlich darstellen und formatieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die verschiedenen Möglichkeiten, mit Tabulatoren und Tabellen Informationen strukturiert darzustellen und kann aufzeigen in welchen Fällen diese einzeln oder in Kombination zur Anwendung kommen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Rechtschreibungs-, Silbentrennungs-, Autokorrekturfunktionen usw. nutzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Automatisierungshilfen gängiger Officewerkzeuge und kann erläutern, wie damit wiederholte Eingaben vereinfacht und die Qualität der Ergebnisse verbessert werden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Vorgegebene Texte und Bilder unter Beachtung von Gestaltungsregeln für eine Präsentation (Hellraumprojektor, Beamer) aufbereiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Einstellungen für die Darstellung und Platzierung von Bildern in einem Dokument und kann aufzeigen, welche Wirkung damit erzielt werden kann.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>7</td>\n<td>Daten/Informationen in einem Tabellenblatt strukturieren, standardisierte Berechnungen durchführen und einfache Diagramme erstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Regeln, die beim Aufbau eines Tabellenblattes zu beachten sind, damit eine Berechnung der verlangten Werte/Kenngrössen oder die Erstellung von Diagrammen gewährleistet werden kann (zeilen- oder spaltenweise Anordnung von gleichen Messgrössen, spalten- oder zeilenweise Zuordnung von verbundenen Werten).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die einfachsten statistischen Kenngrössen (Mittelwert, Minimum, Maximum) und kann aufzeigen, für welche Art von Aussagen diese verwendet werden.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die einfachsten Diagrammtypen für die Darstellung von Informationen und kann erläutern, welche Aussagen sich damit visualisieren lassen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>8</td>\n<td>Informationen aus dem Internet mit einer Suchmaschine gezielt suchen, verfügbar machen und dabei Sicherheitsbestimmungen einhalten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die qualifizierten Suchfunktionen der gebräuchlichen Suchmaschinen und kann deren Wirkung hinsichtlich einer effizienten Informationsbeschaffung erläutern.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Gefahren, die bei der Übertragung von Informationen aus dem Internet auftreten können und kann erläutern, mit welchen Verhaltensmassnahmen und mit welchen Vorkehrungen (Browser-Einstellungen, Virenschutzprogramme) diesen entgegengewirkt werden kann.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>9</td>\n<td>Browserfunktionen für den Zugriff auf Internetadressen nutzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Möglichkeiten von WWW-Browsern für einen vereinfachten und beschleunigten Zugriff auf Internetseiten und kann aufzeigen, in welchen Fällen diese Möglichkeiten sinnvoll eingesetzt werden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>10</td>\n<td>E-Mails austauschen (senden, empfangen, antworten, weiterleiten, verteilen) und dabei Datenschutz-, Sicherheits- und Vertraulichkeitsvorschriften einhalten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Möglichkeiten einer effizienten Anwendung von E-Mail-Systemen (Adressbuch, Verteilerlisten, Ordnerstruktur, Sicherheitseinstellungen etc.) und kann erläutern, welchen Nutzen diese in der täglichen Arbeit bringen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Business Engineering</dd>\n<dt>Objekt</dt>\n<dd>Dokumente und Daten/Informationen.</dd>\n<dt>Niveau</dt>\n<dd>1</dd>\n<dt>Voraussetzung</dt>\n<dd>keine</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('302', ' Fortgeschrittene Funktionen von Office-Werkzeugen nutzen', 4, '', 1, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=494" style="display:block;">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Fortgeschrittene Funktionen von Office Werkzeugen nutzen</dd>\n<dt>Kompetenz</dt>\n<dd>Dokumentverarbeitung mit fortgeschrittenen Funktionen von Text-, Tabellen- bzw. Präsentationswerkzeugen nutzen. Termin- und Aufgabenverwaltung für ein Team einrichten und anwenden.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Mehrseitige Dokumente (Text, Grafik, Tabellen) mit Hilfe von Spalten, Tabellen, Abschnittsgliederungen mehrstufig strukturieren und Kopf-/Fusszeilen mit wechselnden Inhalten steuern.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Grundregeln der Typografie, um ansprechende und lesefreundliche Dokumente zu gestalten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Dokumente auf ein Präsentationsmedium abstimmen und durch den Einsatz von, Fussnoten, Referenzen, Verzeichnissen und Verweisen verständlicher, lesbarer und übersichtlicher machen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Gestaltungs- und Gliederungsmöglichkeiten, die sich mit den Funktionen Spalten, Tabulatoren resp. Tabellen erzielen lassen</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Elemente, die der Kennzeichnung von Begriffen, Textstellen, Objekten dienen (zB. Fussnoten, Referenzen, Verzeichnisse und Verweise) und die Verständlichkeit, Übersichtlichkeit und die Orientierung im Text verbessern .</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Mit Serienfunktionen Dokumente und Datenquellen (Tabelle, Tabellenblatt, Adressverwaltung) verbinden und über Feldinhalte Textbausteine steuern.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den Einsatz der Serienfunktion für die rationelle Erstellung gleichartiger Massendokumente.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Elemente, die beim Seriendruck zum Einsatz kommen (Hauptdokument, Datenquelle, Filter, Textbausteine).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Gestaltungsvorgaben in Format- und Dokumentvorlagen umsetzen. In Dokumentvorlagen Erfassungshilfen (Formularfelder) integrieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Funktionen um Gestaltungsvorgaben (zB. einheitliche Schriften, Beschränkung der Formatierungsmöglichkeiten) bei der Erstellung von Formatvorlagen zu realisieren.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Möglichkeiten wie Formularfelder als Eingabehilfen in Dokumentvorlagen/ Formularen verwendet werden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Formulare für die Erfassung und Änderung von Tabellendaten erstellen und damit die Datenqualität und eine effiziente Datenverwaltung sicherstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Standard-Funktionen, die für die Erstellung von Formularen zur Dateneingabe und –pflege.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Mit Gruppierungsfunktionen, Berechnungsformeln und logischen Verknüpfungen Auswertungen erstellen, denen Daten aus mehreren Tabellenblättern zu Grunde liegen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Möglichkeiten, Auswertungen von Daten in Tabellen übersichtlich darzustellen. (zB. Gruppen, Objekte, Eigenschaften, Filter)</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>7</td>\n<td>Sachverhalte mit einem geeigneten Diagrammtyp darstellen und die Aussagekraft von Diagrammen mit Beschriftungen, Kennwerten und Kennlinien erhöhen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Diagrammtypen und deren Elemente zur Ergänzung (zB. Beschriftungen, Kennwerte, Kennlinien) um die Verständlichkeit und Interpretation zu vereinfachen..</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>8</td>\n<td>Mit Standardfunktionen eines E-Mail-Systems eine automatisierte Nachrichtenverwaltung (Ordner, automatisierte Ablage, Archivierung, Verteiler) einrichten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Standardfunktionen eines E-Mail-Systems, die eine automatisierte Verwaltung der Nachrichten (Verteilung eingehender E-Mails in Ordner, automatisierte Ablage, Archivierung, Verteiler) ermöglichen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>9</td>\n<td>Mit Standardfunktionen eines Kalender-Systems Termine für ein Team festlegen, koordinieren; Aufgaben zuordnen und deren Ausführung überwachen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Team-Funktionen eines Kalender-Systems zur Koordination und Überwachung der Aktivitäten in einer Gruppe.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Business Engineering</dd>\n<dt>Objekt</dt>\n<dd>Mit Daten/Informationen verbundene Dokumente.</dd>\n<dt>Niveau</dt>\n<dd>1</dd>\n<dt>Voraussetzung</dt>\n<dd>Office  Kenntnisse</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n<small>Publiziert: 07.06.2014 17:15:41</small>\n</tab>\n</tabs>\n</tab>', ''),
('303', ' <span style="text-decoration:line-through;">Objektbasiert programmieren mit Komponenten</span>', 2, 'API', NULL, NULL, 40, NULL, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="1.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=182" style="display:block;">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Objektbasiert programmieren mit Komponenten</dd>\n<dt>Kompetenz</dt>\n<dd>Vorgabe interpretieren, mit einer auf RAD-Technologie (Rapid Application Development) basierenden Programmiersprache implementieren, dokumentieren und testen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Vorgabe interpretieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Darstellungsmöglichkeiten für ereignisorientierte Verarbeitungen (z.B. Zustandsdiagramm) und kann an Beispielen erläutern, welche Sachverhalte sich damit abbilden lassen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Einfache graphische Bedieneroberfläche entwerfen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Regeln, die beim Aufbau einer Bedieneroberfläche eingehalten werden müssen (Abfolge der Ereignisse einhalten, klare Zuordnung von Ereignissen und den dazugehörenden Aktionen, klare Bezeichnungen und sprechende Symbole) und kann erläutern, wie damit eine korrekte Bedienung durch die Benutzerinnen und Benutzer sichergestellt wird.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die grundsätzlichen Informationen (z.B. Funktionsbeschreibung), die in der Beschreibung einer Komponente enthalten sein müssen und kann erläutern, welchen Nutzen diese Informationen bei der Auswahl einer Komponente resp. bei der Verwendung dieser Komponente in einem Programm bringen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt GUI-Komponenten, die eine Programmiersprache bzw. deren Bibliotheken für die Definition von Benutzerschnittstellen zur Verfügung stellen und kann erläutern, für welche Art von Benutzerinteraktionen diese eingesetzt werden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Erforderliche Daten bestimmen und Datentypen festlegen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Datentypen (Ganzzahl, Gleitkommazahl, Zeichenketten, Boolean) und kann aufzeigen, welche Arten von Daten/Informationen diesen Datentypen zugeordnet werden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Programmvorgabe unter Nutzung vorhandener Komponenten mit deren Eigenschaften und Methoden, sowie Operatoren, Prozeduren und Funktionen ereignisorientiert implementieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den grundsätzlichen Aufbau einer Komponente (Eigenschaften, Methoden) und kann an Beispielen aus dem Alltag erläutern, wie sich diese für die Verarbeitung eines Ereignisses einsetzen lassen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den grundlegenden Aufbau eines Programms (lokale und globale Variablen, Ereignisprozeduren, Verarbeitung usw.) und kann diesen Aufbau an einem Programm beispielhaft erläutern.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Komponenten mit ihren Eigenschaften, Methoden und Ereignissen und kennt Operatoren, Prozeduren und Funktionen einer Programmiersprache und kann aufzeigen, welche Verarbeitungsanweisungen damit realisiert werden können.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die wichtigsten Regeln, die bei der Datenein- und -ausgabe auf dem Bildschirm einzuhalten sind (Bezeichnung der Informationen, Format für Dateneingabe usw.) und kann erläutern, welche Probleme sich ergeben, wenn diese Regeln nicht eingehalten werden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Beim Programmieren vorgegebene Standards und Richtlinien einhalten, das Programm in line dokumentieren und dabei auf Wartbarkeit und Nachvollziehbarkeit achten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Regeln für einen verständlichen Quellcode (sprechende Komponenten- und Variablenbezeichnungen, optische Strukturierung, geeignete Kommentare) und kann erläutern, welchen Beitrag diese Angaben zur Lesbarkeit von Programmen leisten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Programm auf Einhaltung der Funktionalität testen, Fehler erkennen und beheben.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Elemente eines Testfalls (zu testende Funktion oder Verarbeitung, Eingabedaten, erwartete Ergebnisse) und kann aufzeigen, welche Bedeutung die Definition dieser Elemente für den Nachweis der korrekten Verarbeitung von Informationen in einem Programm hat.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Application Engineering</dd>\n<dt>Objekt</dt>\n<dd>Einfaches, objektbasiertes Programm.</dd>\n<dt>Niveau</dt>\n<dd>1</dd>\n<dt>Voraussetzung</dt>\n<dd>keine</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n<small>Publiziert: 24.05.2014 15:37:28</small>\n</tab>\n</tabs>\n</tab>', ''),
('304', ' Einzelplatz-Computer in Betrieb nehmen', 6, '', 1, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="2.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=418">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Einzelplatz-Computer in Betrieb nehmen</dd>\n<dt>Kompetenz</dt>\n<dd>Einzelplatz-Computer in Betrieb nehmen, Betriebssystem und Office Suite installieren, Gerät mit dem Netzwerk verbinden und das System testen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Komponenten eines Computer-Arbeitsplatzes (Gerät, Bildschirm, Tastatur/Maus, Drucker, Netzwerkanbindung) nach Vorgabe verbinden.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt das Prinzip der Verarbeitung von Informationen in einem Mikrocomputer und kann aufzeigen, welche Aufgaben den Hardwarekomponenten (CPU, Arbeitsspeicher, Massenspeicher, Bussysteme) zugewiesen sind und wie der Informationsfluss zwischen den einzelnen Hardwarekomponenten funktioniert (Eingabe, Verarbeitung, Ausgabe, Speicherung und Kommunikation).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die gängigen Leistungsmerkmale und Kenndaten der Hardwarekomponenten eines Einzelplatz-Computers und kann erläutern, welche Hardware-Konfigurationen für welche Einsatzgebiete erforderlich sind.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt standardisierte Peripherieschnittstellen und Bussysteme eines Einzelplatz-Computers und kann aufgrund ihrer Merkmale aufzeigen, für welche Einsatzfälle diese vorgesehen sind.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Betriebssystem unter Verwendung der Standardeinstellungen installieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die grundlegenden Funktionen eines Einzelplatz-Computer-Betriebssystems und kann anhand von verbreiteten Anwendungsfällen erläutern, wie diese Funktionen die Verarbeitung von Informationen steuern und unterstützen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die wichtigsten Einstellungen bei der Konfiguration eines Einzelplatz-Computer-Betriebssystems und kann an Beispielen aufzeigen, wie damit Hardware und Betriebssystem optimal aufeinander abgestimmt werden können.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Software-Lizenzbestimmungen bei der Installation beachten und erforderliche Registrierungen und Aktivierungen vornehmen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die rechtlichen Verpflichtungen, die eingegangen werden, wenn man den Lizenzvereinbarungen eines Herstellers bei der Installation von Software zustimmt und kann die Konsequenzen aufzeigen, wenn diese missachtet werden.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den prinzipiellen Ablauf von Registrierung und Aktivierung von Informatik-Komponenten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Office Suite unter Verwendung der Standardeinstellungen installieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Einstellungen, die bei einer Standard-Installation eines Office-Paketes hinsichtlich des Funktionsumfangs/der Pfadangaben gewählt werden können und kann aufzeigen, welche Konsequenzen diese Einstellungen auf die Anwendung der Programme und den Betrieb (Datenablage etc.) des Einzelplatz-Computers haben.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Netzwerkzugang nach Vorgabe einrichten und austesten. Die Schutzmassnahmen für eine sichere Nutzung des Computers und des Netzwerks treffen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die prinzipielle Vorgehensweise, um auf einem Client eine Verbindung zum Netzwerk herzustellen und kann erläutern, wie damit die Kommunikation gewährleistet wird.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die nötigen Sicherheitseinstellungen (Virenschutz, Personal-Firewall, Updates) um einen Einzelplatz-Computer vor aktuellen Gefahren zu schützen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Einstellungen zur Reduktion des Energieverbrauchs einsetzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Einstellungsmöglichkeiten eines Einzelplatz-Computers zur Senkung des Energieverbrauchs und kann erläutern, welche Konsequenzen sich daraus für die Benutzerfreundlichkeit, die Umwelt und die Lebensdauer der Geräte ergeben.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>7</td>\n<td>Computer-Arbeitsplatz testen, Defekte und Konfigurationsfehler systematisch eingrenzen und beheben.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Kategorien von Fehlermeldungen (Hardware, Betriebsystem, Anwenderprogramme etc.) und kann die wichtigsten Indizien aufzeigen, welche für die Zuordnung der Fehlermeldungen zu diesen Kategorien ausschlaggebend sind.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt eine Methode (z.B. Ausschluss intakter Teilsysteme), die eine systematische Eingrenzung von fehlerhaften Zuständen erlaubt und kann beispielhaft aufzeigen, wie mit Hilfe dieser Methode ein typischer Fehlerfall effizient aufgedeckt werden kann.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>8</td>\n<td>Computer-Arbeitsplatz nach ergonomischen Grundsätzen einrichten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Richtlinien für das Einrichten eines ergonomischen Einzelplatz-Computer -Arbeitsplatzes und kann erläutern, wie diese zum Wohlbefinden beim Arbeiten beitragen und welchen gesundheitlichen Schäden damit vorgebeugt wird.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>9</td>\n<td>Computer-Arbeitsplatz anhand eines Abnahmeprotokolls übergeben.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Bedeutung einer geordneten Übergabe des Einzelplatz-Computer -Arbeitsplatzes (Abnahmeprotokoll) an die Benutzerin oder den Benutzer und kann diese Bedeutung anhand des konkreten Nutzens, welche die Beteiligten daraus ziehen, aufzeigen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Hardware Management</dd>\n<dt>Objekt</dt>\n<dd>Einzelplatz-Computer mit Drucker und Netzwerkanschluss (Hardware, Betriebssystem, Office Suite).</dd>\n<dt>Niveau</dt>\n<dd>1</dd>\n<dt>Voraussetzung</dt>\n<dd>keine</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('305', ' Betriebssysteme konfigurieren und administrieren ', 5, '', 2, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="2.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=420">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Betriebssysteme installieren, konfigurieren und  administrieren</dd>\n<dt>Kompetenz</dt>\n<dd>Betriebssysteme installieren und für den Multiuserbetrieb konfigurieren. System mit Betriebssystem-Befehlen und Hilfsprogrammen administrieren.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Betriebssysteme hinsichtlich verschiedener Merkmale unterscheiden (z.B. Leistungsdaten, Hard- und Softwareunterstützung, Lizenzierungsart, Supportmodelle).</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Aufgaben, Aufbau und Komponenten eines Betriebssystems und kann damit die unterschiedlichen Grundkonzepte (z.B. Multiuser, Multitasking, Kernel, Kernelspace, User Space, System Call) und Merkmale von Betriebsystemen erläutern.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Lizenzierungsmodelle verschiedener Betriebsysteme (z. B. Open Source, GPL, OEM, usw.) und kann deren grundsätzliche Folgen für die Kosten und den Betrieb der Systeme darlegen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Vor der Installation des Betriebssystems die Hardwareanforderungen sowie die Hardwarekompatibilität abklären und die notwendigen Treiber beschaffen. Konfiguration der Datenträger bestimmen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Vorbereitungsschritte, welche vor der Installation des Betriebssystems zu treffen sind und kann erläutern, wie diese zu einer erfolgreichen Installation beitragen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Möglichkeiten zur Automatisierung einer Installation.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die technischen Rahmenbedingungen einer Partitionierung und kann erläutern, wie diese bei der Installation zu berücksichtigen sind.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Kriterien für die Partitionierung eines Datenträgers und kann erläutern, welchen Nutzen eine entsprechende Partitionierung (Systempartition, Datenpartition, Swappartition) für den Betrieb des Systems bringt.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Datenträger-Partitionen und Dateisysteme einrichten, Bootmanager und Bootoptionen konfigurieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Datenträger-Verwaltungsstrukturen (MBR/GPT, Partitionstabelle, Bootrecord, Bootloader), die für das Booten notwendig sind, und kann erläutern, welche Aufgaben diese in den einzelnen Stufen des Bootvorgangs ausüben.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die wichtigsten Konfigurationsoptionen gängiger Bootmanager um ein Dual-Boot-System einzurichten.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Regeln für die Namensgebung von Partitionen unter verschiedenen Betriebssystemen.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Eigenschaften und Kompatibilität der gängigen Dateisysteme und kann aufzeigen, welche Vor-, Nachteile sowie Einsatzgebiete daraus resultieren.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Installationsumfang festlegen (z.B. minimal, typisch), Softwarepakete auswählen. Nach der Installation System aktualisieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den grundlegenden Funktionsumfang eines Betriebssystems sowie die verfügbare Zusatzsoftware und kann erläutern, welche Aufgaben damit ausgeführt oder unterstützt werden.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Möglichkeiten mit einem Paketmanager Software-Pakete hinzuzufügen oder zu entfernen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt unterschiedliche Konzepte zur Konfiguration von Software (Konfigurationsdateien, Registry, systemweite / benutzerspezifische Konfiguration) und kann Möglichkeiten aufzeigen, wie Software-Konfigurationen zu bearbeiten sind.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Weitere Hardwarekomponenten einbinden und konfigurieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Vorgehensweisen und Werkzeuge, um Hardware (Z.B. zweite Netzwerkkarte, Grafikkarte, usw.) ins System einzubinden und zu verwalten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Benutzerinterface auswählen und konfigurieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Kriterien für den Einsatz eines grafischen Benutzerinterfaces bzw. Konsolenbedienung.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Möglichkeiten für die Einstellung einer erleichterten Bedienung (Z.B. Grösse, Kontrast, usw.)</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>7</td>\n<td>Datei- und Verzeichnisstrukturen aufbauen und verwalten. Benutzer/Gruppen administrieren und Benutzerrechte vergeben.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die unterschiedlichen Konzepte für Verzeichnishierarchien (absolute/relative Pfade, Links, versteckte Dateien, Verknüpfung mit Anwendungen) und kann die dazugehörigen Systembefehle und Hilfsprogramme aufzeigen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die unterschiedlichen Konzepte für den Zugriff auf Datenträger und Dateisysteme (Mount-Befehl, Devicedateien, Treiber) und kann die dazugehörigen Systembefehle und Hilfsprogramme aufzeigen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die unterschiedlichen Konzepte für die Benutzer- und Rechteverwaltung (User-ID, Zugriffsrechte, Gruppenmitgliedschaft, Standardrechte, Vererbung, Heimverzeichnis) und kann die dazugehörigen Systembefehle und Hilfsprogramme aufzeigen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>8</td>\n<td>Ressourcen und Ereignisse überwachen. Im Betriebssystem enthaltene Hilfsprogramme zur Problemanalyse und -behebung einsetzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Massnahmen zum sicheren Arbeiten als Administrator (Benutzerwechsel; Befehle wie z.B. su, sudo, runas; Backup; Dokumentation) und kann erläutern, wie diese zur Sicherheit des Systems beitragen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt gängige Informationsquellen zur Informationsbeschaffung bei unbekannten Problemen/Aufgaben im Zusammenhang mit dem Betriebssystem (z.B. Befehlssyntax, Hilfesystem, Manual-Page, How-To‘s, Foren, usw.) und kann erläutern, für welche Art von Problemen und Aufgaben sich diese speziell eignen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Werkzeuge zur Überwachung des Systems (z.B. Taskmanager, Management-Konsolen Ereignisanzeige, Gerätemanager, Systeminformation, Protokolldateien) und kann erläutern, welche Informationen diese hinsichtlich des Systemzustands und möglicher Probleme liefern.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Werkzeuge und Möglichkeiten zur Beeinflussung von Ressourcen (CPU-Auslastung, Speicherverbrauch, Auslagerungsspeicher und Datenträgerbelegung) und kann erläutern, welche Wirkungen sich hinsichtlich des Systemzustands damit erzielen lassen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>9</td>\n<td>Software aus Sicherheitsgründen regelmässig überprüfen und aktualisieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Möglichkeiten zur manuellen und automatischen Aktualisierung von Betriebssystemen/Software mit Sicherheitspatches.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>System Management</dd>\n<dt>Objekt</dt>\n<dd>Computer, zwei unterschiedliche Betriebssysteme, Bootmanager, Netzwerkanschluss</dd>\n<dt>Niveau</dt>\n<dd>1</dd>\n<dt>Voraussetzung</dt>\n<dd>Einzelplatz-Computer in Betrieb nehmen</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('306', ' IT Kleinprojekte abwickeln', 4, '', 2, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=496">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>IT Kleinprojekt abwickeln</dd>\n<dt>Kompetenz</dt>\n<dd>IT Vorhaben im Team als Projekt mit klar definierter Zielsetzung, Anforderungen, vorgegebenen Ressourcen und Terminen abwickeln.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Zielsetzung unter Berücksichtigung der vorgegebenen Ressourcen, Anforderungen und Termine auf Machbarkeit prüfen und Erkenntnisse mit dem Auftraggeber besprechen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Merkmale eines Vorhabens, die dafür sprechen, dieses sinnvoll in Form eines Projektes abzuwickeln.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die wichtigsten Rollen und deren Aufgaben, Kompetenzen und Verantwortung (zB. Auftraggeber, Projektleiter, Projektmitarbeiter) in einem Projekt.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die wichtigsten Rahmenbedingungen und deren Einfluss, die hinsichtlich der zeitlichen Machbarkeit eines Auftrages  zu berücksichtigen sind. (zB. Anzahl Mitarbeitende, Verfügbarkeit der Mitarbeitenden, Anzahl Arbeitstage bis zum Endtermin, Verfügbarkeit der notwendigen Sachmittel)</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die wichtigsten Rahmenbedingungen und deren Einfluss, die hinsichtlich der inhaltlichen Machbarkeit eines Auftrages zu berücksichtigen sind (zB. Kompetenzen der Mitarbeitenden, technologische Voraussetzungen)</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Die IT Problemstellungen im Projekt identifizieren und Massnahmen zur Bewältigung bestimmen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt mögliche Methoden um Lösungsvarianten zu entwickeln und deren Realisierungsaufwand abzuschätzen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Projektplan zur systematischen Abwicklung des Auftrags erstellen und dabei die Ressourcen, Termine, Problemstellungen und die Arbeitsteilung berücksichtigen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt ein Vorgehensmodell zur Aufteilung  eines Projektes in Phasen und deren systematische Abwicklung.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Kriterien zur Bildung von Arbeitsaufträgen/Arbeitspaketen, die unter Berücksichtigung der Arbeitsteilung und Abwicklung in einem Projektteam parallel und sequentiell zu bearbeiten sind.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Arbeitsaufträge erteilen und deren Ausführung (Qualität, Termine, Kosten) koordinieren und überwachen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Elemente und deren Bedeutung für eine vollständige Abwicklung eines Arbeitsauftrags (zB. Zielsetzungen, Rahmenbedingungen, Termine, Ressourcen, Skills).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Methoden um den Projektfortschritt zu überwachen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Arbeitsfortschrittsbericht erstellen und darin den Stand des Projekts (Ressourcen, Termine, geleistete Arbeiten) für den Auftraggeber dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Bedeutung einer regelmässigen Berichterstattung an den Auftraggeber</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den Aufbau, Inhalt und Darstellung eines Arbeitsfortschrittsberichts (zB. Termine, Kosten, Qualität, Nachvollziehbarkeit)</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Projektergebnis (Lösung) für den Auftraggeber dokumentieren und erläutern.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den Aufbau, Inhalt und die formalen Regeln, die bei der Dokumentation der Arbeitsergebnisse zu berücksichtigen sind.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>7</td>\n<td>Den Projektverlauf mit den Projektbeteiligten im Team reflektieren und Erkenntnisse ableiten, wie effiziente Projektarbeit gestaltet werden kann.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Methoden zur Steuerung und Förderung der Zusammenarbeit (zB. Abgrenzung der Aufträge, regelmässige Koordination, Feedback einholen/geben)</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>IT Projektmanagement</dd>\n<dt>Objekt</dt>\n<dd>IT Vorhaben mit definierten Zielen, Anforderungen und Ergebnissen sowie vorgegebenen Ressourcen und Terminen \nTeam mit 2-5 Personen, Projektumfang ca. 6-10 Personentage</dd>\n<dt>Niveau</dt>\n<dd>3</dd>\n<dt>Voraussetzung</dt>\n<dd>Über erste Erfahrungen bei der Mitarbeit in Projekten verfügen \nStrukturiertes Vorgehen anwenden</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', '');
INSERT INTO `ict_module` (`modul_nr`, `modul_name`, `id_fachschaft`, `bereich`, `semester`, `lbv`, `lessons_ifz`, `lessons_uifz`, `bivo`, `hanoks`, `todo`) VALUES
('307', ' Interaktive Webseite mit Formular erstellen', 1, 'API', 3, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=498" style="display:block;">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Interaktive Webseite mit Formular erstellen</dd>\n<dt>Kompetenz</dt>\n<dd>Interaktive Webseiten gemäss Aufgabenstellung mit einer serverseitigen Programmiersprache realisieren und testen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Funktionalität der interaktiven Webseite gemäss Aufgabenstellung entwerfen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Kriterien, nach denen eine Vorgabe in einen umsetzbaren Entwurf überführt werden kann.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Elemente und Protokolle einer Web-Applikation, sowie deren Aufgaben im Client- / Server-Modell.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die wichtigsten Vorkehren zur Gewährleistung der Sicherheit von interaktiven Webseiten und kann erläutern, wie damit Missbrauch vorgebeugt werden kann. Validierung</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Layout für die Erfassung und Präsentation der Daten unter Berücksichtigung ergonomischer Aspekte entwerfen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt das Prinzip der Gruppenbildung von Informationen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die wichtigsten Regeln, für die Gestaltung eines Web-Formulars für die Dateneingabe und Datenausgabe hinsichtlich Abfolge und Bezeichnung der Informationen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Geeignete Formularelemente zur Erfüllung der Aufgabenstellung auswählen und Validierung der eingegebenen Daten sicherstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Formularelemente in HTML und deren Einsatzmöglichkeiten. Kennt die wesentlichen Kategorien von Formularelementen in HTML und kann erläutern, für welche Arten von Informationen sich die Formularelemente eignen und wie diese ausgelesen und in das Verarbeitungsprogramm übernommen werden können.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Übertragungsmethoden von Formulardaten und deren Auswertung in einer Webapplikation.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Client- und Server-seitige Möglichkeiten der Validierung von Formulareingabedaten und deren Beitrag zur Vermeidung von Fehleingaben und zur Begegnung von Sicherheitsrisiken.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Anwendung modular und gemäss Codierungs-Richtlinien programmieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Grundelemente einer clientseitigen Scriptsprache und welche Funktionen sich damit realisieren lassen (z.B. Popup Fenster, Formularvalidierung, Link Automation).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Grundelemente einer serverseitigen Programmiersprache.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Möglichkeiten zur Strukturierung von Quellcode in Module und deren Umsetzung unter Berücksichtigung von Codierungs-Richtlinien.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Für interaktive Webseiten geeignete Testfälle definieren, durchführen und im Testprotokoll dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den Aufbau der Testspezifikation einer Web-Applikation.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Schritte Testspezifikation, -durchführung, -auswertung, die bei einem Test durchlaufen werden müssen..</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Web Engineering</dd>\n<dt>Objekt</dt>\n<dd>Interaktive Webseiten mit einfachen Formularen. (z.B. Newsletter-Anmeldung, Kontakt-Formular usw.).</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd><ul><li>Statische Webseiten erstellen</li> <li>Anwenden von Script- oder Programmiersprachen</li></ul></dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n<small>Publiziert: 07.06.2014 17:15:41</small>\n</tab>\n</tabs>\n</tab>', ''),
('318', ' <span style="text-decoration:line-through;">Analysieren und objektbasiert programmieren..</span>', 2, 'API', NULL, NULL, 40, NULL, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=500" style="display:block;">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Analysieren und objektbasiert programmieren mit Komponenten</dd>\n<dt>Kompetenz</dt>\n<dd>Eine Aufgabe analysieren  mit einer Entwicklungsumgebung mit integriertem GUI-Designer implementieren, dokumentieren und testen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Problemstellung analysieren, Benutzerschnittstelle entwerfen und Abläufe darstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt eine Methode für die Analyse einer Problemstellung (z.B. Use Case) und kann diese auf einfache Aufgaben anwenden.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt eine Methode für die Darstellung von Programmabläufen (z.B. Struktogramm, Aktivitätdiagramm) und kann damit einfache Sachverhalte darstellen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Strukturelemente Iteration und Selektion und kann deren sinngerechte Anwendung aufzeigen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Grafische Benutzerschnittstelle gestalten und mit GUI-Designer realisieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt allgemeine Regeln der SW-Ergonomie (z.B. DIN EN ISO 9241-110) und kann aufzeigen, wie diese für die Gestaltung einer Oberfläche anzuwenden sind.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die wichtigsten Komponenten einer graphischen Oberfläche und kann diese korrekt einsetzen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Programmentwurf mit einer auf Komponenten basierenden Programmierumgebung mit Einsatz von Ablaufstrukturelementen, eigenen Prozeduren und Funktionen implementieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt eine Komponenten-basierte Programmierumgebung (z.B. .NET, Java/Swing) und deren typische Einsatzgebiete.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die unterschiedlichen Möglichkeiten für Selektion (einfach/mehrfach) und Iteration (vor-/nachprüfend) und kann diese mit einer Programmiersprache umsetzen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Prozeduren und Funktionen und kann deren Aufbau (inkl. Parameterlisten) und Einsatz erklären.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt den Mechanismus der Ereignisverarbeitung eines objektbasierten Systems und kann Ereignisse in Verarbeitungsroutinen auswerten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Beim Programmieren vorgegebene Codekonventionen einhalten, den Programmcode kommentieren und dabei auf Wartbarkeit und Nachvollziehbarkeit achten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Bedeutung von Kommentaren bei Prozeduren und Funktionen und deren Beitrag zu besser wartbarem Programmcode.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Ausgehend von der Aufgabenstellung Testfälle erstellen, das Programm testen und Fehler beheben.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt ein Verfahren für den statischen Test (z.B. Codereview) von Programmcode und kann die Bedeutung für die Qualität der Software aufzeigen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt ein Verfahren (z.B. Grenzwertanalyse) zur Definition von Testfällen, um die Zuverlässigkeit von Algorithmen nachzuweisen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Application Engineering</dd>\n<dt>Objekt</dt>\n<dd>Mit einer Komponenten-Bibliothek implementiertes Programm</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>Objektbasiert programmieren nach Vorgabe</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n<small>Publiziert: 07.06.2014 17:15:41</small>\n</tab>\n</tabs>\n</tab>', ''),
('326', 'Objektorientiert entwerfen und implementieren', 2, 'API', 4, '', 40, NULL, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=502">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Objektorientiert entwerfen und implementieren</dd>\n<dt>Kompetenz</dt>\n<dd>Eine objektorientierte Analyse (OOA) in ein objektorientiertes Design (OOD) überführen, implementieren, testen und dokumentieren.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Aufgrund der Businessanalyse Anwendungsfälle formulieren und daraus die fachlichen Klassen ableiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Elemente eines Use Case Models und wie Sachverhalte aus dem Alltag damit abgebildet werden.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Vorgehensprinzipien zur Klassenfindung sowie die Bedeutung eines iterativen Design-Prozesses.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Kriterien, nach denen Objekte der realen oder Vorstellungswelt zueinander in Beziehung gebracht werden können.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Ein objektorientiertes Design erstellen und mittels Unified Modeling Language darstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Architekturen um die Fachklassen zu einer lauffähigen Applikation zu ergänzen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt geeignete UML-Diagramme und welche Sachverhalte sich damit abbilden lassen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Objektorientiertes Design implementieren..</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Möglichkeiten, wie eine Architektur mit einer objektorientierten Programmiersprache umgesetzt werden kann.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Vorteile eines iterativen Vorgehens für die Implementa-tion des Designs.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Entwurfsmuster und deren Nutzen bei der Konkretisierung und Implementierung eines Designs.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die grundlegenden Vorteile von Frameworks und mit welchen Funktionen diese das objektorientierte Design unterstützen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Erfüllung der fachlichen Anforderung prüfen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Vorgehensweisen, um auf Grund der Analysedokumente (Use Cases, Aktivitätsdiagramme, Klassendiagramme) Testfälle zu bestimmen und auszuarbeiten.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die grundlegenden Schritte die bei einem Systemtest durchlaufen werden müssen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Applikations-Architektur nachvollziehbar dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Kriterien für die Auswahl geeigneter UML-Diagramme für die nachvollziehbare Dokumentation einer Applikations-Architektur.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Application Engineering</dd>\n<dt>Objekt</dt>\n<dd>Applikation mit 3-5 fachlichen Klassen (z.B. Bibliothek, Wertschriftendepot, Börsenticker usw.).</dd>\n<dt>Niveau</dt>\n<dd>3</dd>\n<dt>Voraussetzung</dt>\n<dd>Objektorientiert implementieren</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('330', ' <span style="text-decoration:line-through;">IP-Telefoniesysteme in Betrieb nehmen</span>', 6, '', 0, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=504">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>IP-Telefoniesystem in Betrieb nehmen</dd>\n<dt>Kompetenz</dt>\n<dd>IP-Telefoniesystem gemäss den erhobenen Anforderungen installieren, konfigurieren und in Betrieb nehmen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Anforderungen (Sicherheit, Lastprofil, Datenvolumen, Verfügbarkeit der notwendigen Dienste, Schnittstellen, Endgeräte) an ein IP-Telefonie-system aufnehmen und dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Kriterien (inkl. Quality of Service), welche bei der Dimensionierung eines IP-Telefoniesystems angewendet werden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Bestehende Netz-Infrastruktur und Verbindung zur Aussenwelt mit den Anforderungen vergleichen und bei Bedarf notwendige Anpassungen vorschlagen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt technische Spezifikationen von Server- und Netzwerksystemen für IP-Telefonie.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt unterschiedliche VoIP-Konzepte und Modelle, deren Vor- und Nachteile.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt verwendete Protokolle (z.B. SIP- und RTP-Protokoll inkl. Codecs und Protokolle wie H.323, STUN, NAT, aLaw, uLaw).</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die wichtigsten Dienste der klassischen Telefonie (analog, ISDN) sowie deren Anbindung mittels Gateway.</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt Möglichkeiten der Anbindung des IP-Telefoniesystems mittels IP an das Weitverkehrsnetz.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Erforderliche Einstellungen gemäss Sicherheits- und Betriebskonzept realisieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt spezifische Sicherheitsmassnahmen für ein IP-Telefonie-system.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Massnahmen zur Sicherstellung der Verfügbarkeit von IP-Telefoniesystemen (z.B. Redundanz, USV).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Endgeräte und Software installieren, konfigurieren und notwendige Dienste einrichten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Möglichkeit der Anbindung an bestehende ERP- und/oder Groupwaresysteme.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die  Möglichkeit der Anbindung mittels Gateway an die klassische Telefonie</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt verschiedene Arten von VoIP-Clients und deren Vor- und Nachteile.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Telefonie-Funktionalitäten implementieren und dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt grundlegende Telefoniefunktionaliäten (Call-Routing, Ringruf, Anrufbeantwortung, Weiterleitung, MOH, Blacklist usw.).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Möglichkeiten Dienste und Funktionen für Nummern/Benut¬zer zu definieren.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>IP-Telefoniesystem testen (Funktionstest, Lasttest, Sicherheit, Crashtest inkl. Stromausfalltest).</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt ein Verfahren um das IP-Telefoniesystem nachvollziehbar zu testen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>System Management</dd>\n<dt>Objekt</dt>\n<dd>IP-Telefoniesystem für ein KMU-Umfeld</dd>\n<dt>Niveau</dt>\n<dd>4</dd>\n<dt>Voraussetzung</dt>\n<dd>Erfahrung mit TCP/IP Protokollen \nErfahrung mit Client/Server-Umgebungen \nNetzwerkdienste konfigurieren</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('335', ' Mobile-Applikation realisieren', 2, 'API', 4, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=506">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Mobile-Applikation realisieren</dd>\n<dt>Kompetenz</dt>\n<dd>Eine Applikation für mobile Geräte nach Vorgabe umsetzen und testen</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Vorgabe analysieren, Funktionalität und Storyboard entwerfen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die ergonomischen Standards (z.B. EN-9241-110) und deren Anwendung auf einer mobilen Plattform.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Guidelines für das Design und Benutzerführung einer App.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Eigenschaften unterschiedlicher mobiler Geräte bezüglich Display-Grösse und -Ausrichtung und der Eingabeoptionen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Lösungskonzept für die Applikation erarbeiten und Einbettung in bestehenden Lösungen überprüfen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt verschiedene Applikationstypen (native App, Cross-(Hybrid-) und Web-App) und Frameworks.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt spezielle Funktionen, sowie Aktoren und Sensoren (z.B. Geolocation, Bewegungssensoren, Spracherkennung und –generierung).</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Besonderheiten der App-Architekturen bezüglich Funktionalitäten, Möglichkeiten und Grenzen der mobilen Devices</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Möglichkeiten von Persistenzlösungen von mobilen Plattformen (Online-/Offline Szenarien).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Mobile-Applikation mit einer gängigen Entwicklungsumgebung unter Berücksichtigung der Möglichkeiten und Einschränkungen von mobilen Geräten programmieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Bedienung und den Einsatz einer passenden Entwicklungs-, Simulations- und Testumgebung.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt ein gängiges Framework und dessen APIs (z.B. für IOS, Android, HTML5)</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Veröffentlichung der Applikation auf einer gängigen Plattform planen und nötige Schritte festhalten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Möglichkeiten und Bedingungen zur Veröffentlichung einer App.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die nötigen Schritte zur Veröffentlichung einer App.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Schritte zur Vermarktung der App (Product, Placement, Price, Promotion).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Mobile-Applikation gemäss Testplan überprüfen, Testergebnisse festhalten und allenfalls erforderliche Korrekturen vornehmen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Testverfahren zur Überprüfung der funktionalen Anforderungen von Apps auf mobilen Geräten.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Testverfahren zur Überprüfung der nicht-funktionalen Anforderungen (Portabilität, Ergonomie, Sicherheit, User-experience) von Apps auf mobilen Geräten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Application Engineering</dd>\n<dt>Objekt</dt>\n<dd>Mobile Applikation mit Benutzerinteraktion (z.B. Stundenplan, Veranstaltungsbewertung, Chat, Wetter-App etc.).</dd>\n<dt>Niveau</dt>\n<dd>3</dd>\n<dt>Voraussetzung</dt>\n<dd>Objektorientiert implementieren, Benutzerschnittstellen implementieren</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('340', ' IT-Infrastruktur virtualisieren', 5, '', 0, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="3.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=508">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>IT Infrastruktur virtualisieren</dd>\n<dt>Kompetenz</dt>\n<dd>Virtualisierung einer vorgegebenen IT-Infrastruktur einschätzen, planen und durchführen</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Ziele und Verfahren sowie Einsatzgebiete von virtualisierten Systemen erläutern.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Verfahren zur Virtualisierung von IT Infrastrukturen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die lizenzrechtlichen Auswirkungen einer Virtualisierungs-lösung.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Unterschiede zwischen Desktop-, Server- und Applika-tionsvirtualisierung.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Konzepte zur Virtualisierung von Netzwerken.</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt technische und wirtschaftliche Vorteile einer virtualisierten IT Infrastruktur.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Geeignetes Virtualisierungs-Konzept (Hardwareressourcen, finanzielle Mittel, Energieverbrauch) für einen gegebenen Auftrag erstellen. Finanzielle Einsparungsmöglichkeiten aufzeigen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Server-Hard- und Softwarekosten und deren finanzielle Auswirkung auf eine Virtualisierung.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Vor- und Nachteile in Bezug auf Hardware, Energieverbrauch und finanzielle Mittel, die eine virtualisierte IT Infrastruktur mit sich bringt.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Mittel und Verfahren um die Virtualisierungslösung in einem Konzept darzustellen. .</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>IT Infrastruktur mit ausgewählten Tools virtualisieren und testen</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Hardwarevoraussetzungen für die Virtualisierung einer IT Infrastruktur.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt ein Verfahren um Hardware Instanzen auf eine virtuelle Umgebung zu migrieren.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die technischen Möglichkeiten des ausgewählten Virtualisierungsproduktes.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Verfahren um die virtuelle IT Infrastruktur zu testen und zu dokumentieren.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Geeignete Storage- und Backuplösung definieren, installieren und konfigurieren</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt mögliche Storage Lösungen für das Speichern der Daten der virtualisierten IT Infrastruktur.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Kriterien die bei der Datensicherung von virtuellen IT Infrastrukturen wichtig sind.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Backuplösungen zum Sichern der von der virtuellen IT Infrastruktur produzierten Daten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Virtuelle IT Infrastruktur in den produktiven Betrieb überführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt ein Verfahren für die Überführung in den produktiven Betrieb.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>System Management</dd>\n<dt>Objekt</dt>\n<dd>KMU Netzwerk mit Serverumgebung mit drei Servern (z.B. File-, Web- und Mailserver)</dd>\n<dt>Niveau</dt>\n<dd>4</dd>\n<dt>Voraussetzung</dt>\n<dd>Erfahrungen in der Serverinstallation und in Betriebssystemen</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('403', ' Programmabläufe prozedural  implementieren', 2, '', 1, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="1.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=422">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Programmabläufe prozedural implementieren</dd>\n<dt>Kompetenz</dt>\n<dd>Aufgrund einer Vorgabe Programmabläufe prozedural erstellen, testen und dokumentieren.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Vorgegebenen Programmablauf in Form einer Ablaufstruktur abbilden.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Kontrollstrukturen Sequenz, Selektion und Iteration, deren Funktionsweise und Einsatz.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt eine Notation um den Ablauf einer Programmlogik abzubilden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Die erforderlichen Daten für Eingabe, Verarbeitung und Ausgabe bestimmen und ihre Datentypen festlegen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Datentypen, ihren Speicherbedarf und Wertebereich.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Regeln (implizite und explizite), die bei der Konversion von Datentypen angewendet werden und die Auswirkungen hinsichtlich Genauigkeit.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kontrollstrukturen für die Steuerung des Programmablaufs anwenden.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die grundlegenden Anweisungen, Kontrollstrukturen, Bedingungen und Operatoren einer Programmiersprache.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Ablaufstruktur und Daten mit einer Programmiersprache in einen Programmablauf umsetzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den grundlegenden Aufbau eines Programms.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den Sinn und Zweck von Variablen und Konstanten, sowie deren Unterschied.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt den Gültigkeitsbereich von Variablen und wie Variablen eingesetzt werden.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die Möglichkeiten einer Entwicklungsumgebung, sowie den Umgang mit Fehler- und Warnmeldungen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Vorgegebene funktionale Testfälle (Eingabewerte, erwartete Ergebnisse) anwenden um Fehler im  Programmablauf zu erkennen und zu beheben.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den Sinn und Zweck des Testens und die Absicht von einzelnen Testfällen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Kriterien für die Definition eines Testfalls und die gezielte Auswahl der Testdaten.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt ein Vorgehen um Fehler im Programm zu lokalisieren und zu beheben.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Einen Debugger einsetzen um die Programmausführung zu überwachen und Fehler zu erkennen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den Zweck, die Funktionen und die Bedienung eines Debugger.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den Nutzen von Breakpoints und der Überwachung von Variablen zur Kontrolle von Programmabläufen und Bedingungen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Application Engineering</dd>\n<dt>Objekt</dt>\n<dd>Programmablauf mit verschachtelten Kontrollstrukturen\nZum Beispiel: Anmeldeprozess mit Sperrung nach 3 Falscheingaben</dd>\n<dt>Niveau</dt>\n<dd>1</dd>\n<dt>Voraussetzung</dt>\n<dd>keine</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', '<h3>Rework Hugo Jun2017</h3><p>Toolbox, Drehbuch auf Processing/Java und neues Lehrmittel anpassen</p>'),
('404', ' Objektbasiert programmieren nach Vorgabe', 2, '', 2, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="1.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=424">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Objektbasiert programmieren nach Vorgabe</dd>\n<dt>Kompetenz</dt>\n<dd>Vorgabe interpretieren, objektbasiert mit einer Programmiersprache implementieren, dokumentieren und testen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Aufgrund einer Vorgabe den Ablauf darstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Darstellungsmöglichkeiten für Programmabläufe Verarbeitungen (z.B. Zustandsdiagramm) und weiss, welche Sachverhalte sich damit abbilden lassen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Eine Benutzerschnittstelle entwerfen und implementieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Regeln für den Aufbau einer Benutzerschnittstelle.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Lösungsansätze für die Verarbeitung und Validierung von Benutzerinteraktionen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Erforderliche Daten bestimmen und Datentypen festlegen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den Einsatz und die korrekte Anwendung der wichtigsten Datentypen Ganzzahl, Gleitkommazahl, Zeichen, Zeichenketten und Boolean.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Programmvorgabe unter Nutzung vorhandener Komponenten mit deren Eigenschaften und Methoden, sowie Operatoren und Kontrollstrukturen implementieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den grundsätzlichen Aufbau einer Komponente (Eigenschaften, Methoden und Ereignisse) und wie sich diese für die Verarbeitung einsetzen lassen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den grundlegenden Aufbau eines Programms und den Einfluss auf den Arbeitsspeicher.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt verfügbare Komponenten einer Programmiersprache mit ihren Eigenschaften, Methoden und Ereignissen sowie Operatoren, und Kontrollstrukturen .</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Beim Programmieren vorgegebene Standards und Richtlinien einhalten, das Programm inline dokumentieren und dabei auf Wartbarkeit und Nachvollziehbarkeit achten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die wichtigsten Regeln für einen verständlichen Quellcode (sprechende Komponenten- und Variablenbezeichnungen, optische Strukturierung, sinnvolle Kommentare).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Programm auf Einhaltung der Funktionalität testen, Fehler erkennen und beheben.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Elemente eines Testfalls (zu testende Funktion oder Verarbeitung, Eingabedaten, erwartete Ergebnisse)</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Bedeutung des Testens für die Sicherung der Programmqualität.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Application Engineering</dd>\n<dt>Objekt</dt>\n<dd>Einfaches, objektbasiertes Programm.\nZum Beispiel: Taschenrechner</dd>\n<dt>Niveau</dt>\n<dd>1</dd>\n<dt>Voraussetzung</dt>\n<dd>Keine</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', '<h3>Rework Sven Jun2017</h3><p>Toolbox, Drehbuch auf SWING und neues Lehrmittel anpassen</p>'),
('411', ' Datenstrukturen und Algorithmen entwerfen', 2, 'API', 3, '', 40, 0, '2014', '<tabs>  <tab data-title="1.00">  <div class="pull-right"> <a href="modules.php?name=&amp;a=20106&amp;nmodulid=510">Als Word herunterladen</a>  </div>  <dl><dt>Modul</dt><dd>Datenstrukturen und Algorithmen entwerfen und anwenden</dd><dt>Kompetenz</dt><dd>Problemstellung analysieren und mit geeigneten Datenstrukturen und Algorithmen eine Lösung umsetzen, testen und dokumentieren.</dd><dt>Handlungsziele</dt><dd> <table> <colgroup>  <col width="5%">  <col width="90%">  <col width="5%"> </colgroup> <tbody><tr><td>1</td><td>Für ein gegebenes Problem eine geeignete Datenstruktur definieren und mit den Mitteln einer Programmiersprache, wie Structs, Referenzen / Zeiger und Arrays umsetzen.</td><td> </td>  </tr>  <tr><td>&nbsp;</td><td> <b>Handlungsnotwendige Kenntnisse:</b> <table> <tbody><tr><td>1</td><td>Kennt den Unterschied zwischen Wertevariablen und Referenzvariablen / Zeigervariablen.</td>  </tr> <tr><td>2</td><td>Kennt Sprachmittel einer Programmiersprache für das Deklarieren und das Arbeiten mit Werte- und Referenzvariablen.</td>  </tr> <tr><td>3</td><td>Kennt Konzept und Nutzen von verketteten Datenstrukturen.</td>  </tr> <tr><td>4</td><td>Kennt eine Notation für die Darstellung von verketteten Datenstrukturen.</td>  </tr></tbody></table></td><td>&nbsp;</td>  </tr><tr><td>2</td><td>Ein Problem analysieren und einen geeigneten Algorithmus zur Lösung mit den Grundelementen Zuweisung, Verzweigung und Schleife entwerfen und mit Prozeduren und Funktionen umsetzen.</td><td> </td>  </tr>  <tr><td>&nbsp;</td><td> <b>Handlungsnotwendige Kenntnisse:</b> <table> <tbody><tr><td>1</td><td>Kennt eine systematische Vorgehensweise für die Analyse eines Problems.</td>  </tr> <tr><td>2</td><td>Kennt die Grundelemente von Algorithmen und deren Umsetzung in einer Programmiersprache.</td>  </tr> <tr><td>3</td><td>Kennt die Sprachmittel einer Programmiersprache für das Deklarieren und Aufrufen von Funktionen und Prozeduren.</td>  </tr> <tr><td>4</td><td>Kennt Notationen um Algorithmen dazustellen wie z.B. Struktogrammen, PAP usw.</td>  </tr></tbody></table></td><td>&nbsp;</td>  </tr><tr><td>3</td><td>Algorithmen und Datenstrukturen hinsichtlich Speicher- und Zeitkomplexität analysieren und dokumentieren.</td><td> </td>  </tr>  <tr><td>&nbsp;</td><td> <b>Handlungsnotwendige Kenntnisse:</b> <table> <tbody><tr><td>1</td><td>Kennt Möglichkeiten zur Analyse von Zeit- und Speicherkomplexität von Algorithmen und Datenstrukturen.</td>  </tr> <tr><td>2</td><td>Kennt Komplexitätsklassen von Algorithmen (z.B. lineare, exponentielle Komplexität).</td>  </tr> <tr><td>3</td><td>Kennt Notationen zur Dokumentation der Speicher- und Zeitkomplexität eines Algorithmus.</td>  </tr></tbody></table></td><td>&nbsp;</td>  </tr><tr><td>4</td><td>Ein komplexeres Problem auf kleinere Teilprobleme zurückführen und je nach Problemstellung  Iteration oder Rekursion einsetzen.</td><td> </td>  </tr>  <tr><td>&nbsp;</td><td> <b>Handlungsnotwendige Kenntnisse:</b> <table> <tbody><tr><td>1</td><td>Kennt ein Vorgehen bei der Aufteilung eines komplexen Problems in kleinere Teilprobleme.</td>  </tr> <tr><td>2</td><td>Kennt den Aufbau von iterativen Algorithmen.</td>  </tr> <tr><td>3</td><td>Kennt den Aufbau von rekursiven Algorithmen.</td>  </tr> <tr><td>4</td><td>Kennt Vor- und Nachteile sowie geeignete Einsatzgebiete des iterativen und des rekursiven Ansatzes.</td>  </tr></tbody></table></td><td>&nbsp;</td>  </tr><tr><td>5</td><td>Abstrakte Datentypen, wie Liste, Set, Map etc. und die darauf definierten Operationen kennen und zielgerichtet einsetzen können.</td><td> </td>  </tr>  <tr><td>&nbsp;</td><td> <b>Handlungsnotwendige Kenntnisse:</b> <table> <tbody><tr><td>1</td><td>Kennt eine Programmbibliothek, die ADTs wie Liste, Set, Map, etc. zur Verfügung stellt und deren Anwendung.</td>  </tr> <tr><td>2</td><td>Kennt Eigenschaften von einzelnen abstrakten Datentypen.</td>  </tr></tbody></table></td><td>&nbsp;</td>  </tr><tr><td>6</td><td>Datenstrukturen und Algorithmen mit dem Debugger und weiteren Tools untersuchen und dabei speziell die Situation auf Stack und Heap Analysieren und in geeigneter Form darstellen.</td><td> </td>  </tr>  <tr><td>&nbsp;</td><td> <b>Handlungsnotwendige Kenntnisse:</b> <table> <tbody><tr><td>1</td><td>Kennt das Speicherlayout für einen Prozess mit den Bereichen Programmcode, globale Variablen, Stack und Heap.</td>  </tr> <tr><td>2</td><td>Kennt die Anordnung von lokalen Variablen und Parametern in einem Stackframe.</td>  </tr> <tr><td>3</td><td>Kennt die Funktionsweise des Stacks bei einem Funktionsaufruf.</td>  </tr> <tr><td>4</td><td>Kennt die Funktionsweise im Heap bei dynamisch erzeugten Objekten.</td>  </tr> <tr><td>5</td><td>Kennt die Anwendung eines Debuggers für die Untersuchung von Heap und Stack zur Laufzeit und/oder ein Tool zur Laufzeitanalyse für Grössen wie Memory Leaks, Aufrufhäufigkeit und Verweildauer von Funktionen, etc.</td>  </tr></tbody></table></td><td>&nbsp;</td>  </tr>  </tbody></table></dd><dt>Kompetenzfeld</dt><dd>Application Engineering</dd><dt>Objekt</dt><dd>Durchführung einer Analyse und darauffolgende Realisierung mit Datenstrukturen und Algorithmen.</dd><dt>Niveau</dt><dd>2</dd><dt>Voraussetzung</dt><dd>Prozedurales ProgrammierenObjektbasiertes Programmieren</dd><dt>Anzahl Lektionen</dt><dd>40</dd><dt>Anerkennung</dt><dd>Eidg. Fähigkeitszeugnis</dd>  </dl> </tab> </tabs>  ', '<h3>Ideensammlung</h3><ul><li>Toolbox und Skript parat, wird permanent gepflegt unter <a href"https://github.com/greenorca/ICT_EFZ_API_M411">https://github.com/greenorca/ICT_EFZ_API_M411</a></li></ul>'),
('426', ' Software mit agilen Methoden entwickeln', 2, 'API', 4, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="1.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=512">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Software mit agilen Methoden entwickeln</dd>\n<dt>Kompetenz</dt>\n<dd>Agile Methoden zur Softwareentwicklung in Release-Zyklen anwenden.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Vorgegebene Funktionalität im Rahmen eines Softwareprojekts mit einer agilen Methode umsetzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt eine agile Methode zur Softwareentwicklung</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Inhalt und Aufbau einer User-Story</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Werte und Prinzipien von agilen Methoden.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die Abgrenzung von agilen und linearen Methoden.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Funktionalitäten schrittweise mit Praktiken der agilen Entwicklung  in vorgegebenen Release-Zyklen realisieren, testen und den Software-Release in Kurzform präsentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt den Ablauf und die Bestandteile eines Releasezyklus.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den Aufbau und Inhalt einer Releasepräsentation.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Bedeutung von lauffähigen Softwarereleases.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Praktiken (best practices) der agilen Softwareentwicklung wie z.B. einfaches Design, Metapher, Refactoring, Testdriven Design, Continuous Integration, Pair Programming, Coding Standards.</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt auf die agile Entwicklung zugeschnittene Tools für Unit-Tests, System-Build und -integration, Akzeptanztest, usw.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Bestehende Entwurfsmuster und/oder geprüfte Softwarekomponenten zur Lösung von Problemen gezielt einsetzen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Bedeutung und den Aufbau relevanter Entwurfsmuster.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Erkennt den Nutzen der Wiederverwendung bestehender Softwarekomponenten.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kenn ein Vorgehen zur Evaluation von Softwarekomponenten um Problemstellungen zu lösen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Die Ergebnisse und das Arbeitsvorgehen eines Release-Zyklus reflektieren und daraus Erkenntnisse für das weitere Vorgehen ableiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt ein Vorgehen um die Ergebnisse eines Releasezyklus zu reflektieren.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Möglichkeiten um das eigene Arbeitsvorgehen im Releasezyklus zu reflektieren.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Möglichkeiten um das weitere Vorgehen aufgrund der Reflektion zu optimieren  (SCRUM, ceremony, product backlog issues).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Die Projektdokumente und den Programmquelltext in einem gemeinsamen Versionsverwaltungssystem bereitstellen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Vorteile und Nutzen eines gemeinsam genutzten Versionsverwaltungssystems.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Funktionalitäten und die Bedienung eines Versionsverwaltungssystems, sowie die Möglichkeiten unterschiedliche Objekte einzupflegen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Unterschiede zwischen Versionsverwaltungssystemen mit exklusivem und parallelem Zugriff.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Programmquelltext nach Konventionen intuitiv verständlich formulieren und bei Bedarf überarbeiten.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Konzepte, Regeln und Verfahren von Clean Code.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Wichtigkeit des Refactoring zur Verbesserung der intuitiven Lesbarkeit von Programmquelltext.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Application Engineering</dd>\n<dt>Objekt</dt>\n<dd>Funktionsfähiger, getesteter und dokumentierter Software-Release</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>Objektorientiert implementieren</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', ''),
('431', ' Aufträge im IT-Umfeld selbständig durchführen', 4, '', 1, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="1.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=426">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Aufträge im IT-Umfeld selbstständig durchführen</dd>\n<dt>Kompetenz</dt>\n<dd>Aufträge aus dem IT-Umfeld gemäss Vorgaben des Auftraggebers selbständig durchführen.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Auftrag entgegen nehmen, eigenes Verständnis klären, Abhängigkeiten erkennen und Unklarheiten mit dem Auftraggeber klären.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die grundsätzlichen Elemente einer Organisation (Aufgabe, Aufgabenträger, Sachmittel, Information) und kann erläutern, welchen Beitrag diese Elemente zur Bestimmung einer Aufgabe leisten.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den generellen Phasenablauf eines IT-Projektes (z.B. Initialisierung, Konzept, Realisierung, Einführung) als Umfeld seiner Auftragsbearbeitung.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die Rollen von Auftraggeber und Auftragnehmer und kann deren Aufgaben, Kompetenzen und Verantwortungen beschreiben.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Auftrag gemäss Prinzip der vollständigen Handlung planen und durchführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt ein Modell der vollständigen Handlung (z.B. IPERKA).</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt eine Technik zur Planung, Überprüfung und Dokumentation des persönlichen Arbeitsfortschrittes und kann erläutern, wie diese die Einhaltung der Vorgaben eines Auftrages unterstützt.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt den Mehrwert von Checklisten und kann diese im Auftrag einsetzen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Auftrag unter Verwendung geeigneter Techniken (Kreativitätstechnik, Problemlösetechnik, Lerntechnik usw.) durchführen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt eine für den Auftrag geeignete Problemlösetechnik und kann diese einsetzen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt eine für den Auftrag geeignete Kreativitätstechnik und kann diese einsetzen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt eine für den Auftrag geeignete Lerntechnik um sich das nötige Wissen für die Auftragsbearbeitung anzueignen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Ergebnis des Auftrags dokumentieren, dem Auftraggeber präsentieren und das Ergebnis kritisch hinterfragen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kann den Beitrag einer Dokumentation zur Sicherstellung und Nachvollziehbarkeit von Arbeitsergebnissen erläutern.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die wichtigsten inhaltlichen und formalen Regeln, die bei der Dokumentation von Arbeitsergebnissen einzuhalten sind und kann erläutern, wie diese deren Sicherstellung und Nachvollziehbarkeit unterstützen.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt eine für den Auftrag geeignete Präsentationstechnik und kann diese einsetzen.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt Möglichkeiten Feedback einzuholen</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt Möglichkeiten die Auftragsabwicklung zu reflektieren (z.B. Sozialverhalten, Kommunikation, Arbeitstechnik, Lerntechnik usw.).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>IT Projektmanagement</dd>\n<dt>Objekt</dt>\n<dd>Auftrag aus dem IT-Umfeld mit definierten Zielen und Ergebnissen</dd>\n<dt>Niveau</dt>\n<dd>1</dd>\n<dt>Voraussetzung</dt>\n<dd>keine</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', '');
INSERT INTO `ict_module` (`modul_nr`, `modul_name`, `id_fachschaft`, `bereich`, `semester`, `lbv`, `lessons_ifz`, `lessons_uifz`, `bivo`, `hanoks`, `todo`) VALUES
('437', ' Im Support arbeiten', 4, '', 1, '', 40, 0, '2014', '<tab data-title="Modulbeschrieb">\n<tabs>\n<tab data-title="1.00">\n<div class="pull-right">\n<a href="modules.php?name=&amp;a=20106&amp;nmodulid=514">Als Word herunterladen</a>\n</div>\n<dl>\n<dt>Modul</dt>\n<dd>Im Support arbeiten</dd>\n<dt>Kompetenz</dt>\n<dd>Supportprozess verstehen und Kundenanliegen im 1st und 2nd Level bearbeiten.</dd>\n<dt>Handlungsziele</dt>\n<dd>\n<table>\n<colgroup>\n<col width="5%">\n<col width="90%">\n<col width="5%">\n</col></col></col></colgroup>\n<tbody>\n<tr>\n<td>1</td>\n<td>Den Supportprozess in seiner Gesamtheit verstehen. Die unterschiedlichen Rollen und Nahtstellen beschreiben können.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die unterschiedlichen Supportlevel und deren Funktionen im Supportprozess.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die gebräuchlichen Fachbegriffe im Supportprozess (z.B. Service Desk, Incident, Problem, Request)</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt die beteiligten Akteure und deren Rollen im Supportprozess.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kundenanliegen im 1st Level Support vollständig aufnehmen, bearbeiten, dokumentieren oder eskalieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt das Prinzip eines Tickets und den darin festgehaltenen Informationen.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Modelle Kommunikation zwischen Menschen (z.B. 4-Ohren, Sender-Empfänger, Sach- und Beziehungsebene).</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Möglichkeiten schwierige Situationen bei Kundengesprächen professionell zu meistern (z.B. emotionale Gespräche auf die Sachebene zurückbringen).</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt den Einsatz verschiedener Fragetechniken zum effizienten Erfassen und Lösen eines Kundenanliegens.</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt Möglichkeiten und Grenzen für einen sinnvollen Einsatz von Remote-Support.</td>\n</tr>\n<tr>\n<td>6</td>\n<td>Kennt Kriterien für die Eskalation einer Support-Anfrage an den Second-Level-Support.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>3</td>\n<td>Die dem 2nd Level zugewiesenen Kundenanliegen analysieren, Lösung vorbereiten, Lösung implementieren und dokumentieren oder Anliegen eskalieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt eine Arbeitstechnik um den Einsatz beim Kunden zielgerichtet vorzubereiten.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt die Wirkung des eigenen Auftretens und Erscheinungsbildes auf die Kundenbeziehung.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt den Einfluss der nonverbalen und verbalen Kommunikation vor Ort beim Kunden auf den Arbeitserfolg.</td>\n</tr>\n<tr>\n<td>4</td>\n<td>Kennt die Bedeutung einer vollständigen und korrekten Dokumentation des Support-Falls im Hinblick auf die Nachvollziehbarkeit.</td>\n</tr>\n<tr>\n<td>5</td>\n<td>Kennt Kriterien für die Eskalation einer Support-Anfrage an den Third-Level-Support.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>4</td>\n<td>Anfragen an den 3rd Level Support vorbereiten und Antworten in den Supportprozess einfliessen lassen.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt die Anforderungen um ein technisches Problem fachgerecht zu formulieren und an die zuständige Stelle für die Bearbeitung weiterzuleiten.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt den Supportprozess um Antworten an die richtige Stelle weiterzuleiten.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>5</td>\n<td>Störungen analysieren und mit geeigneten Methoden Lösungen herbeiführen und dokumentieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt Methoden um Fehler systematisch einzugrenzen und zu beheben.</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Kennt Möglichkeiten Störungsfälle auszuwerten und daraus Regelmässigkeiten zu erkennen um geeignete Massnahmen einzuleiten.</td>\n</tr>\n<tr>\n<td>3</td>\n<td>Kennt Möglichkeiten auf Grund von Parametern mögliche Störungen schon im Vorfeld zu erkennen.</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n<tr>\n<td>6</td>\n<td>Lösungen für Störungen zielgruppengerecht aufbereiten, dokumentieren und kommunizieren.</td>\n<td>\n</td>\n</tr>\n<tr>\n<td> </td>\n<td>\n<b>Handlungsnotwendige Kenntnisse:</b>\n<table>\n<tr>\n<td>1</td>\n<td>Kennt verschiedene Medien um Lösungen zu dokumentieren und kommunizieren (z.B. Knowledgebase, FAQ, Help, Wiki).</td>\n</tr>\n</table>\n</td>\n<td> </td>\n</tr>\n</tbody>\n</table>\n</dd>\n<dt>Kompetenzfeld</dt>\n<dd>Service Management</dd>\n<dt>Objekt</dt>\n<dd>Supportorganisation mit Kundenanliegen</dd>\n<dt>Niveau</dt>\n<dd>2</dd>\n<dt>Voraussetzung</dt>\n<dd>Betriebssystem- und Grundlagen Netzwerkkenntnisse</dd>\n<dt>Anzahl Lektionen</dt>\n<dd>40</dd>\n<dt>Anerkennung</dt>\n<dd>Eidg. Fähigkeitszeugnis</dd>\n</dl>\n</tab>\n</tabs>\n</tab>', '');
